import nodemailer from 'nodemailer';
import type { DeliveryProvider } from "@shared/schema";

export interface NotificationData {
  orderId: string;
  trackingId?: string;
  customerName: string;
  customerPhone: string;
  pickupAddress: string;
  deliveryAddress: string;
  packageDescription: string;
  deliveryFee: number;
  estimatedDeliveryTime?: string;
  vendorName: string;
  vendorPhone: string;
  specialInstructions?: string;
}

export class NotificationService {
  private transporter?: nodemailer.Transporter;

  constructor() {
    if (process.env.GMAIL_USER && process.env.GMAIL_APP_PASSWORD) {
      this.transporter = nodemailer.createTransporter({
        service: 'gmail',
        auth: {
          user: process.env.GMAIL_USER,
          pass: process.env.GMAIL_APP_PASSWORD
        }
      });
    }
  }

  async notifyCourier(provider: DeliveryProvider, data: NotificationData): Promise<boolean> {
    try {
      switch (provider.notificationMethod) {
        case 'email':
          return await this.sendEmailNotification(provider, data);
        case 'sms':
          return await this.sendSMSNotification(provider, data);
        case 'webhook':
          return await this.sendWebhookNotification(provider, data);
        default:
          console.error(`Unknown notification method: ${provider.notificationMethod}`);
          return false;
      }
    } catch (error) {
      console.error('Notification error:', error);
      return false;
    }
  }

  private async sendEmailNotification(provider: DeliveryProvider, data: NotificationData): Promise<boolean> {
    if (!this.transporter || !provider.contactEmail) {
      console.error('Gmail not configured or no contact email for provider');
      return false;
    }

    const emailContent = {
      from: process.env.GMAIL_USER,
      to: provider.contactEmail,
      subject: `ðŸšš New Delivery Order - ${data.orderId}`,
      html: `
        <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto; background: white; border-radius: 10px; box-shadow: 0 4px 6px rgba(0,0,0,0.1);">
          <div style="background: #FF4605; color: white; padding: 20px; border-radius: 10px 10px 0 0; text-align: center;">
            <h1 style="margin: 0; font-size: 24px;">ðŸ§  Modern Minds Couriers</h1>
            <h2 style="margin: 10px 0 0 0; font-size: 18px; font-weight: normal;">New Delivery Assignment</h2>
          </div>

          <div style="padding: 30px;">
            <div style="background: #f8f9fa; padding: 20px; border-radius: 8px; margin: 20px 0; border-left: 4px solid #FF4605;">
              <h3 style="color: #FF4605; margin-top: 0;">ðŸ“‹ Order Details</h3>
              <table style="width: 100%; border-spacing: 0;">
                <tr><td style="padding: 5px 0; font-weight: bold; width: 40%;">Order ID:</td><td>${data.orderId}</td></tr>
                <tr><td style="padding: 5px 0; font-weight: bold;">Customer:</td><td>${data.customerName}</td></tr>
                <tr><td style="padding: 5px 0; font-weight: bold;">Customer Phone:</td><td>${data.customerPhone}</td></tr>
                <tr><td style="padding: 5px 0; font-weight: bold;">Package:</td><td>${data.packageDescription}</td></tr>
                <tr><td style="padding: 5px 0; font-weight: bold;">Delivery Fee:</td><td style="color: #28a745; font-weight: bold;">KSh ${Number(data.deliveryFee).toLocaleString()}</td></tr>
                ${data.estimatedDeliveryTime ? `<tr><td style="padding: 5px 0; font-weight: bold;">ETA:</td><td>${data.estimatedDeliveryTime}</td></tr>` : ''}
              </table>
            </div>

            <div style="background: #e3f2fd; padding: 20px; border-radius: 8px; margin: 20px 0; border-left: 4px solid #2196f3;">
              <h3 style="color: #1976d2; margin-top: 0;">ðŸ“ Pickup Information</h3>
              <table style="width: 100%; border-spacing: 0;">
                <tr><td style="padding: 5px 0; font-weight: bold; width: 40%;">Vendor:</td><td>${data.vendorName}</td></tr>
                <tr><td style="padding: 5px 0; font-weight: bold;">Vendor Phone:</td><td>${data.vendorPhone}</td></tr>
                <tr><td style="padding: 5px 0; font-weight: bold;">Pickup Address:</td><td>${data.pickupAddress}</td></tr>
              </table>
            </div>

            <div style="background: #f3e5f5; padding: 20px; border-radius: 8px; margin: 20px 0; border-left: 4px solid #9c27b0;">
              <h3 style="color: #7b1fa2; margin-top: 0;">ðŸ  Delivery Information</h3>
              <table style="width: 100%; border-spacing: 0;">
                <tr><td style="padding: 5px 0; font-weight: bold; width: 40%;">Delivery Address:</td><td>${data.deliveryAddress}</td></tr>
                ${data.specialInstructions ? `<tr><td style="padding: 5px 0; font-weight: bold;">Special Instructions:</td><td>${data.specialInstructions}</td></tr>` : ''}
              </table>
            </div>

            <div style="background: linear-gradient(135deg, #FF4605, #e63900); color: white; padding: 20px; border-radius: 8px; margin: 30px 0; text-align: center;">
              <h3 style="margin: 0 0 10px 0;">âš¡ Action Required</h3>
              <p style="margin: 0; font-size: 16px;">Please coordinate with the vendor for pickup and provide tracking updates via our system.</p>
            </div>

            <div style="text-align: center; padding: 20px 0; border-top: 1px solid #eee;">
              <p style="color: #666; font-size: 12px; margin: 0;">
                This is an automated notification from BuyLock Marketplace.<br>
                Powered by Modern Minds Couriers Integration
              </p>
            </div>
          </div>
        </div>
      `
    };

    try {
      await this.transporter.sendMail(emailContent);
      console.log(`âœ… Email sent successfully to ${provider.contactEmail}`);
      return true;
    } catch (error) {
      console.error('Gmail send error:', error);
      return false;
    }
  }

  private async sendSMSNotification(provider: DeliveryProvider, data: NotificationData): Promise<boolean> {
    if (!provider.contactPhone) {
      console.error('No contact phone for SMS notification');
      return false;
    }

    // For now, we'll simulate SMS sending
    // In production, integrate with SMS provider (Twilio, Africa's Talking, etc.)
    console.log(`SMS to ${provider.contactPhone}: New delivery order ${data.orderId} ready for pickup from ${data.vendorName}. Contact: ${data.vendorPhone}`);

    // TODO: Implement actual SMS sending
    return true;
  }

  private async sendWebhookNotification(provider: DeliveryProvider, data: NotificationData): Promise<boolean> {
    if (!provider.webhookNotificationUrl) {
      console.error('No webhook URL configured for provider');
      return false;
    }

    const webhookPayload = {
      event: 'new_delivery_order',
      orderId: data.orderId,
      providerId: provider.id,
      timestamp: new Date().toISOString(),
      orderDetails: {
        customer: {
          name: data.customerName,
          phone: data.customerPhone,
        },
        vendor: {
          name: data.vendorName,
          phone: data.vendorPhone,
        },
        pickup: {
          address: data.pickupAddress,
        },
        delivery: {
          address: data.deliveryAddress,
          instructions: data.specialInstructions,
        },
        package: {
          description: data.packageDescription,
        },
        payment: {
          deliveryFee: data.deliveryFee,
        },
        estimatedDeliveryTime: data.estimatedDeliveryTime,
      }
    };

    try {
      const response = await fetch(provider.webhookNotificationUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'User-Agent': 'BuyLock-Delivery-System/1.0',
        },
        body: JSON.stringify(webhookPayload),
      });

      if (response.ok) {
        console.log(`Webhook notification sent successfully to ${provider.name}`);
        return true;
      } else {
        console.error(`Webhook failed with status ${response.status}`);
        return false;
      }
    } catch (error) {
      console.error('Webhook notification error:', error);
      return false;
    }
  }

  async notifyDispatchService(data: NotificationData): Promise<boolean> {
    // For the internal dispatch service, we'll use a special internal notification
    console.log(`Internal dispatch notification: Order ${data.orderId} ready for internal delivery processing`);

    // In a real microservice architecture, this would be a message queue or internal API call
    // For now, we'll just log and return success
    return true;
  }
}

export const notificationService = new NotificationService();

server/objectAcl.ts.content:
import { File } from "@google-cloud/storage";

const ACL_POLICY_METADATA_KEY = "custom:aclPolicy";

// The type of the access group.
//
// Can be flexibly defined according to the use case.
//
// Examples:
// - USER_LIST: the users from a list stored in the database;
// - EMAIL_DOMAIN: the users whose email is in a specific domain;
// - GROUP_MEMBER: the users who are members of a specific group;
// - SUBSCRIBER: the users who are subscribers of a specific service / content
//   creator.
export enum ObjectAccessGroupType {}

// The logic user group that can access the object.
export interface ObjectAccessGroup {
  // The type of the access group.
  type: ObjectAccessGroupType;
  // The logic id that is enough to identify the qualified group members.
  //
  // It may have different format for different types. For example:
  // - for USER_LIST, the id could be the user list db entity id, and the
  //   user list db entity could contain a bunch of user ids. User needs
  //   to be a member of the user list to be able to access the object.
  // - for EMAIL_DOMAIN, the id could be the email domain, and the user needs
  //   to have an email with the domain to be able to access the object.
  // - for GROUP_MEMBER, the id could be the group db entity id, and the
  //   group db entity could contain a bunch of user ids. User needs to be
  //   a member of the group to be able to access the object.
  // - for SUBSCRIBER, the id could be the subscriber db entity id, and the
  //   subscriber db entity could contain a bunch of user ids. User needs to
  //   be a subscriber to be able to access the object.
  id: string;
}

export enum ObjectPermission {
  READ = "read",
  WRITE = "write",
}

export interface ObjectAclRule {
  group: ObjectAccessGroup;
  permission: ObjectPermission;
}

// The ACL policy of the object.
// This would be set as part of the object custom metadata:
// - key: "custom:aclPolicy"
// - value: JSON string of the ObjectAclPolicy object.
export interface ObjectAclPolicy {
  owner: string;
  visibility: "public" | "private";
  aclRules?: Array<ObjectAclRule>;
}

// Check if the requested permission is allowed based on the granted permission.
function isPermissionAllowed(
  requested: ObjectPermission,
  granted: ObjectPermission,
): boolean {
  // Users granted with read or write permissions can read the object.
  if (requested === ObjectPermission.READ) {
    return [ObjectPermission.READ, ObjectPermission.WRITE].includes(granted);
  }

  // Only users granted with write permissions can write the object.
  return granted === ObjectPermission.WRITE;
}

// The base class for all access groups.
//
// Different types of access groups can be implemented according to the use case.
abstract class BaseObjectAccessGroup implements ObjectAccessGroup {
  constructor(
    public readonly type: ObjectAccessGroupType,
    public readonly id: string,
  ) {}

  // Check if the user is a member of the group.
  public abstract hasMember(userId: string): Promise<boolean>;
}

function createObjectAccessGroup(
  group: ObjectAccessGroup,
): BaseObjectAccessGroup {
  switch (group.type) {
    // Implement the case for each type of access group to instantiate.
    //
    // For example:
    // case "USER_LIST":
    //   return new UserListAccessGroup(group.id);
    // case "EMAIL_DOMAIN":
    //   return new EmailDomainAccessGroup(group.id);
    // case "GROUP_MEMBER":
    //   return new GroupMemberAccessGroup(group.id);
    // case "SUBSCRIBER":
    //   return new SubscriberAccessGroup(group.id);
    default:
      throw new Error(`Unknown access group type: ${group.type}`);
  }
}

// Sets the ACL policy to the object metadata.
export async function setObjectAclPolicy(
  objectFile: File,
  aclPolicy: ObjectAclPolicy,
): Promise<void> {
  const [exists] = await objectFile.exists();
  if (!exists) {
    throw new Error(`Object not found: ${objectFile.name}`);
  }

  await objectFile.setMetadata({
    metadata: {
      [ACL_POLICY_METADATA_KEY]: JSON.stringify(aclPolicy),
    },
  });
}

// Gets the ACL policy from the object metadata.
export async function getObjectAclPolicy(
  objectFile: File,
): Promise<ObjectAclPolicy | null> {
  const [metadata] = await objectFile.getMetadata();
  const aclPolicy = metadata?.metadata?.[ACL_POLICY_METADATA_KEY];
  if (!aclPolicy) {
    return null;
  }
  return JSON.parse(aclPolicy as string);
}

// Checks if the user can access the object.
export async function canAccessObject({
  userId,
  objectFile,
  requestedPermission,
}: {
  userId?: string;
  objectFile: File;
  requestedPermission: ObjectPermission;
}): Promise<boolean> {
  // When this function is called, the acl policy is required.
  const aclPolicy = await getObjectAclPolicy(objectFile);
  if (!aclPolicy) {
    return false;
  }

  // Public objects are always accessible for read.
  if (
    aclPolicy.visibility === "public" &&
    requestedPermission === ObjectPermission.READ
  ) {
    return true;
  }

  // Access control requires the user id.
  if (!userId) {
    return false;
  }

  // The owner of the object can always access it.
  if (aclPolicy.owner === userId) {
    return true;
  }

  // Go through the ACL rules to check if the user has the required permission.
  for (const rule of aclPolicy.aclRules || []) {
    const accessGroup = createObjectAccessGroup(rule.group);
    if (
      (await accessGroup.hasMember(userId)) &&
      isPermissionAllowed(requestedPermission, rule.permission)
    ) {
      return true;
    }
  }

  return false;
}

server/objectStorage.ts.content:
import { Storage, File } from "@google-cloud/storage";
import { Response } from "express";
import { randomUUID } from "crypto";
import {
  ObjectAclPolicy,
  ObjectPermission,
  canAccessObject,
  getObjectAclPolicy,
  setObjectAclPolicy,
} from "./objectAcl";

const REPLIT_SIDECAR_ENDPOINT = "http://127.0.0.1:1106";

// The object storage client is used to interact with the object storage service.
export const objectStorageClient = new Storage({
  credentials: {
    audience: "replit",
    subject_token_type: "access_token",
    token_url: `${REPLIT_SIDECAR_ENDPOINT}/token`,
    type: "external_account",
    credential_source: {
      url: `${REPLIT_SIDECAR_ENDPOINT}/credential`,
      format: {
        type: "json",
        subject_token_field_name: "access_token",
      },
    },
    universe_domain: "googleapis.com",
  },
  projectId: "",
});

export class ObjectNotFoundError extends Error {
  constructor() {
    super("Object not found");
    this.name = "ObjectNotFoundError";
    Object.setPrototypeOf(this, ObjectNotFoundError.prototype);
  }
}

// The object storage service is used to interact with the object storage service.
export class ObjectStorageService {
  constructor() {}

  // Gets the public object search paths.
  getPublicObjectSearchPaths(): Array<string> {
    const pathsStr = process.env.PUBLIC_OBJECT_SEARCH_PATHS || "";
    const paths = Array.from(
      new Set(
        pathsStr
          .split(",")
          .map((path) => path.trim())
          .filter((path) => path.length > 0)
      )
    );
    if (paths.length === 0) {
      throw new Error(
        "PUBLIC_OBJECT_SEARCH_PATHS not set. Create a bucket in 'Object Storage' " +
          "tool and set PUBLIC_OBJECT_SEARCH_PATHS env var (comma-separated paths)."
      );
    }
    return paths;
  }

  // Gets the private object directory.
  getPrivateObjectDir(): string {
    const dir = process.env.PRIVATE_OBJECT_DIR || "";
    if (!dir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }
    return dir;
  }

  // Search for a public object from the search paths.
  async searchPublicObject(filePath: string): Promise<File | null> {
    for (const searchPath of this.getPublicObjectSearchPaths()) {
      const fullPath = `${searchPath}/${filePath}`;

      // Full path format: /<bucket_name>/<object_name>
      const { bucketName, objectName } = parseObjectPath(fullPath);
      const bucket = objectStorageClient.bucket(bucketName);
      const file = bucket.file(objectName);

      // Check if file exists
      const [exists] = await file.exists();
      if (exists) {
        return file;
      }
    }

    return null;
  }

  // Downloads an object to the response.
  async downloadObject(file: File, res: Response, cacheTtlSec: number = 3600) {
    try {
      // Get file metadata
      const [metadata] = await file.getMetadata();
      // Get the ACL policy for the object.
      const aclPolicy = await getObjectAclPolicy(file);
      const isPublic = aclPolicy?.visibility === "public";
      // Set appropriate headers
      res.set({
        "Content-Type": metadata.contentType || "application/octet-stream",
        "Content-Length": metadata.size,
        "Cache-Control": `${
          isPublic ? "public" : "private"
        }, max-age=${cacheTtlSec}`,
      });

      // Stream the file to the response
      const stream = file.createReadStream();

      stream.on("error", (err) => {
        console.error("Stream error:", err);
        if (!res.headersSent) {
          res.status(500).json({ error: "Error streaming file" });
        }
      });

      stream.pipe(res);
    } catch (error) {
      console.error("Error downloading file:", error);
      if (!res.headersSent) {
        res.status(500).json({ error: "Error downloading file" });
      }
    }
  }

  // Gets the upload URL for an object entity.
  async getObjectEntityUploadURL(): Promise<string> {
    const privateObjectDir = this.getPrivateObjectDir();
    if (!privateObjectDir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }

    const objectId = randomUUID();
    const fullPath = `${privateObjectDir}/uploads/${objectId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    // Sign URL for PUT method with TTL
    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900,
    });
  }

  // Gets the object entity file from the object path.
  async getObjectEntityFile(objectPath: string): Promise<File> {
    if (!objectPath.startsWith("/objects/")) {
      throw new ObjectNotFoundError();
    }

    const parts = objectPath.slice(1).split("/");
    if (parts.length < 2) {
      throw new ObjectNotFoundError();
    }

    const entityId = parts.slice(1).join("/");
    let entityDir = this.getPrivateObjectDir();
    if (!entityDir.endsWith("/")) {
      entityDir = `${entityDir}/`;
    }
    const objectEntityPath = `${entityDir}${entityId}`;
    const { bucketName, objectName } = parseObjectPath(objectEntityPath);
    const bucket = objectStorageClient.bucket(bucketName);
    const objectFile = bucket.file(objectName);
    const [exists] = await objectFile.exists();
    if (!exists) {
      throw new ObjectNotFoundError();
    }
    return objectFile;
  }

  normalizeObjectEntityPath(
    rawPath: string,
  ): string {
    if (!rawPath.startsWith("https://storage.googleapis.com/")) {
      return rawPath;
    }

    // Extract the path from the URL by removing query parameters and domain
    const url = new URL(rawPath);
    const rawObjectPath = url.pathname;

    let objectEntityDir = this.getPrivateObjectDir();
    if (!objectEntityDir.endsWith("/")) {
      objectEntityDir = `${objectEntityDir}/`;
    }

    if (!rawObjectPath.startsWith(objectEntityDir)) {
      return rawObjectPath;
    }

    // Extract the entity ID from the path
    const entityId = rawObjectPath.slice(objectEntityDir.length);
    return `/objects/${entityId}`;
  }

  // Tries to set the ACL policy for the object entity and return the normalized path.
  async trySetObjectEntityAclPolicy(
    rawPath: string,
    aclPolicy: ObjectAclPolicy
  ): Promise<string> {
    const normalizedPath = this.normalizeObjectEntityPath(rawPath);
    if (!normalizedPath.startsWith("/")) {
      return normalizedPath;
    }

    const objectFile = await this.getObjectEntityFile(normalizedPath);
    await setObjectAclPolicy(objectFile, aclPolicy);
    return normalizedPath;
  }

  // Checks if the user can access the object entity.
  async canAccessObjectEntity({
    userId,
    objectFile,
    requestedPermission,
  }: {
    userId?: string;
    objectFile: File;
    requestedPermission?: ObjectPermission;
  }): Promise<boolean> {
    return canAccessObject({
      userId,
      objectFile,
      requestedPermission: requestedPermission ?? ObjectPermission.READ,
    });
  }
}

function parseObjectPath(path: string): {
  bucketName: string;
  objectName: string;
} {
  if (!path.startsWith("/")) {
    path = `/${path}`;
  }
  const pathParts = path.split("/");
  if (pathParts.length < 3) {
    throw new Error("Invalid path: must contain at least a bucket name");
  }

  const bucketName = pathParts[1];
  const objectName = pathParts.slice(2).join("/");

  return {
    bucketName,
    objectName,
  };
}

async function signObjectURL({
  bucketName,
  objectName,
  method,
  ttlSec,
}: {
  bucketName: string;
  objectName: string;
  method: "GET" | "PUT" | "DELETE" | "HEAD";
  ttlSec: number;
}): Promise<string> {
  const request = {
    bucket_name: bucketName,
    object_name: objectName,
    method,
    expires_at: new Date(Date.now() + ttlSec * 1000).toISOString(),
  };
  const response = await fetch(
    `${REPLIT_SIDECAR_ENDPOINT}/object-storage/signed-object-url`,
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(request),
    }
  );
  if (!response.ok) {
    throw new Error(
      `Failed to sign object URL, errorcode: ${response.status}, ` +
        `make sure you're running on Replit`
    );
  }

  const { signed_url: signedURL } = await response.json();
  return signedURL;
}

server/paystackService.ts.content:
// Paystack API service for subaccount and transfer management
interface PaystackConfig {
  secretKey: string;
  baseUrl: string;
}

interface PaystackSubaccountData {
  business_name: string;
  settlement_bank: string;
  account_number: string;
  percentage_charge: number;
  description?: string;
  primary_contact_email?: string;
  primary_contact_name?: string;
  primary_contact_phone?: string;
  metadata?: Record<string, any>;
}

interface PaystackTransferData {
  source: string;
  amount: number;
  recipient: string;
  reason?: string;
  currency?: string;
}

interface PaystackTransferRecipientData {
  type: string;
  name: string;
  account_number: string;
  bank_code: string;
  currency?: string;
  description?: string;
  metadata?: Record<string, any>;
}

export class PaystackService {
  private config: PaystackConfig;

  constructor(secretKey?: string) {
    this.config = {
      secretKey: secretKey || process.env.PAYSTACK_SECRET_KEY || '',
      baseUrl: 'https://api.paystack.co',
    };

    if (!this.config.secretKey) {
      throw new Error('Paystack secret key is required');
    }
  }

  private async makeRequest(endpoint: string, method: 'GET' | 'POST' = 'GET', data?: any): Promise<any> {
    const url = `${this.config.baseUrl}${endpoint}`;
    const headers = {
      'Authorization': `Bearer ${this.config.secretKey}`,
      'Content-Type': 'application/json',
    };

    const options: RequestInit = {
      method,
      headers,
    };

    if (data && method === 'POST') {
      options.body = JSON.stringify(data);
    }

    try {
      const response = await fetch(url, options);
      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.message || `Paystack API error: ${response.status}`);
      }

      return result;
    } catch (error) {
      console.error(`Paystack API request failed:`, error);
      throw error;
    }
  }

  // Create a subaccount for a vendor
  async createSubaccount(subaccountData: PaystackSubaccountData): Promise<any> {
    console.log('Creating Paystack subaccount:', subaccountData.business_name);

    try {
      const response = await this.makeRequest('/subaccount', 'POST', subaccountData);
      return response.data;
    } catch (error) {
      console.error('Failed to create Paystack subaccount:', error);
      throw error;
    }
  }

  // Get subaccount details
  async getSubaccount(subaccountCode: string): Promise<any> {
    try {
      const response = await this.makeRequest(`/subaccount/${subaccountCode}`);
      return response.data;
    } catch (error) {
      console.error('Failed to get Paystack subaccount:', error);
      throw error;
    }
  }

  // Update subaccount
  async updateSubaccount(subaccountCode: string, updateData: Partial<PaystackSubaccountData>): Promise<any> {
    try {
      const response = await this.makeRequest(`/subaccount/${subaccountCode}`, 'POST', updateData);
      return response.data;
    } catch (error) {
      console.error('Failed to update Paystack subaccount:', error);
      throw error;
    }
  }

  // Create transfer recipient
  async createTransferRecipient(recipientData: PaystackTransferRecipientData): Promise<any> {
    console.log('Creating transfer recipient:', recipientData.name);

    try {
      const response = await this.makeRequest('/transferrecipient', 'POST', recipientData);
      return response.data;
    } catch (error) {
      console.error('Failed to create transfer recipient:', error);
      throw error;
    }
  }

  // Initiate transfer
  async initiateTransfer(transferData: PaystackTransferData): Promise<any> {
    console.log('Initiating transfer:', transferData.amount, 'kobo to', transferData.recipient);

    try {
      const response = await this.makeRequest('/transfer', 'POST', transferData);
      return response.data;
    } catch (error) {
      console.error('Failed to initiate transfer:', error);
      throw error;
    }
  }

  // Get transfer details
  async getTransfer(transferId: string): Promise<any> {
    try {
      const response = await this.makeRequest(`/transfer/${transferId}`);
      return response.data;
    } catch (error) {
      console.error('Failed to get transfer details:', error);
      throw error;
    }
  }

  // Verify transfer
  async verifyTransfer(transferCode: string): Promise<any> {
    try {
      const response = await this.makeRequest(`/transfer/verify/${transferCode}`);
      return response.data;
    } catch (error) {
      console.error('Failed to verify transfer:', error);
      throw error;
    }
  }

  // Get list of banks for Kenya
  async getBanks(country: string = 'kenya'): Promise<any[]> {
    try {
      const response = await this.makeRequest(`/bank?country=${country}`);
      return response.data;
    } catch (error) {
      console.error('Failed to get banks list:', error);
      throw error;
    }
  }

  // Resolve bank account details
  async resolveAccountNumber(accountNumber: string, bankCode: string): Promise<any> {
    try {
      const response = await this.makeRequest(`/bank/resolve?account_number=${accountNumber}&bank_code=${bankCode}`);
      return response.data;
    } catch (error) {
      console.error('Failed to resolve bank account:', error);
      throw error;
    }
  }

  // Convert amount from KES to kobo (Paystack uses kobo as the smallest unit)
  kesToKobo(amountInKes: number): number {
    return Math.round(amountInKes * 100);
  }

  // Convert amount from kobo to KES
  koboToKes(amountInKobo: number): number {
    return amountInKobo / 100;
  }

  // Helper method to create subaccount for vendor
  async createVendorSubaccount(vendor: {
    businessName: string;
    contactName: string;
    email: string;
    bankName: string;
    bankCode?: string;
    accountNumber: string;
    accountName: string;
  }): Promise<{ subaccountId: string; subaccountCode: string }> {
    // First, resolve the bank code if not provided
    let bankCode = vendor.bankCode;
    if (!bankCode) {
      const banks = await this.getBanks();
      const bank = banks.find(b => 
        b.name.toLowerCase().includes(vendor.bankName.toLowerCase()) ||
        vendor.bankName.toLowerCase().includes(b.name.toLowerCase())
      );
      if (!bank) {
        throw new Error(`Bank not found: ${vendor.bankName}`);
      }
      bankCode = bank.code;
    }

    // Verify the account details
    try {
      await this.resolveAccountNumber(vendor.accountNumber, bankCode);
    } catch (error) {
      throw new Error(`Invalid account details: ${error}`);
    }

    // Create the subaccount
    const subaccountData: PaystackSubaccountData = {
      business_name: vendor.businessName,
      settlement_bank: bankCode,
      account_number: vendor.accountNumber,
      percentage_charge: 80, // Vendor gets 80%, platform gets 20%
      description: `Subaccount for ${vendor.businessName}`,
      primary_contact_email: vendor.email,
      primary_contact_name: vendor.contactName,
      metadata: {
        account_name: vendor.accountName,
        created_by: 'buylock_marketplace',
        vendor_email: vendor.email
      }
    };

    const subaccount = await this.createSubaccount(subaccountData);

    return {
      subaccountId: subaccount.id.toString(),
      subaccountCode: subaccount.subaccount_code
    };
  }

  // Helper method to process vendor payout
  async processVendorPayout(vendor: {
    businessName: string;
    contactName: string;
    email: string;
    bankName: string;
    bankCode?: string;
    accountNumber: string;
    accountName: string;
  }, amount: number, reason?: string): Promise<{ transferId: string; transferCode: string }> {
    // First, resolve the bank code if not provided
    let bankCode = vendor.bankCode;
    if (!bankCode) {
      const banks = await this.getBanks();
      const bank = banks.find(b => 
        b.name.toLowerCase().includes(vendor.bankName.toLowerCase()) ||
        vendor.bankName.toLowerCase().includes(b.name.toLowerCase())
      );
      if (!bank) {
        throw new Error(`Bank not found: ${vendor.bankName}`);
      }
      bankCode = bank.code;
    }

    // Create transfer recipient
    const recipientData: PaystackTransferRecipientData = {
      type: 'nuban',
      name: vendor.accountName,
      account_number: vendor.accountNumber,
      bank_code: bankCode,
      currency: 'KES',
      description: `Transfer recipient for ${vendor.businessName}`,
      metadata: {
        business_name: vendor.businessName,
        vendor_email: vendor.email,
        created_by: 'buylock_marketplace'
      }
    };

    const recipient = await this.createTransferRecipient(recipientData);

    // Initiate transfer
    const transferData: PaystackTransferData = {
      source: 'balance',
      amount: this.kesToKobo(amount),
      recipient: recipient.recipient_code,
      reason: reason || `Payout to ${vendor.businessName}`,
      currency: 'KES'
    };

    const transfer = await this.initiateTransfer(transferData);

    return {
      transferId: transfer.id.toString(),
      transferCode: transfer.transfer_code
    };
  }
}

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.ORIGINAL_DATABASE_URL || process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET || 'ea18cd32d964cf85eb3b4575d880763ada2b58612ae923793df3b4d75b82b17c',
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

