import express from "express";
import path from "path";
import { registerVendorRoutes } from "./routes.js";
import { seedVendorDatabase } from "./seed.js";

const app = express();
const PORT = process.env.VENDOR_PORT || 5001;

// Middleware
app.use(express.json());
app.use(express.urlencoded({ extended: true }));

// CORS for development
app.use((req, res, next) => {
  res.header("Access-Control-Allow-Origin", "*");
  res.header("Access-Control-Allow-Methods", "GET, POST, PUT, DELETE, OPTIONS");
  res.header("Access-Control-Allow-Headers", "Origin, X-Requested-With, Content-Type, Accept, Authorization");
  if (req.method === "OPTIONS") {
    res.sendStatus(200);
  } else {
    next();
  }
});

async function startServer() {
  try {
    console.log("ðŸš€ Starting Vendor Dashboard server...");

    // TODO: Set up database tables first
    // await seedVendorDatabase();

    // Register routes
    const server = await registerVendorRoutes(app);

    // Serve static files in production
    if (process.env.NODE_ENV === "production") {
      app.use(express.static("dist"));
      app.get("*", (req, res) => {
        res.sendFile(path.join(__dirname, "../dist/index.html"));
      });
    }

    server.listen(PORT, "0.0.0.0", () => {
      console.log(`ðŸª Vendor Dashboard server running on port ${PORT}`);
      console.log(`ðŸ“± Access dashboard at: http://localhost:${PORT}`);
      console.log(`ðŸ“§ Demo login: vendor@buylock.com / password123`);
    });
  } catch (error) {
    console.error("âŒ Failed to start vendor dashboard server:", error);
    process.exit(1);
  }
}

startServer();

