}

export function ProductCard({ product, onAddToCart }: ProductCardProps) {
  return (
    <Link href={`/products/${product.slug}`}>
      <div className="bg-white rounded-xl shadow-sm hover:shadow-md transition-shadow border group cursor-pointer">
        <div className="relative">
          <img 
            src={product.imageUrl || "https://images.unsplash.com/photo-1560472354-b33ff0c44a43?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=250"} 
            alt={product.name} 
            className="w-full h-48 object-cover rounded-t-xl"
          />
          {product.isFeatured && (
            <div className="absolute top-3 left-3 bg-buylock-primary text-white px-2 py-1 rounded text-xs font-semibold">
              Featured
            </div>
          )}
          {product.stock === 0 && (
            <div className="absolute top-3 right-3 bg-red-500 text-white px-2 py-1 rounded text-xs font-semibold">
              Out of Stock
            </div>
          )}
        </div>
        <div className="p-6">
          <h3 className="font-semibold text-gray-900 mb-2 text-xl">{product.name}</h3>
          <p className="text-gray-600 mb-4 line-clamp-2">{product.shortDescription}</p>
          <div className="flex items-center mb-4">
            <div className="flex text-yellow-400 text-sm">
              {Array.from({ length: 5 }).map((_, i) => (
                <Star 
                  key={i} 
                  className={`w-4 h-4 ${i < Math.floor(parseFloat(product.rating || "0")) ? 'fill-current' : ''}`} 
                />
              ))}
            </div>
            <span className="text-gray-500 text-sm ml-2">({product.reviewCount || 0} reviews)</span>
          </div>
          <div className="flex flex-col gap-4">
            <PriceDisplay 
              price={product.price} 
              originalPrice={product.originalPrice}
              size="lg" 
            />
            <Button 
              className="bg-buylock-primary text-white hover:bg-buylock-primary/90 font-semibold w-full"
              onClick={(e) => {
                e.preventDefault();
                e.stopPropagation();
                onAddToCart?.(product);
              }}
              disabled={product.stock === 0}
            >
              <ShoppingCart className="w-4 h-4 mr-2" />
              {product.stock === 0 ? 'Out of Stock' : 'Add to Cart'}
            </Button>
          </div>
        </div>
      </div>
    </Link>
  );
}

client/src/components/ProximityBadge.tsx.content:
import { MapPin } from "lucide-react";
import { Badge } from "@/components/ui/badge";

interface ProximityBadgeProps {
  distance?: number;
  className?: string;
}

export function ProximityBadge({ distance, className = "" }: ProximityBadgeProps) {
  // Debug logging
  console.log("ProximityBadge received distance:", distance, typeof distance);

  // Show badge for any valid distance
  if (distance === undefined || distance === null || distance === Infinity) {
    return null;
  }

  const formatDistance = (distanceKm: number): string => {
    console.log("Formatting distance:", distanceKm, "type:", typeof distanceKm);
    if (distanceKm < 1) {
      return `${Math.round(distanceKm * 1000)}m away`;
    } else if (distanceKm < 10) {
      return `${distanceKm.toFixed(1)}km away`;
    } else {
      return `${Math.round(distanceKm)}km away`;
    }
  };

  const getDistanceColor = (distanceKm: number): string => {
    if (distanceKm < 5) return "bg-green-100 text-green-800";
    if (distanceKm < 15) return "bg-yellow-100 text-yellow-800";
    return "bg-gray-100 text-gray-600";
  };

  return (
    <Badge 
      variant="secondary" 
      className={`${getDistanceColor(distance)} ${className} text-xs font-medium`}
    >
      <MapPin className="w-3 h-3 mr-1" />
      {formatDistance(distance)}
    </Badge>
  );
}

