import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Truck, Package, Clock, MapPin, Phone, CheckCircle, AlertCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface Delivery {
  id: string;
  orderId: string;
  providerId: string;
  externalTrackingId?: string;
  status: string;
  pickupAddress: string;
  deliveryAddress: string;
  estimatedPickupTime?: string;
  actualPickupTime?: string;
  estimatedDeliveryTime?: string;
  actualDeliveryTime?: string;
  deliveryFee: string;
  packageDescription: string;
  customerPhone?: string;
  vendorPhone?: string;
  courierName?: string;
  courierPhone?: string;
  specialInstructions?: string;
  createdAt: string;
  updatedAt: string;
}

interface DeliveryProvider {
  id: string;
  name: string;
  type: string;
  contactPhone?: string;
  contactEmail?: string;
  estimatedDeliveryTime?: string;
}

interface PickupOrder {
  id: string;
  trackingNumber: string;
  deliveryAddress: string;
  totalAmount: string;
  courierName?: string;
  courierId?: string;
  user?: {
    firstName: string;
    lastName: string;
  };
}

export default function DeliveryPortal() {
  const [selectedStatus, setSelectedStatus] = useState<string>("all");
  const [selectedDelivery, setSelectedDelivery] = useState<Delivery | null>(null);
  const [pickupInstructions, setPickupInstructions] = useState<string>("");
  const [showInstructionsDialog, setShowInstructionsDialog] = useState<string | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: deliveries, isLoading } = useQuery<Delivery[]>({
    queryKey: ['/api/deliveries'],
  });

  const { data: pickupOrders, isLoading: isLoadingPickups } = useQuery<PickupOrder[]>({
    queryKey: ['/api/deliveries/pickup-orders'],
  });

  const { data: providers } = useQuery<DeliveryProvider[]>({
    queryKey: ['/api/delivery/providers'],
  });

  const createDeliveryMutation = useMutation({
    mutationFn: async (data: { 
      orderId: string; 
      providerId: string;
      pickupInstructions?: string;
    }) => {
      return await apiRequest('/api/deliveries/create', 'POST', {
        orderId: data.orderId,
        providerId: data.providerId,
        pickupInstructions: data.pickupInstructions,
      });
    },
    onSuccess: (data, variables) => {
      queryClient.invalidateQueries({ queryKey: ['/api/deliveries'] });
      queryClient.invalidateQueries({ queryKey: ['/api/deliveries/pickup-orders'] });

      const courier = providers?.find(p => p.id === variables.providerId);
      const courierName = courier?.name || 'Selected courier';
      const estimatedTime = courier?.estimatedDeliveryTime || '2-4 hours';

      toast({
        title: "Pickup Requested Successfully! ðŸšš",
        description: `${courierName} has been notified. Expected pickup within ${estimatedTime}.`,
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to assign order to courier",
        variant: "destructive",
      });
    },
  });

  const updateDeliveryMutation = useMutation({
    mutationFn: async (data: { 
      deliveryId: string; 
      status: string; 
      description?: string; 
      trackingId?: string;
    }) => {
      return await apiRequest(`/api/deliveries/${data.deliveryId}/status`, 'PUT', {
        status: data.status,
        description: data.description,
        trackingId: data.trackingId,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/deliveries'] });
      toast({
        title: "Success",
        description: "Delivery status updated successfully",
      });
      setSelectedDelivery(null);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update delivery status",
        variant: "destructive",
      });
    },
  });

  const filteredDeliveries = deliveries?.filter(delivery => 
    selectedStatus === "all" || delivery.status === selectedStatus
  ) || [];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'pickup_scheduled':
        return 'bg-blue-100 text-blue-800';
      case 'picked_up':
        return 'bg-indigo-100 text-indigo-800';
      case 'in_transit':
        return 'bg-purple-100 text-purple-800';
      case 'out_for_delivery':
        return 'bg-orange-100 text-orange-800';
      case 'delivered':
        return 'bg-green-100 text-green-800';
      case 'failed':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'delivered':
        return <CheckCircle className="w-4 h-4" />;
      case 'failed':
        return <AlertCircle className="w-4 h-4" />;
      default:
        return <Clock className="w-4 h-4" />;
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 p-6">
        <div className="max-w-7xl mx-auto">
          <div className="text-center py-8">Loading delivery portal...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <Truck className="w-8 h-8 text-buylock-primary" />
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Delivery Portal</h1>
              <p className="text-gray-600">Manage deliveries and dispatch operations</p>
            </div>
          </div>
          <Badge variant="outline" className="text-lg px-3 py-1">
            {filteredDeliveries.length} Deliveries
          </Badge>
        </div>

        {/* Orders Ready for Pickup */}
        {pickupOrders && pickupOrders.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <Package className="w-5 h-5 text-orange-600" />
                <span>Orders Ready for Pickup ({pickupOrders.length})</span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {pickupOrders.map((order) => (
                  <div key={order.id} className="border border-orange-200 rounded-lg p-4 bg-orange-50">
                    <div className="flex items-center justify-between mb-3">
                      <h4 className="font-semibold text-gray-900">Order #{order.trackingNumber}</h4>
                      <Badge className="bg-orange-100 text-orange-800">Ready</Badge>
                    </div>

                    <div className="space-y-2 text-sm text-gray-600 mb-4">
                      <div className="flex items-center space-x-2">
                        <MapPin className="w-4 h-4" />
                        <span>{order.deliveryAddress}</span>
                      </div>
                      <div className="flex items-center space-x-2">
                        <Package className="w-4 h-4" />
                        <span>KES {parseFloat(order.totalAmount).toLocaleString()}</span>
                      </div>
                      {order.user && (
                        <div className="flex items-center space-x-2">
                          <Phone className="w-4 h-4" />
                          <span>{order.user.firstName} {order.user.lastName}</span>
                        </div>
                      )}
                      {(() => {
                        // Use assigned courier or default to Fargo courier
                        const courierId = order.courierId || 'fargo-courier';
                        const courier = providers?.find(p => p.id === courierId);
                        const courierName = order.courierName || courier?.name || 'Fargo Courier Services';

                        return (
                          <div className="space-y-1">
                            <div className="flex items-center space-x-2">
                              <Truck className="w-4 h-4" />
                              <span className="font-medium">Courier: {courierName}</span>
                            </div>
                            {courier && (
                              <div className="ml-6 space-y-1 text-xs text-gray-500">
                                <div>ðŸ“ž {courier.contactPhone || 'Contact via platform'}</div>
                                <div>ðŸ“§ {courier.contactEmail || 'orders@buylock.co.ke'}</div>
                                <div>â±ï¸ ETA: {courier.estimatedDeliveryTime || '2-4 hours'}</div>
                              </div>
                            )}
                          </div>
                        );
                      })()}
                    </div>

                    <Button 
                      onClick={() => {
                        setShowInstructionsDialog(order.id);
                        setPickupInstructions("");
                      }}
                      disabled={createDeliveryMutation.isPending}
                      className="w-full bg-[#FF4605] hover:bg-[#E63E05] text-white"
                    >
                      <Truck className="w-4 h-4 mr-2" />
                      {createDeliveryMutation.isPending ? 'Requesting...' : 
                        `Request ${order.courierName || providers?.find(p => p.id === (order.courierId || 'fargo-courier'))?.name || 'Fargo Courier Services'} Pickup`}
                    </Button>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Status Filter */}
        <Card>
          <CardHeader>
            <CardTitle>Filter by Status</CardTitle>
          </CardHeader>
          <CardContent>
            <Select value={selectedStatus} onValueChange={setSelectedStatus}>
              <SelectTrigger className="w-64">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Deliveries</SelectItem>
                <SelectItem value="pending">Pending</SelectItem>
                <SelectItem value="pickup_scheduled">Pickup Scheduled</SelectItem>
                <SelectItem value="picked_up">Picked Up</SelectItem>
                <SelectItem value="in_transit">In Transit</SelectItem>
                <SelectItem value="out_for_delivery">Out for Delivery</SelectItem>
                <SelectItem value="delivered">Delivered</SelectItem>
                <SelectItem value="failed">Failed</SelectItem>
              </SelectContent>
            </Select>
          </CardContent>
        </Card>

        {/* Deliveries Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredDeliveries.map((delivery) => (
            <Card key={delivery.id} className="hover:shadow-lg transition-shadow">
              <CardHeader className="pb-3">
                <div className="flex items-center justify-between">
                  <CardTitle className="text-lg">Order {delivery.orderId.slice(-8)}</CardTitle>
                  <Badge className={getStatusColor(delivery.status)}>
                    <div className="flex items-center space-x-1">
                      {getStatusIcon(delivery.status)}
                      <span className="capitalize">{delivery.status.replace('_', ' ')}</span>
                    </div>
                  </Badge>
                </div>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-2">
                  <div className="flex items-center space-x-2 text-sm">
                    <Package className="w-4 h-4 text-gray-500" />
                    <span>{delivery.packageDescription}</span>
                  </div>
                  <div className="flex items-center space-x-2 text-sm">
                    <MapPin className="w-4 h-4 text-gray-500" />
                    <span className="truncate">{delivery.deliveryAddress}</span>
                  </div>
                  {delivery.customerPhone && (
                    <div className="flex items-center space-x-2 text-sm">
                      <Phone className="w-4 h-4 text-gray-500" />
                      <span>{delivery.customerPhone}</span>
                    </div>
                  )}
                </div>

                <div className="grid grid-cols-2 gap-2 text-sm">
                  <div>
                    <p className="text-gray-600">Delivery Fee</p>
                    <p className="font-medium">KES {Number(delivery.deliveryFee).toLocaleString()}</p>
                  </div>
                  <div>
                    <p className="text-gray-600">Tracking ID</p>
                    <p className="font-medium text-xs">
                      {delivery.externalTrackingId || 'Not assigned'}
                    </p>
                  </div>
                </div>

                <Dialog>
                  <DialogTrigger asChild>
                    <Button 
                      variant="outline" 
                      className="w-full"
                      onClick={() => setSelectedDelivery(delivery)}
                    >
                      Manage Delivery
                    </Button>
                  </DialogTrigger>
                  <DialogContent className="max-w-md">
                    <DialogHeader>
                      <DialogTitle>Update Delivery Status</DialogTitle>
                    </DialogHeader>
                    <DeliveryUpdateForm 
                      delivery={delivery}
                      onUpdate={(status, description, trackingId) => {
                        updateDeliveryMutation.mutate({
                          deliveryId: delivery.id,
                          status,
                          description,
                          trackingId,
                        });
                      }}
                      isLoading={updateDeliveryMutation.isPending}
                    />
                  </DialogContent>
                </Dialog>
              </CardContent>
            </Card>
          ))}
        </div>

        {filteredDeliveries.length === 0 && (
          <Card>
            <CardContent className="text-center py-8">
              <Truck className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No deliveries found</h3>
              <p className="text-gray-500">
                {selectedStatus === "all" 
                  ? "No deliveries available in the system" 
                  : `No deliveries with status: ${selectedStatus.replace('_', ' ')}`}
              </p>
            </CardContent>
          </Card>
        )}

        {/* Dispatch Service Section */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Truck className="w-5 h-5" />
              <span>Dispatch Service</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-gray-600 mb-4">
              Internal dispatch operations for BuyLock delivery service. Orders assigned to our internal 
              dispatch service will appear here for processing.
            </p>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="text-center p-4 bg-blue-50 rounded-lg">
                <div className="text-2xl font-bold text-blue-600">
                  {filteredDeliveries.filter(d => d.providerId === 'dispatch_service').length}
                </div>
                <div className="text-sm text-blue-800">Dispatch Orders</div>
              </div>
              <div className="text-center p-4 bg-green-50 rounded-lg">
                <div className="text-2xl font-bold text-green-600">
                  {filteredDeliveries.filter(d => d.providerId === 'dispatch_service' && d.status === 'delivered').length}
                </div>
                <div className="text-sm text-green-800">Completed</div>
              </div>
              <div className="text-center p-4 bg-orange-50 rounded-lg">
                <div className="text-2xl font-bold text-orange-600">
                  {filteredDeliveries.filter(d => d.providerId === 'dispatch_service' && ['pending', 'picked_up', 'in_transit'].includes(d.status)).length}
                </div>
                <div className="text-sm text-orange-800">In Progress</div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Pickup Instructions Dialog */}
        <Dialog open={showInstructionsDialog !== null} onOpenChange={(open) => !open && setShowInstructionsDialog(null)}>
          <DialogContent className="max-w-md">
            <DialogHeader>
              <DialogTitle>Pickup Instructions</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="pickup-instructions">
                  Special Instructions for Courier (Optional)
                </Label>
                <Textarea
                  id="pickup-instructions"
                  value={pickupInstructions}
                  onChange={(e) => setPickupInstructions(e.target.value)}
                  placeholder="e.g., Call vendor first, use back entrance, package is fragile, pickup after 2 PM..."
                  rows={4}
                />
                <p className="text-sm text-gray-500">
                  These instructions will be included in the email sent to the courier.
                </p>
              </div>
              <div className="flex space-x-2">
                <Button
                  variant="outline"
                  onClick={() => setShowInstructionsDialog(null)}
                  className="w-full"
                >
                  Cancel
                </Button>
                <Button
                  onClick={() => {
                    if (showInstructionsDialog) {
                      const order = pickupOrders?.find(o => o.id === showInstructionsDialog);
                      if (order) {
                        const courierId = order.courierId || 'fargo-courier';

                        createDeliveryMutation.mutate({
                          orderId: order.id,
                          providerId: courierId,
                          pickupInstructions: pickupInstructions.trim() || undefined
                        });
                      }
                      setShowInstructionsDialog(null);
                      setPickupInstructions("");
                    }
                  }}
                  disabled={createDeliveryMutation.isPending}
                  className="w-full bg-[#FF4605] hover:bg-[#E63E05] text-white"
                >
                  <Truck className="w-4 h-4 mr-2" />
                  {createDeliveryMutation.isPending ? 'Requesting...' : 'Request Pickup'}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );
}

interface DeliveryUpdateFormProps {
  delivery: Delivery;
  onUpdate: (status: string, description?: string, trackingId?: string) => void;
  isLoading: boolean;
}

function DeliveryUpdateForm({ delivery, onUpdate, isLoading }: DeliveryUpdateFormProps) {
  const [status, setStatus] = useState(delivery.status);
  const [description, setDescription] = useState('');
  const [trackingId, setTrackingId] = useState(delivery.externalTrackingId || '');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onUpdate(status, description, trackingId);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="space-y-2">
        <Label htmlFor="status">Delivery Status</Label>
        <Select value={status} onValueChange={setStatus}>
          <SelectTrigger>
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="pending">Pending</SelectItem>
            <SelectItem value="pickup_scheduled">Pickup Scheduled</SelectItem>
            <SelectItem value="picked_up">Picked Up</SelectItem>
            <SelectItem value="in_transit">In Transit</SelectItem>
            <SelectItem value="out_for_delivery">Out for Delivery</SelectItem>
            <SelectItem value="delivered">Delivered</SelectItem>
            <SelectItem value="failed">Failed</SelectItem>
          </SelectContent>
        </Select>
      </div>

      <div className="space-y-2">
        <Label htmlFor="trackingId">Tracking ID</Label>
        <Input
          id="trackingId"
          value={trackingId}
          onChange={(e) => setTrackingId(e.target.value)}
          placeholder="Enter tracking ID from courier"
        />
      </div>

      <div className="space-y-2">
        <Label htmlFor="description">Update Description</Label>
        <Textarea
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Describe the status update..."
          rows={3}
        />
      </div>

      <Button type="submit" disabled={isLoading} className="w-full">
        {isLoading ? 'Updating...' : 'Update Delivery Status'}
      </Button>
    </form>
  );
}

client/src/pages/home.tsx.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { HeroSection } from "@/components/hero-section";
import { CategoryGrid } from "@/components/category-grid";
import { FlashDeals } from "@/components/flash-deals";
import { ProductCard } from "@/components/ProductCard";
import { ServiceCard } from "@/components/service-card";
import { WhyChoose } from "@/components/why-choose";
import { Newsletter } from "@/components/newsletter";
import { Footer } from "@/components/footer";
import { Header } from "@/components/header";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { Link } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import type { Product, Service } from "@shared/schema";

export default function Home() {
  const { toast } = useToast();
  const { isAuthenticated } = useAuth();
  const queryClient = useQueryClient();

  const { data: featuredProducts = [], isLoading: productsLoading, error: productsError } = useQuery<Product[]>({
    queryKey: ["/api/products", { featured: true, limit: 5 }],
    staleTime: 1000 * 60 * 5, // 5 minutes
    refetchOnWindowFocus: false,
    retry: 1,
    enabled: true, // Always enable this query regardless of auth state
  });

  const { data: featuredServices = [], isLoading: servicesLoading, error: servicesError } = useQuery<Service[]>({
    queryKey: ["/api/services", { featured: true, limit: 3 }],
    staleTime: 1000 * 60 * 5, // 5 minutes
    refetchOnWindowFocus: false,
    retry: 1,
    enabled: true, // Always enable this query regardless of auth state
  });

  const addToCartMutation = useMutation({
    mutationFn: async ({ productId }: { productId: string }) => {
      await apiRequest("/api/cart", "POST", {
        productId,
        quantity: 1,
      });
    },
    onSuccess: (_, { productId }) => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      const product = featuredProducts.find(p => p.id === productId);
      toast({
        title: "Added to cart",
        description: `${product?.name || 'Product'} has been added to your cart`,
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Login required",
          description: "Please log in to add items to cart",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 1500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add item to cart",
        variant: "destructive",
      });
    },
  });

  const handleAddToCart = (product: Product) => {
    if (!isAuthenticated) {
      toast({
        title: "Login required",
        description: "Please log in to add items to cart",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 1500);
      return;
    }

    addToCartMutation.mutate({ productId: product.id });
  };

  // Debug logging
  console.log("Featured Products Query:", { 
    data: featuredProducts, 
    loading: productsLoading, 
    error: productsError,
    dataLength: featuredProducts?.length || 0 
  });
  console.log("Featured Services Query:", { 
    data: featuredServices, 
    loading: servicesLoading, 
    error: servicesError,
    dataLength: featuredServices?.length || 0 
  });

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      <HeroSection />
      <CategoryGrid />
      <FlashDeals />

      {/* Featured Products */}
      <section className="container mx-auto px-4 py-12">
        <div className="flex justify-between items-center mb-8">
          <h2 className="text-3xl font-bold text-gray-900">Featured Products</h2>
          <Link href="/products">
            <Button variant="ghost" className="text-buylock-primary font-semibold hover:underline">
              View All
            </Button>
          </Link>
        </div>

        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-6">
          {productsLoading ? (
            Array.from({ length: 5 }).map((_, i) => (
              <div key={i} className="bg-white rounded-xl shadow-sm border">
                <Skeleton className="w-full h-48 rounded-t-xl" />
                <div className="p-4 space-y-3">
                  <Skeleton className="h-4 w-3/4" />
                  <Skeleton className="h-4 w-1/2" />
                  <Skeleton className="h-4 w-1/3" />
                  <Skeleton className="h-10 w-full" />
                </div>
              </div>
            ))
          ) : featuredProducts.length === 0 ? (
            <div className="col-span-full text-center py-12">
              <p className="text-gray-500">No featured products available</p>
            </div>
          ) : (
            featuredProducts.map((product) => (
              <ProductCard 
                key={product.id} 
                product={product} 
                onAddToCart={handleAddToCart}
              />
            ))
          )}
        </div>
      </section>

      {/* Featured Services */}
      <section className="bg-gray-100 py-12">
        <div className="container mx-auto px-4">
          <div className="flex justify-between items-center mb-8">
            <h2 className="text-3xl font-bold text-gray-900">Popular Services</h2>
            <Link href="/services">
              <Button variant="ghost" className="text-buylock-primary font-semibold hover:underline">
                View All Services
              </Button>
            </Link>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {servicesLoading ? (
              Array.from({ length: 3 }).map((_, i) => (
                <div key={i} className="bg-white rounded-xl shadow-sm border">
                  <Skeleton className="w-full h-48 rounded-t-xl" />
                  <div className="p-6 space-y-4">
                    <Skeleton className="h-6 w-3/4" />
                    <Skeleton className="h-4 w-full" />
                    <Skeleton className="h-4 w-1/2" />
                    <div className="flex justify-between items-center">
                      <Skeleton className="h-6 w-1/3" />
                      <Skeleton className="h-10 w-24" />
                    </div>
                  </div>
                </div>
              ))
            ) : featuredServices.length === 0 ? (
              <div className="col-span-full text-center py-12">
                <p className="text-gray-500">No featured services available</p>
              </div>
            ) : (
              featuredServices.map((service) => (
                <ServiceCard key={service.id} service={service} />
              ))
            )}
          </div>
        </div>
      </section>

      <WhyChoose />
      <Newsletter />
      <Footer />
    </div>
  );
}

client/src/pages/my-orders.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { format } from "date-fns";
import { 
  Package, 
  Truck, 
  CheckCircle, 
  XCircle, 
  Clock, 
  MapPin, 
  Eye,
  AlertCircle,
  Calendar,
  DollarSign,
  ShoppingBag,
  Play,
  Wrench,
  AlertTriangle
} from "lucide-react";
import { Header } from "@/components/header";
import { Footer } from "@/components/footer";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { Order, OrderTracking, OrderItem } from "@shared/schema";

interface OrderWithItems extends Order {
  orderItems: OrderItem[];
}

export default function MyOrders() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedOrder, setSelectedOrder] = useState<OrderWithItems | null>(null);
  const [trackingDialogOpen, setTrackingDialogOpen] = useState(false);

  // Currency formatting function for Kenyan Shillings
  const formatPrice = (price: string | number) => {
    const numPrice = typeof price === 'string' ? parseFloat(price) : price;
    return new Intl.NumberFormat('en-KE', {
      style: 'currency',
      currency: 'KES',
      minimumFractionDigits: 0,
    }).format(numPrice);
  };

  const { data: orders = [], isLoading } = useQuery<Order[]>({
    queryKey: ["/api/orders"],
  });

  const { data: orderTracking = [], isLoading: trackingLoading } = useQuery<OrderTracking[]>({
    queryKey: ["/api/orders", selectedOrder?.id, "tracking"],
    enabled: !!selectedOrder?.id,
  });

  const cancelOrderMutation = useMutation({
    mutationFn: async (orderId: string) => {
      const response = await fetch(`/api/orders/${orderId}/cancel`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
      });
      if (!response.ok) throw new Error("Failed to cancel order");
      return response.json();
    },
    onSuccess: (cancelledOrder) => {
      toast({
        title: "Order Cancelled",
        description: "Your order has been successfully cancelled.",
      });
      // Invalidate all order-related queries to ensure global update
      queryClient.invalidateQueries({ queryKey: ["/api/orders"] });
      queryClient.invalidateQueries({ queryKey: ["/api/vendor"], type: 'all' }); // For vendor dashboard
      queryClient.invalidateQueries({ queryKey: ["/api/admin"], type: 'all' }); // For admin portal
      queryClient.invalidateQueries({ queryKey: ["/api/deliveries"], type: 'all' }); // For delivery tracking

      // Also refetch the current orders to ensure immediate update
      queryClient.refetchQueries({ queryKey: ["/api/orders"] });

      console.log(`Order ${cancelledOrder.id} cancelled - cache invalidated`);
      setSelectedOrder(null);
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to cancel order",
        variant: "destructive",
      });
    },
  });

  const generateTrackingMutation = useMutation({
    mutationFn: async (orderId: string) => {
      const response = await fetch(`/api/orders/${orderId}/track`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
      });
      if (!response.ok) throw new Error("Failed to update tracking");
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Tracking Updated",
        description: "New tracking information has been generated.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/orders", selectedOrder?.id, "tracking"] });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update tracking",
        variant: "destructive",
      });
    },
  });

  const getOrderDetails = async (orderId: string) => {
    try {
      const response = await fetch(`/api/orders/${orderId}`);
      if (!response.ok) throw new Error("Failed to fetch order details");
      const order = await response.json();
      setSelectedOrder(order);
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to fetch order details",
        variant: "destructive",
      });
    }
  };

  const getStatusColor = (status: string | null) => {
    switch (status?.toLowerCase()) {
      case "pending": 
      case "pending_acceptance": return "bg-yellow-100 text-yellow-800";
      case "confirmed":
      case "accepted": return "bg-blue-100 text-blue-800";
      case "starting_job": return "bg-indigo-100 text-indigo-800";
      case "in_progress": return "bg-purple-100 text-purple-800";
      case "delayed": return "bg-orange-100 text-orange-800";
      case "almost_done": return "bg-teal-100 text-teal-800";
      case "shipped": return "bg-purple-100 text-purple-800";
      case "delivered":
      case "completed": return "bg-green-100 text-green-800";
      case "cancelled": return "bg-red-100 text-red-800";
      default: return "bg-gray-100 text-gray-800";
    }
  };

  const getStatusIcon = (status: string | null) => {
    switch (status?.toLowerCase()) {
      case "pending": 
      case "pending_acceptance": return <Clock className="w-4 h-4" />;
      case "confirmed":
      case "accepted": return <CheckCircle className="w-4 h-4" />;
      case "starting_job": return <Play className="w-4 h-4" />;
      case "in_progress": return <Wrench className="w-4 h-4" />;
      case "delayed": return <AlertTriangle className="w-4 h-4" />;
      case "almost_done": return <Clock className="w-4 h-4" />;
      case "shipped": return <Truck className="w-4 h-4" />;
      case "delivered": 
      case "completed": return <CheckCircle className="w-4 h-4" />;
      case "cancelled": return <XCircle className="w-4 h-4" />;
      default: return <AlertCircle className="w-4 h-4" />;
    }
  };

  const getStatusLabel = (status: string | null) => {
    switch (status?.toLowerCase()) {
      case "pending": return "Pending";
      case "pending_acceptance": return "Pending Acceptance";
      case "confirmed": return "Confirmed";
      case "accepted": return "Accepted";
      case "starting_job": return "Starting Job";
      case "in_progress": return "In Progress";
      case "delayed": return "Delayed";
      case "almost_done": return "Almost Done";
      case "shipped": return "Shipped";
      case "delivered": return "Delivered";
      case "completed": return "Completed";
      case "cancelled": return "Cancelled";
      default: return status ? status.charAt(0).toUpperCase() + status.slice(1) : 'Unknown';
    }
  };

  const ongoingOrders = orders.filter(order => 
    order.status && !["delivered", "cancelled"].includes(order.status.toLowerCase())
  );

  const pastOrders = orders.filter(order => 
    order.status && ["delivered", "cancelled"].includes(order.status.toLowerCase())
  );

  const OrderCard = ({ order }: { order: Order }) => (
    <Card className="hover:shadow-md transition-shadow">
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div>
            <CardTitle className="text-lg font-semibold">
              Order #{order.id.slice(-8).toUpperCase()}
            </CardTitle>
            <p className="text-sm text-gray-600 mt-1">
              {order.createdAt && format(new Date(order.createdAt), "MMM dd, yyyy 'at' h:mm a")}
            </p>
          </div>
          <Badge className={`${getStatusColor(order.status)} border-0`}>
            <span className="flex items-center gap-1">
              {getStatusIcon(order.status)}
              {getStatusLabel(order.status)}
            </span>
          </Badge>
        </div>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <DollarSign className="w-4 h-4 text-gray-500" />
            <span className="font-semibold">{formatPrice(order.totalAmount)}</span>
          </div>
          {order.trackingNumber && (
            <div className="flex items-center gap-2 text-sm text-gray-600">
              <Package className="w-4 h-4" />
              <span>#{order.trackingNumber}</span>
            </div>
          )}
        </div>

        {order.deliveryAddress && (
          <div className="flex items-start gap-2 text-sm text-gray-600">
            <MapPin className="w-4 h-4 mt-0.5" />
            <span>{order.deliveryAddress}</span>
          </div>
        )}

        <div className="flex gap-2 pt-2">
          <Button
            variant="outline"
            size="sm"
            onClick={() => getOrderDetails(order.id)}
            className="flex items-center gap-2"
          >
            <Eye className="w-4 h-4" />
            View Details
          </Button>

          {order.status && !["delivered", "cancelled"].includes(order.status.toLowerCase()) && (
            <>
              <Dialog open={trackingDialogOpen} onOpenChange={setTrackingDialogOpen}>
                <DialogTrigger asChild>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      setSelectedOrder(order as OrderWithItems);
                      setTrackingDialogOpen(true);
                    }}
                    className="flex items-center gap-2"
                  >
                    <Truck className="w-4 h-4" />
                    Track Order
                  </Button>
                </DialogTrigger>
              </Dialog>

              <Button
                variant="outline"
                size="sm"
                onClick={() => cancelOrderMutation.mutate(order.id)}
                disabled={cancelOrderMutation.isPending}
                className="flex items-center gap-2 text-red-600 hover:text-red-700 hover:bg-red-50"
              >
                <XCircle className="w-4 h-4" />
                Cancel
              </Button>
            </>
          )}
        </div>
      </CardContent>
    </Card>
  );

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">My Orders</h1>
          <p className="text-gray-600">Track and manage your order history</p>
        </div>

        <Tabs defaultValue="ongoing" className="space-y-6">
          <TabsList className="grid w-full max-w-md grid-cols-2">
            <TabsTrigger value="ongoing" className="flex items-center gap-2">
              <ShoppingBag className="w-4 h-4" />
              Ongoing ({ongoingOrders.length})
            </TabsTrigger>
            <TabsTrigger value="past" className="flex items-center gap-2">
              <Package className="w-4 h-4" />
              Past Orders ({pastOrders.length})
            </TabsTrigger>
          </TabsList>

          <TabsContent value="ongoing" className="space-y-4">
            {isLoading ? (
              <div className="grid gap-4">
                {Array.from({ length: 3 }).map((_, i) => (
                  <Card key={i} className="animate-pulse">
                    <CardHeader>
                      <div className="h-6 bg-gray-200 rounded w-1/3"></div>
                      <div className="h-4 bg-gray-200 rounded w-1/4"></div>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-3">
                        <div className="h-4 bg-gray-200 rounded w-1/2"></div>
                        <div className="h-4 bg-gray-200 rounded w-full"></div>
                        <div className="flex gap-2">
                          <div className="h-8 bg-gray-200 rounded w-24"></div>
                          <div className="h-8 bg-gray-200 rounded w-24"></div>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            ) : ongoingOrders.length > 0 ? (
              <div className="grid gap-4">
                {ongoingOrders.map((order) => (
                  <OrderCard key={order.id} order={order} />
                ))}
              </div>
            ) : (
              <Card className="text-center py-12">
                <CardContent>
                  <ShoppingBag className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                  <h3 className="text-lg font-semibold text-gray-900 mb-2">No ongoing orders</h3>
                  <p className="text-gray-600 mb-6">You don't have any orders in progress.</p>
                  <Button asChild>
                    <a href="/shop">Start Shopping</a>
                  </Button>
                </CardContent>
              </Card>
            )}
          </TabsContent>

          <TabsContent value="past" className="space-y-4">
            {isLoading ? (
              <div className="grid gap-4">
                {Array.from({ length: 3 }).map((_, i) => (
                  <Card key={i} className="animate-pulse">
                    <CardHeader>
                      <div className="h-6 bg-gray-200 rounded w-1/3"></div>
                      <div className="h-4 bg-gray-200 rounded w-1/4"></div>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-3">
                        <div className="h-4 bg-gray-200 rounded w-1/2"></div>
                        <div className="h-4 bg-gray-200 rounded w-full"></div>
                        <div className="flex gap-2">
                          <div className="h-8 bg-gray-200 rounded w-24"></div>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            ) : pastOrders.length > 0 ? (
              <div className="grid gap-4">
                {pastOrders.map((order) => (
                  <OrderCard key={order.id} order={order} />
                ))}
              </div>
            ) : (
              <Card className="text-center py-12">
                <CardContent>
                  <Package className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                  <h3 className="text-lg font-semibold text-gray-900 mb-2">No past orders</h3>
                  <p className="text-gray-600">Your order history will appear here.</p>
                </CardContent>
              </Card>
            )}
          </TabsContent>
        </Tabs>
      </div>

      {/* Order Details Dialog */}
      <Dialog open={!!selectedOrder && !trackingDialogOpen} onOpenChange={() => setSelectedOrder(null)}>
        <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Order Details #{selectedOrder?.id.slice(-8).toUpperCase()}</DialogTitle>
          </DialogHeader>

          {selectedOrder && (
            <div className="space-y-6">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <h4 className="font-semibold text-gray-900 mb-2">Order Information</h4>
                  <div className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <span className="text-gray-600">Order ID:</span>
                      <span className="font-medium">#{selectedOrder.id.slice(-8).toUpperCase()}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Date:</span>
                      <span>{selectedOrder.createdAt && format(new Date(selectedOrder.createdAt), "MMM dd, yyyy")}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Status:</span>
                      <Badge className={`${getStatusColor(selectedOrder.status)} border-0`}>
                        {selectedOrder.status ? selectedOrder.status.charAt(0).toUpperCase() + selectedOrder.status.slice(1) : 'Unknown'}
                      </Badge>
                    </div>
                    {selectedOrder.trackingNumber && (
                      <div className="flex justify-between">
                        <span className="text-gray-600">Tracking:</span>
                        <span className="font-medium">#{selectedOrder.trackingNumber}</span>
                      </div>
                    )}
                  </div>
                </div>

                <div>
                  <h4 className="font-semibold text-gray-900 mb-2">Delivery Information</h4>
                  <div className="space-y-2 text-sm">
                    <div>
                      <span className="text-gray-600">Address:</span>
                      <p className="mt-1">{selectedOrder.deliveryAddress || "Not provided"}</p>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Payment:</span>
                      <span>{selectedOrder.paymentMethod || "Card"}</span>
                    </div>
                  </div>
                </div>
              </div>

              <Separator />

              <div>
                <h4 className="font-semibold text-gray-900 mb-4">Order Items</h4>
                <div className="space-y-3">
                  {selectedOrder.orderItems?.map((item, index) => (
                    <div key={index} className="flex justify-between items-center p-3 bg-gray-50 rounded-lg">
                      <div>
                        <h5 className="font-medium">{item.name}</h5>
                        <p className="text-sm text-gray-600">Quantity: {item.quantity}</p>
                      </div>
                      <span className="font-semibold">{formatPrice(item.price)}</span>
                    </div>
                  ))}
                </div>
              </div>

              <Separator />

              <div className="flex justify-between items-center">
                <span className="font-semibold text-lg">Total Amount</span>
                <span className="font-bold text-xl text-buylock-primary">
                  {formatPrice(selectedOrder.totalAmount)}
                </span>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>

      {/* Tracking Dialog */}
      <Dialog open={trackingDialogOpen} onOpenChange={setTrackingDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Order Tracking #{selectedOrder?.id.slice(-8).toUpperCase()}</DialogTitle>
          </DialogHeader>

          <div className="space-y-6">
            <div className="flex justify-between items-center">
              <h4 className="font-semibold">Tracking Progress</h4>
              <Button
                variant="outline"
                size="sm"
                onClick={() => selectedOrder && generateTrackingMutation.mutate(selectedOrder.id)}
                disabled={generateTrackingMutation.isPending}
              >
                Generate Next Update
              </Button>
            </div>

            {trackingLoading ? (
              <div className="space-y-4">
                {Array.from({ length: 3 }).map((_, i) => (
                  <div key={i} className="flex gap-4 animate-pulse">
                    <div className="w-8 h-8 bg-gray-200 rounded-full"></div>
                    <div className="flex-1 space-y-2">
                      <div className="h-4 bg-gray-200 rounded w-1/3"></div>
                      <div className="h-3 bg-gray-200 rounded w-full"></div>
                    </div>
                    <div className="h-3 bg-gray-200 rounded w-20"></div>
                  </div>
                ))}
              </div>
            ) : orderTracking.length > 0 ? (
              <div className="space-y-4">
                {orderTracking.map((tracking, index) => (
                  <div key={tracking.id} className="flex gap-4">
                    <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                      index === 0 ? 'bg-buylock-primary text-white' : 'bg-gray-200'
                    }`}>
                      {index === 0 ? (
                        <CheckCircle className="w-4 h-4" />
                      ) : (
                        <div className="w-2 h-2 bg-gray-400 rounded-full"></div>
                      )}
                    </div>
                    <div className="flex-1">
                      <h5 className="font-semibold text-gray-900">{tracking.status}</h5>
                      <p className="text-sm text-gray-600 mt-1">{tracking.description}</p>
                      {tracking.location && (
                        <div className="flex items-center gap-1 text-xs text-gray-500 mt-1">
                          <MapPin className="w-3 h-3" />
                          <span>{tracking.location}</span>
                        </div>
                      )}
                    </div>
                    <div className="text-xs text-gray-500">
                      {tracking.timestamp && format(new Date(tracking.timestamp), "MMM dd, h:mm a")}
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-8">
                <Truck className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                <p className="text-gray-600">No tracking information available yet.</p>
              </div>
            )}
          </div>
        </DialogContent>
      </Dialog>

      <Footer />
    </div>
  );
}

