client/src/pages/payment.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Header } from "@/components/header";
import { Footer } from "@/components/footer";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { 
  CreditCard, 
  Shield, 
  CheckCircle, 
  ArrowLeft,
  Loader2,
  MapPin,
  Clock,
  User
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { isUnauthorizedError } from "@/lib/authUtils";

declare global {
  interface Window {
    PaystackPop?: any;
  }
}

export default function Payment() {
  const [, setLocation] = useLocation();
  const [isPaystackLoaded, setIsPaystackLoaded] = useState(false);
  const [isProcessingPayment, setIsProcessingPayment] = useState(false);
  const { toast } = useToast();
  const { isAuthenticated, user } = useAuth();

  // Get order details from URL params
  const urlParams = new URLSearchParams(window.location.search);
  const orderId = urlParams.get('orderId');
  const amount = urlParams.get('amount');

  // Fetch order details
  const { data: orderDetails, isLoading } = useQuery({
    queryKey: ["/api/orders", orderId],
    queryFn: async () => {
      const response = await fetch(`/api/orders/${orderId}`, {
        credentials: "include",
      });
      if (!response.ok) throw new Error("Failed to fetch order details");
      return response.json();
    },
    enabled: !!orderId && !!isAuthenticated,
  });

  // Fetch Paystack config
  const { data: paymentConfig } = useQuery({
    queryKey: ["/api/payments/config"],
    queryFn: async () => {
      const response = await fetch("/api/payments/config");
      if (!response.ok) throw new Error("Failed to fetch payment config");
      return response.json();
    },
  });

  // Payment verification mutation
  const verifyPaymentMutation = useMutation({
    mutationFn: async ({ reference, orderId }: { reference: string; orderId: string }) => {
      return apiRequest("/api/payments/verify", "POST", {
        reference,
        orderId,
      });
    },
    onSuccess: (data) => {
      toast({
        title: "Payment successful!",
        description: "Your service has been booked successfully. Redirecting to your orders...",
      });

      // Redirect to orders page after 2 seconds
      setTimeout(() => {
        setLocation("/my-orders");
      }, 2000);
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Session expired",
          description: "Please log in again",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 1500);
        return;
      }

      toast({
        title: "Payment verification failed",
        description: error.message || "There was an issue verifying your payment. Please contact support.",
        variant: "destructive",
      });
    },
  });

  // Load Paystack script
  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://js.paystack.co/v1/inline.js';
    script.onload = () => setIsPaystackLoaded(true);
    document.head.appendChild(script);

    return () => {
      document.head.removeChild(script);
    };
  }, []);

  const formatPrice = (price: string | number) => {
    const numPrice = typeof price === 'string' ? parseFloat(price) : price;
    return new Intl.NumberFormat('en-KE', {
      style: 'currency',
      currency: 'KES',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(numPrice);
  };

  const handlePayment = async () => {
    if (!isPaystackLoaded || !window.PaystackPop) {
      toast({
        title: "Payment system loading",
        description: "Please wait for the payment system to load and try again.",
        variant: "destructive",
      });
      return;
    }

    if (!user?.email) {
      toast({
        title: "Email required",
        description: "Please ensure your profile has a valid email address.",
        variant: "destructive",
      });
      return;
    }

    setIsProcessingPayment(true);

    if (!paymentConfig?.publicKey) {
      toast({
        title: "Payment system error",
        description: "Unable to load payment configuration. Please try again.",
        variant: "destructive",
      });
      return;
    }

    const handler = window.PaystackPop.setup({
      key: paymentConfig.publicKey,
      email: user.email,
      amount: parseInt(amount!) * 100, // Convert to kobo (smallest currency unit)
      ref: `BL_${orderId}_${Date.now()}`,
      currency: 'KES',
      metadata: {
        custom_fields: [
          {
            display_name: "Order ID",
            variable_name: "order_id",
            value: orderId
          },
          {
            display_name: "Service Type",
            variable_name: "service_type",
            value: "service_booking"
          }
        ]
      },
      callback: function(response: any) {
        setIsProcessingPayment(false);
        // Verify payment on the backend
        verifyPaymentMutation.mutate({
          reference: response.reference,
          orderId: orderId!,
        });
      },
      onClose: function() {
        setIsProcessingPayment(false);
        toast({
          title: "Payment cancelled",
          description: "You can continue the payment process anytime from your orders.",
        });
      }
    });

    handler.openIframe();
  };

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-16 text-center">
          <h1 className="text-2xl font-bold mb-4">Access Denied</h1>
          <p className="text-gray-600 mb-8">Please log in to access payment</p>
          <Button onClick={() => window.location.href = "/api/login"}>
            Log In
          </Button>
        </div>
        <Footer />
      </div>
    );
  }

  if (!orderId || !amount) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-16 text-center">
          <h1 className="text-2xl font-bold mb-4">Invalid Payment Link</h1>
          <p className="text-gray-600 mb-8">The payment link is invalid or expired.</p>
          <Button onClick={() => setLocation("/")}>
            Return Home
          </Button>
        </div>
        <Footer />
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-16 text-center">
          <Loader2 className="w-8 h-8 animate-spin mx-auto mb-4" />
          <p>Loading payment details...</p>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8 max-w-4xl">
        {/* Back Button */}
        <Button 
          variant="ghost" 
          onClick={() => setLocation("/")}
          className="mb-6"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Home
        </Button>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Order Summary */}
          <div>
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <CheckCircle className="w-5 h-5 text-green-600" />
                  Service Booking Summary
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-6">
                {orderDetails?.orderItems?.map((item: any) => (
                  <div key={item.id} className="space-y-4">
                    <div>
                      <h3 className="font-semibold text-lg">{item.name}</h3>
                      <div className="flex items-center gap-4 text-sm text-gray-600 mt-2">
                        <div className="flex items-center gap-1">
                          <Clock className="w-4 h-4" />
                          <span>{item.duration} hours</span>
                        </div>
                        {item.appointmentDate && (
                          <div className="flex items-center gap-1">
                            <Clock className="w-4 h-4" />
                            <span>
                              {new Date(item.appointmentDate).toLocaleDateString()} at {item.appointmentTime}
                            </span>
                          </div>
                        )}
                      </div>
                    </div>

                    {/* Service Location */}
                    {item.serviceAddress && (
                      <div className="space-y-2">
                        <h4 className="font-medium">Service Location</h4>
                        <div className="flex items-start gap-2 text-sm text-gray-600">
                          <MapPin className="w-4 h-4 mt-0.5" />
                          <span>{item.serviceAddress}</span>
                        </div>
                        {item.serviceLatitude && item.serviceLongitude && (
                          <p className="text-xs text-green-600 ml-6">
                            GPS: {item.serviceLatitude?.toFixed(6)}, {item.serviceLongitude?.toFixed(6)}
                          </p>
                        )}
                      </div>
                    )}

                    {/* Service Instructions */}
                    {item.serviceInstructions && (
                      <div className="space-y-2">
                        <h4 className="font-medium">Service Instructions</h4>
                        <div className="p-3 bg-blue-50 rounded text-sm">
                          {item.serviceInstructions}
                        </div>
                      </div>
                    )}

                    {/* Additional Notes */}
                    {item.serviceNotes && (
                      <div className="space-y-2">
                        <h4 className="font-medium">Additional Notes</h4>
                        <div className="p-3 bg-gray-50 rounded text-sm">
                          {item.serviceNotes}
                        </div>
                      </div>
                    )}
                  </div>
                ))}

                <Separator />

                {/* Total Amount */}
                <div className="flex justify-between items-center text-lg font-semibold">
                  <span>Total Amount:</span>
                  <span className="text-buylock-primary">{formatPrice(amount)}</span>
                </div>

                {/* Order Status */}
                <div className="flex items-center gap-2">
                  <Badge variant="outline" className="bg-yellow-50 text-yellow-700 border-yellow-200">
                    Pending Payment
                  </Badge>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Payment Section */}
          <div>
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <CreditCard className="w-5 h-5" />
                  Complete Payment
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-6">
                {/* User Info */}
                <div className="space-y-2">
                  <div className="flex items-center gap-2 text-sm">
                    <User className="w-4 h-4 text-gray-500" />
                    <span>Paying as: {user?.email}</span>
                  </div>
                </div>

                {/* Payment Amount */}
                <div className="p-4 bg-green-50 rounded-lg border border-green-200">
                  <div className="flex justify-between items-center">
                    <span className="text-lg font-medium">Amount to Pay:</span>
                    <span className="text-2xl font-bold text-green-700">
                      {formatPrice(amount)}
                    </span>
                  </div>
                  <p className="text-sm text-green-600 mt-1">
                    Secure payment processed by Paystack
                  </p>
                </div>

                {/* Security Info */}
                <div className="flex items-start gap-3 p-4 bg-blue-50 rounded-lg">
                  <Shield className="w-5 h-5 text-blue-600 mt-0.5" />
                  <div className="text-sm">
                    <p className="font-medium text-blue-900">Secure Payment</p>
                    <p className="text-blue-700">
                      Your payment is secured with 256-bit SSL encryption. We accept all major cards and mobile money.
                    </p>
                  </div>
                </div>

                {/* Pay Button */}
                <Button
                  className="w-full bg-buylock-primary hover:bg-buylock-primary/90"
                  size="lg"
                  onClick={handlePayment}
                  disabled={!isPaystackLoaded || isProcessingPayment || verifyPaymentMutation.isPending}
                >
                  {isProcessingPayment ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Processing Payment...
                    </>
                  ) : verifyPaymentMutation.isPending ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Verifying Payment...
                    </>
                  ) : !isPaystackLoaded ? (
                    "Loading Payment System..."
                  ) : (
                    <>
                      <CreditCard className="w-4 h-4 mr-2" />
                      Pay {formatPrice(amount)}
                    </>
                  )}
                </Button>

                {/* Payment Methods */}
                <div className="text-center text-sm text-gray-600">
                  <p>Accepts: Cards â€¢ Mobile Money â€¢ Bank Transfer</p>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

client/src/pages/product-detail.tsx.content:
import { useParams } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useState } from "react";
import { Header } from "@/components/header";
import { Footer } from "@/components/footer";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { Heart, Star, Minus, Plus, ShoppingCart, Truck, Shield, RotateCcw } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { isUnauthorizedError } from "@/lib/authUtils";
import { PriceDisplay } from "@/components/PriceDisplay";
import { useCurrency } from "@/contexts/CurrencyContext";
import type { Product } from "@shared/schema";

export default function ProductDetail() {
  const { slug } = useParams<{ slug: string }>();
  const [quantity, setQuantity] = useState(1);
  const [isWished, setIsWished] = useState(false);
  const { toast } = useToast();
  const { isAuthenticated } = useAuth();
  const queryClient = useQueryClient();

  const { data: product, isLoading } = useQuery<Product>({
    queryKey: [`/api/products/${slug}`],
    enabled: !!slug,
  });

  const addToCartMutation = useMutation({
    mutationFn: async () => {
      await apiRequest("/api/cart", "POST", {
        productId: product!.id,
        quantity,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "Added to cart",
        description: `${product!.name} has been added to your cart`,
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Login required",
          description: "Please log in to add items to cart",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 1500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add item to cart",
        variant: "destructive",
      });
    },
  });

  const handleAddToCart = () => {
    if (!isAuthenticated) {
      toast({
        title: "Login required",
        description: "Please log in to add items to cart",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 1500);
      return;
    }

    addToCartMutation.mutate();
  };

  const { formatPrice } = useCurrency();

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-8">
          <div className="grid md:grid-cols-2 gap-8">
            <Skeleton className="w-full h-96 rounded-xl" />
            <div className="space-y-4">
              <Skeleton className="h-8 w-3/4" />
              <Skeleton className="h-4 w-1/2" />
              <Skeleton className="h-6 w-1/3" />
              <Skeleton className="h-20 w-full" />
              <Skeleton className="h-12 w-full" />
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  if (!product) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-8">
          <div className="text-center py-12">
            <h1 className="text-2xl font-bold text-gray-900 mb-2">Product not found</h1>
            <p className="text-gray-600 mb-6">The product you're looking for doesn't exist.</p>
            <Button onClick={() => window.history.back()}>Go Back</Button>
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  const hasDiscount = product.originalPrice && parseFloat(product.originalPrice) > parseFloat(product.price);
  const discountPercent = hasDiscount 
    ? Math.round(((parseFloat(product.originalPrice!) - parseFloat(product.price)) / parseFloat(product.originalPrice!)) * 100)
    : 0;

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8">
        <div className="grid md:grid-cols-2 gap-8">
          {/* Product Images */}
          <div className="space-y-4">
            <div className="relative">
              <img 
                src={product.imageUrl || "https://images.unsplash.com/photo-1505740420928-5e560c06d30e?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&h=600"} 
                alt={product.name}
                className="w-full h-96 object-cover rounded-xl"
              />
              {hasDiscount && (
                <Badge className="absolute top-4 left-4 bg-red-500 text-white">
                  -{discountPercent}% OFF
                </Badge>
              )}
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setIsWished(!isWished)}
                className="absolute top-4 right-4 w-10 h-10 bg-white/80 hover:bg-white rounded-full"
              >
                <Heart className={`w-5 h-5 ${isWished ? 'fill-red-500 text-red-500' : 'text-gray-600'}`} />
              </Button>
            </div>

            {/* Thumbnail images would go here */}
            {product.imageUrls && product.imageUrls.length > 0 && (
              <div className="flex space-x-2 overflow-x-auto">
                {product.imageUrls.slice(0, 4).map((url, index) => (
                  <img 
                    key={index}
                    src={url}
                    alt={`${product.name} ${index + 1}`}
                    className="w-20 h-20 object-cover rounded-lg cursor-pointer border-2 border-transparent hover:border-buylock-primary"
                  />
                ))}
              </div>
            )}
          </div>

          {/* Product Info */}
          <div className="space-y-6">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 mb-2">{product.name}</h1>
              <div className="flex items-center space-x-4 mb-4">
                <div className="flex items-center">
                  <div className="flex text-yellow-400">
                    {Array.from({ length: 5 }).map((_, i) => (
                      <Star 
                        key={i} 
                        className={`w-5 h-5 ${i < Math.floor(parseFloat(product.rating || "0")) ? 'fill-current' : ''}`} 
                      />
                    ))}
                  </div>
                  <span className="text-gray-600 ml-2">({product.reviewCount || 0} reviews)</span>
                </div>
                <Badge variant={product.stock && product.stock > 0 ? "default" : "destructive"}>
                  {product.stock && product.stock > 0 ? "In Stock" : "Out of Stock"}
                </Badge>
              </div>
            </div>

            {/* Price */}
            <div className="space-y-2">
              <PriceDisplay 
                price={product.price} 
                originalPrice={product.originalPrice}
                size="xl"
                className="text-buylock-primary"
              />
              {hasDiscount && (
                <p className="text-green-600 font-semibold">You save {formatPrice(parseFloat(product.originalPrice!) - parseFloat(product.price))}</p>
              )}
            </div>

            {/* Description */}
            <div>
              <h3 className="font-semibold text-gray-900 mb-2">Description</h3>
              <p className="text-gray-600 leading-relaxed">
                {product.description || product.shortDescription || "No description available."}
              </p>
            </div>

            {/* Quantity and Add to Cart */}
            <div className="space-y-4">
              <div className="flex items-center space-x-4">
                <span className="font-semibold text-gray-900">Quantity:</span>
                <div className="flex items-center border rounded-lg">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setQuantity(Math.max(1, quantity - 1))}
                    disabled={quantity <= 1}
                  >
                    <Minus className="w-4 h-4" />
                  </Button>
                  <span className="px-4 py-2 min-w-[60px] text-center">{quantity}</span>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setQuantity(quantity + 1)}
                    disabled={!product.stock || quantity >= product.stock}
                  >
                    <Plus className="w-4 h-4" />
                  </Button>
                </div>
                {product.stock && (
                  <span className="text-sm text-gray-500">
                    {product.stock} items available
                  </span>
                )}
              </div>

              <div className="flex space-x-4">
                <Button
                  onClick={handleAddToCart}
                  disabled={addToCartMutation.isPending || !product.stock || product.stock === 0}
                  className="flex-1 bg-buylock-primary hover:bg-buylock-primary/90 text-white font-semibold py-3"
                  size="lg"
                >
                  <ShoppingCart className="w-5 h-5 mr-2" />
                  {addToCartMutation.isPending ? "Adding..." : "Add to Cart"}
                </Button>
                <Button
                  variant="outline"
                  size="lg"
                  className="px-8 border-buylock-primary text-buylock-primary hover:bg-buylock-primary hover:text-white"
                >
                  Buy Now
                </Button>
              </div>
            </div>

            {/* Features */}
            <div className="space-y-3 pt-6 border-t">
              <div className="flex items-center space-x-3 text-gray-600">
                <Truck className="w-5 h-5 text-buylock-primary" />
                <span>Free delivery on orders over KES 25,000</span>
              </div>
              <div className="flex items-center space-x-3 text-gray-600">
                <Shield className="w-5 h-5 text-buylock-primary" />
                <span>Secure payment & buyer protection</span>
              </div>
              <div className="flex items-center space-x-3 text-gray-600">
                <RotateCcw className="w-5 h-5 text-buylock-primary" />
                <span>Easy returns within 30 days</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

client/src/pages/products.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useLocation, useSearch } from "wouter";
import { Header } from "@/components/header";
import { Footer } from "@/components/footer";
import { ProductCard } from "@/components/product-card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Slider } from "@/components/ui/slider";
import { Separator } from "@/components/ui/separator";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { Filter, Search, Star } from "lucide-react";
import type { Product, Category } from "@shared/schema";

export default function Products() {
  const search = useSearch();
  const [location, navigate] = useLocation();
  const params = new URLSearchParams(search);

  const [searchQuery, setSearchQuery] = useState(params.get("search") || "");
  const [debouncedSearch, setDebouncedSearch] = useState("");
  const [selectedCategory, setSelectedCategory] = useState(params.get("category") || "all");
  const [selectedBrand, setSelectedBrand] = useState<string>("all");
  const [priceRange, setPriceRange] = useState([0, 100000]);
  const [selectedRating, setSelectedRating] = useState<number | null>(null);
  const [inStock, setInStock] = useState(false);
  const [sortBy, setSortBy] = useState(params.get("sort") || "newest");
  const [showFilters, setShowFilters] = useState(false);

  // Debounce search query
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedSearch(searchQuery);
    }, 300);
    return () => clearTimeout(timer);
  }, [searchQuery]);

  const { data: categories = [] } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
  });

  // Build query string for API call
  const buildQueryString = () => {
    const params = new URLSearchParams();
    if (debouncedSearch) params.set('search', debouncedSearch);
    if (selectedCategory && selectedCategory !== 'all') params.set('category', selectedCategory);
    params.set('limit', '50');
    return params.toString();
  };

  const { data: products = [], isLoading } = useQuery<Product[]>({
    queryKey: [`/api/products?${buildQueryString()}`],
  });

  useEffect(() => {
    const newParams = new URLSearchParams();
    if (searchQuery) newParams.set("search", searchQuery);
    if (selectedCategory) newParams.set("category", selectedCategory);
    if (sortBy !== "newest") newParams.set("sort", sortBy);

    const newSearch = newParams.toString();
    const newPath = `/products${newSearch ? `?${newSearch}` : ""}`;

    if (location !== newPath) {
      navigate(newPath, { replace: true });
    }
  }, [searchQuery, selectedCategory, sortBy, location, navigate]);

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
  };

  // Get unique brands from products - note: products don't have brand field, using brandId
  const brands = Array.from(new Set((products as Product[]).map(p => p.brandId).filter(Boolean)));

  // Filter and sort logic
  const filteredProducts = (products as Product[]).filter(product => {
    if (selectedBrand !== "all" && selectedBrand && product.brandId !== selectedBrand) return false;

    // Parse price as number since it's stored as string
    const productPrice = typeof product.price === 'string' ? parseFloat(product.price) : product.price;
    if (productPrice < priceRange[0] || productPrice > priceRange[1]) return false;

    const productRating = typeof product.rating === 'string' ? parseFloat(product.rating) : product.rating;
    if (selectedRating && (productRating || 0) < selectedRating) return false;
    if (inStock && (product.stock || 0) <= 0) return false;
    return true;
  });

  const sortItems = (items: Product[]) => {
    switch (sortBy) {
      case "price-low":
        return [...items].sort((a, b) => {
          const priceA = typeof a.price === 'string' ? parseFloat(a.price) : a.price;
          const priceB = typeof b.price === 'string' ? parseFloat(b.price) : b.price;
          return priceA - priceB;
        });
      case "price-high":
        return [...items].sort((a, b) => {
          const priceA = typeof a.price === 'string' ? parseFloat(a.price) : a.price;
          const priceB = typeof b.price === 'string' ? parseFloat(b.price) : b.price;
          return priceB - priceA;
        });
      case "rating":
        return [...items].sort((a, b) => {
          const ratingA = typeof a.rating === 'string' ? parseFloat(a.rating) : a.rating;
          const ratingB = typeof b.rating === 'string' ? parseFloat(b.rating) : b.rating;
          return (ratingB || 0) - (ratingA || 0);
        });
      case "newest":
        return [...items].sort((a, b) => {
          const aDate = a.createdAt ? new Date(a.createdAt).getTime() : 0;
          const bDate = b.createdAt ? new Date(b.createdAt).getTime() : 0;
          return bDate - aDate;
        });
      default:
        return items;
    }
  };

  const sortedProducts = sortItems(filteredProducts);

  const clearFilters = () => {
    setSelectedCategory("");
    setSelectedBrand("");
    setPriceRange([0, 500000]);
    setSelectedRating(null);
    setInStock(false);
  };

  const renderStarRating = (rating: number) => {
    return (
      <div className="flex items-center space-x-1">
        {[1, 2, 3, 4, 5].map((star) => (
          <Star
            key={star}
            className={`w-4 h-4 ${star <= rating ? "fill-yellow-400 text-yellow-400" : "text-gray-300"}`}
          />
        ))}
        <span className="text-sm text-gray-600">& up</span>
      </div>
    );
  };

  const activeFiltersCount = [
    selectedCategory,
    selectedBrand,
    selectedRating,
    inStock,
    priceRange[0] > 0 || priceRange[1] < 500000,
  ].filter(Boolean).length;

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8">
        {/* Page Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">Products</h1>
        </div>

        <div className="flex flex-col lg:flex-row gap-8">
          {/* Filters Sidebar */}
          <div className={`lg:w-80 ${showFilters ? "block" : "hidden lg:block"}`}>
            {/* Dropdown Controls Above Filters */}
            <div className="space-y-4 mb-6">
              {/* Category Filter */}
              <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                <SelectTrigger className="w-full">
                  <SelectValue placeholder="All Categories" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Categories</SelectItem>
                  {categories.map((category) => (
                    <SelectItem key={category.id} value={category.id}>
                      {category.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              {/* Sort Filter */}
              <Select value={sortBy} onValueChange={setSortBy}>
                <SelectTrigger className="w-full">
                  <SelectValue placeholder="Sort by" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="relevance">Relevance</SelectItem>
                  <SelectItem value="price-low">Price: Low to High</SelectItem>
                  <SelectItem value="price-high">Price: High to Low</SelectItem>
                  <SelectItem value="rating">Highest Rated</SelectItem>
                  <SelectItem value="newest">Newest</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <Card className="sticky top-4">
              <CardHeader>
                <div className="flex items-center justify-between">
                  <CardTitle className="text-lg">Filters</CardTitle>
                  <div className="flex items-center space-x-2">
                    {activeFiltersCount > 0 && (
                      <Badge variant="secondary" className="text-xs">
                        {activeFiltersCount}
                      </Badge>
                    )}
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={clearFilters}
                      className="text-buylock-primary hover:text-buylock-primary/80"
                    >
                      Clear All
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-6">
                {/* Brand Filter */}
                {brands.length > 0 && (
                  <>
                    <div>
                      <h3 className="font-semibold text-gray-900 mb-3">Brand</h3>
                      <Select value={selectedBrand} onValueChange={setSelectedBrand}>
                        <SelectTrigger>
                          <SelectValue placeholder="All Brands" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="all">All Brands</SelectItem>
                          {brands.map((brand) => (
                            <SelectItem key={brand} value={brand || ''}>
                              {brand}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                    <Separator />
                  </>
                )}

                {/* Price Range */}
                <div>
                  <h3 className="font-semibold text-gray-900 mb-3">Price Range</h3>
                  <div className="space-y-4">
                    <Slider
                      value={priceRange}
                      onValueChange={setPriceRange}
                      max={100000}
                      min={0}
                      step={500}
                      className="w-full"
                    />
                    <div className="flex justify-between text-sm text-gray-600">
                      <span>KES {priceRange[0].toLocaleString()}</span>
                      <span>KES {priceRange[1].toLocaleString()}</span>
                    </div>
                  </div>
                </div>

                <Separator />

                {/* Customer Rating */}
                <div>
                  <h3 className="font-semibold text-gray-900 mb-3">Customer Rating</h3>
                  <div className="space-y-2">
                    {[4, 3, 2, 1].map((rating) => (
                      <div key={rating} className="flex items-center space-x-2">
                        <Checkbox
                          id={`rating-${rating}`}
                          checked={selectedRating === rating}
                          onCheckedChange={(checked) => setSelectedRating(checked ? rating : null)}
                        />
                        <label htmlFor={`rating-${rating}`} className="cursor-pointer">
                          {renderStarRating(rating)}
                        </label>
                      </div>
                    ))}
                  </div>
                </div>

                <Separator />

                {/* Availability */}
                <div>
                  <h3 className="font-semibold text-gray-900 mb-3">Availability</h3>
                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="in-stock"
                      checked={inStock}
                      onCheckedChange={(checked) => setInStock(checked === true)}
                    />
                    <label htmlFor="in-stock" className="text-sm text-gray-700 cursor-pointer">
                      In Stock
                    </label>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Results Area */}
          <div className="flex-1">
            {/* Search Bar Above Results */}
            <div className="mb-6">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                <Input
                  type="text"
                  placeholder="Search products..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10 pr-4 py-3 w-full border border-gray-300 rounded-lg focus:ring-2 focus:ring-buylock-primary focus:border-transparent"
                />
              </div>
            </div>

            {/* Controls Bar */}
            <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-6 gap-4">
              <div className="flex items-center space-x-4">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setShowFilters(!showFilters)}
                  className="lg:hidden"
                >
                  <Filter className="w-4 h-4 mr-2" />
                  Filters
                  {activeFiltersCount > 0 && (
                    <Badge variant="secondary" className="ml-2 text-xs">
                      {activeFiltersCount}
                    </Badge>
                  )}
                </Button>

                <p className="text-sm text-gray-600">
                  {sortedProducts.length} result{sortedProducts.length !== 1 ? "s" : ""} found
                </p>
              </div>
            </div>

            {/* Results Grid */}
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
              {isLoading ? (
                Array.from({ length: 12 }).map((_, i) => (
                  <div key={i} className="bg-white rounded-xl shadow-sm border">
                    <Skeleton className="w-full h-48 rounded-t-xl" />
                    <div className="p-4 space-y-3">
                      <Skeleton className="h-4 w-3/4" />
                      <Skeleton className="h-4 w-1/2" />
                      <Skeleton className="h-4 w-1/3" />
                      <Skeleton className="h-10 w-full" />
                    </div>
                  </div>
                ))
              ) : sortedProducts.length === 0 ? (
                <div className="col-span-full text-center py-12">
                  <p className="text-gray-500 text-lg">No products found</p>
                  <p className="text-gray-400 text-sm mt-2">Try adjusting your search or filters</p>
                </div>
              ) : (
                sortedProducts.map((product) => (
                  <ProductCard key={product.id} product={product} />
                ))
              )}
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

client/src/pages/service-detail.tsx.content:
import { useState } from "react";
import { useParams, useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Header } from "@/components/header";
import { Footer } from "@/components/footer";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { format } from "date-fns";
import { 
  Star, 
  Clock, 
  MapPin, 
  Shield, 
  CheckCircle, 
  Calendar as CalendarIcon,
  MessageSquare,
  Phone,
  Mail,
  Plus,
  Minus,
  User,
  Award,
  Navigation,
  FileText
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { isUnauthorizedError } from "@/lib/authUtils";
import { ServicePriceDisplay } from "@/components/ServicePriceDisplay";
import { useCurrency } from "@/contexts/CurrencyContext";
import type { Service } from "@shared/schema";

export default function ServiceDetail() {
  const { slug } = useParams<{ slug: string }>();
  const [, setLocation] = useLocation();
  const [duration, setDuration] = useState(2); // Default 2 hours
  const [notes, setNotes] = useState("");
  const [serviceLocation, setServiceLocation] = useState("");
  const [locationCoordinates, setLocationCoordinates] = useState("");
  const [detailedInstructions, setDetailedInstructions] = useState("");
  const [appointmentDate, setAppointmentDate] = useState<Date>();
  const [appointmentTime, setAppointmentTime] = useState("");
  const [isBookingOpen, setIsBookingOpen] = useState(false);
  const [isLoadingLocation, setIsLoadingLocation] = useState(false);

  const { toast } = useToast();
  const { isAuthenticated } = useAuth();
  const queryClient = useQueryClient();

  const { data: service, isLoading, error } = useQuery<Service>({
    queryKey: ["/api/services", slug],
    enabled: !!slug,
  });

  // Location functions
  const getCurrentLocation = () => {
    if (!navigator.geolocation) {
      toast({
        title: "Location not supported",
        description: "Your browser doesn't support location services",
        variant: "destructive",
      });
      return;
    }

    setIsLoadingLocation(true);
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        setLocationCoordinates(`${latitude},${longitude}`);
        setServiceLocation(`Location: ${latitude.toFixed(6)}, ${longitude.toFixed(6)}`);
        setIsLoadingLocation(false);

        toast({
          title: "Location captured",
          description: "Your current location has been set for the service",
        });
      },
      (error) => {
        setIsLoadingLocation(false);
        toast({
          title: "Location access denied",
          description: "Please enter your address manually or allow location access",
          variant: "destructive",
        });
      }
    );
  };

  const directBookingMutation = useMutation({
    mutationFn: async () => {
      if (!appointmentDate || !appointmentTime) {
        throw new Error("Please select appointment date and time");
      }
      if (!serviceLocation) {
        throw new Error("Please provide service location");
      }

      const response = await apiRequest("/api/services/book", "POST", {
        serviceId: service?.id,
        appointmentDate: appointmentDate.toISOString(),
        appointmentTime,
        duration,
        notes,
        serviceLocation,
        locationCoordinates,
        detailedInstructions,
      });

      return response;
    },
    onSuccess: (data) => {
      toast({
        title: "Service booked successfully!",
        description: `Booking created for ${format(appointmentDate!, "PPP")} at ${appointmentTime}. Proceed to payment.`,
      });

      // Navigate to payment with order details
      setLocation(`/payment?orderId=${data.order.id}&amount=${data.totalAmount}`);
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Please log in",
          description: "You need to be logged in to book services",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 1500);
        return;
      }

      toast({
        title: "Booking failed",
        description: error.message || "Failed to book service. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Format price to KES
  const { formatPrice } = useCurrency();

  // Calculate total price based on duration
  const totalPrice = service ? parseFloat(service.price) * duration : 0;

  // Generate available time slots
  const timeSlots = [
    "08:00", "09:00", "10:00", "11:00", "12:00", "13:00", 
    "14:00", "15:00", "16:00", "17:00", "18:00"
  ];

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-8">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            <div className="space-y-4">
              <div className="h-64 bg-gray-200 rounded-lg animate-pulse" />
              <div className="h-8 bg-gray-200 rounded animate-pulse" />
              <div className="h-4 bg-gray-200 rounded animate-pulse w-3/4" />
            </div>
            <div className="space-y-4">
              <div className="h-32 bg-gray-200 rounded-lg animate-pulse" />
              <div className="h-48 bg-gray-200 rounded-lg animate-pulse" />
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  if (error || !service) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-8 text-center">
          <h1 className="text-2xl font-bold text-gray-900 mb-4">Service Not Found</h1>
          <p className="text-gray-600 mb-8">The service you're looking for doesn't exist.</p>
          <Button onClick={() => window.history.back()}>Go Back</Button>
        </div>
        <Footer />
      </div>
    );
  }

  const rating = 4.8; // In real app, this would come from reviews data
  const reviewCount = 127;

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Service Details */}
          <div className="lg:col-span-2 space-y-6">
            {/* Service Image */}
            <div className="aspect-video bg-gradient-to-br from-blue-100 to-purple-100 rounded-lg overflow-hidden">
              {service.imageUrl ? (
                <img 
                  src={service.imageUrl} 
                  alt={service.name}
                  className="w-full h-full object-cover"
                />
              ) : (
                <div className="w-full h-full flex items-center justify-center">
                  <div className="text-center">
                    <div className="w-16 h-16 bg-buylock-primary/20 rounded-full flex items-center justify-center mx-auto mb-4">
                      <Shield className="w-8 h-8 text-buylock-primary" />
                    </div>
                    <p className="text-gray-600 font-medium">{service.name}</p>
                  </div>
                </div>
              )}
            </div>

            {/* Service Info */}
            <div>
              <div className="flex items-center gap-2 mb-2">
                <Badge variant="secondary">{service.categoryId}</Badge>
                {service.isActive && (
                  <Badge className="bg-green-100 text-green-800">Available</Badge>
                )}
              </div>

              <h1 className="text-3xl font-bold text-gray-900 mb-4">{service.name}</h1>

              <div className="flex items-center gap-4 mb-4">
                <div className="flex items-center gap-1">
                  <Star className="w-5 h-5 fill-yellow-400 text-yellow-400" />
                  <span className="font-semibold">{rating}</span>
                  <span className="text-gray-600">({reviewCount} reviews)</span>
                </div>
                <div className="flex items-center gap-1 text-gray-600">
                  <Clock className="w-4 h-4" />
                  <span>Flexible timing</span>
                </div>
                <div className="flex items-center gap-1 text-gray-600">
                  <MapPin className="w-4 h-4" />
                  <span>{service.location || "Remote/On-site"}</span>
                </div>
              </div>

              <p className="text-gray-700 leading-relaxed mb-6">
                {service.description}
              </p>

              {/* Features */}
              <Card>
                <CardContent className="p-6">
                  <h3 className="text-lg font-semibold mb-4">What's Included</h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                    {[
                      "Professional consultation",
                      "Quality guarantee", 
                      "Timely delivery",
                      "Post-service support",
                      "Secure communication",
                      "Flexible scheduling"
                    ].map((feature, index) => (
                      <div key={index} className="flex items-center gap-2">
                        <CheckCircle className="w-4 h-4 text-green-600" />
                        <span className="text-sm text-gray-700">{feature}</span>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>

          {/* Booking Panel */}
          <div className="space-y-6">
            <Card className="sticky top-4">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <CalendarIcon className="h-5 w-5" />
                  Book Appointment
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-6">
                {/* Hourly Rate Display */}
                <div className="text-center p-4 bg-gray-50 rounded-lg">
                  <ServicePriceDisplay 
                    price={service.price}
                    priceType={service.priceType || 'hourly'}
                    size="xl"
                  />
                </div>

                {/* Duration Selection */}
                <div className="space-y-3">
                  <Label className="text-base font-semibold">Duration (hours)</Label>
                  <div className="flex items-center gap-3">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setDuration(Math.max(1, duration - 1))}
                      disabled={duration <= 1}
                    >
                      <Minus className="w-4 h-4" />
                    </Button>
                    <span className="w-16 text-center font-semibold text-lg">{duration}h</span>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setDuration(duration + 1)}
                      disabled={duration >= 8}
                    >
                      <Plus className="w-4 h-4" />
                    </Button>
                  </div>
                  <p className="text-sm text-gray-600">Minimum 1 hour, maximum 8 hours per booking</p>
                </div>

                {/* Date Selection */}
                <div className="space-y-3">
                  <Label className="text-base font-semibold">Select Date</Label>
                  <Popover>
                    <PopoverTrigger asChild>
                      <Button
                        variant="outline"
                        className={`w-full justify-start text-left font-normal ${
                          !appointmentDate && "text-muted-foreground"
                        }`}
                      >
                        <CalendarIcon className="mr-2 h-4 w-4" />
                        {appointmentDate ? format(appointmentDate, "PPP") : "Pick a date"}
                      </Button>
                    </PopoverTrigger>
                    <PopoverContent className="w-auto p-0" align="start">
                      <Calendar
                        mode="single"
                        selected={appointmentDate}
                        onSelect={setAppointmentDate}
                        disabled={(date) => date < new Date() || date < new Date("1900-01-01")}
                        initialFocus
                      />
                    </PopoverContent>
                  </Popover>
                </div>

                {/* Time Selection */}
                <div className="space-y-3">
                  <Label className="text-base font-semibold">Select Time</Label>
                  <Select value={appointmentTime} onValueChange={setAppointmentTime}>
                    <SelectTrigger>
                      <SelectValue placeholder="Choose time slot" />
                    </SelectTrigger>
                    <SelectContent>
                      {timeSlots.map((time) => (
                        <SelectItem key={time} value={time}>
                          {time}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* Service Location */}
                <div className="space-y-3">
                  <Label className="text-base font-semibold flex items-center gap-2">
                    <MapPin className="w-4 h-4" />
                    Service Location
                  </Label>
                  <div className="space-y-2">
                    <div className="flex gap-2">
                      <Input
                        placeholder="Enter your complete address..."
                        value={serviceLocation}
                        onChange={(e) => setServiceLocation(e.target.value)}
                        className="flex-1"
                      />
                      <Button
                        type="button"
                        variant="outline"
                        size="sm"
                        onClick={getCurrentLocation}
                        disabled={isLoadingLocation}
                        className="shrink-0"
                      >
                        {isLoadingLocation ? (
                          "Getting..."
                        ) : (
                          <>
                            <Navigation className="w-4 h-4 mr-1" />
                            Pin Location
                          </>
                        )}
                      </Button>
                    </div>
                    {locationCoordinates && (
                      <p className="text-xs text-green-600">
                        âœ“ Location pinned: {locationCoordinates}
                      </p>
                    )}
                  </div>
                </div>

                {/* Detailed Instructions */}
                <div className="space-y-3">
                  <Label htmlFor="instructions" className="text-base font-semibold flex items-center gap-2">
                    <FileText className="w-4 h-4" />
                    Detailed Instructions
                  </Label>
                  <Textarea
                    id="instructions"
                    placeholder="Provide detailed instructions for the service provider:
â€¢ Access instructions (gate codes, apartment numbers)
â€¢ Specific areas to focus on
â€¢ Materials or tools already available
â€¢ Any special requirements or preferences..."
                    value={detailedInstructions}
                    onChange={(e) => setDetailedInstructions(e.target.value)}
                    rows={4}
                    className="resize-none"
                  />
                </div>

                {/* Additional Notes */}
                <div className="space-y-3">
                  <Label htmlFor="notes" className="text-base font-semibold">
                    Additional Notes (Optional)
                  </Label>
                  <Textarea
                    id="notes"
                    placeholder="Any other special requirements or information..."
                    value={notes}
                    onChange={(e) => setNotes(e.target.value)}
                    rows={2}
                  />
                </div>

                {/* Total Price Display */}
                <div className="p-4 bg-blue-50 rounded-lg border border-blue-200">
                  <div className="flex justify-between items-center">
                    <span className="text-lg font-semibold">Total Cost:</span>
                    <ServicePriceDisplay 
                      price={totalPrice}
                      priceType="fixed"
                      size="xl"
                      className="text-buylock-primary"
                    />
                  </div>
                  {duration > 1 && (
                    <p className="text-sm text-gray-600 mt-1">
                      {formatPrice(service.price)} Ã— {duration} hours
                    </p>
                  )}
                </div>

                {/* Book Service Button */}
                <Button
                  className="w-full bg-buylock-primary hover:bg-buylock-primary/90"
                  onClick={() => directBookingMutation.mutate()}
                  disabled={directBookingMutation.isPending || !isAuthenticated || !appointmentDate || !appointmentTime || !serviceLocation}
                  size="lg"
                >
                  {directBookingMutation.isPending ? (
                    "Processing Booking..."
                  ) : !isAuthenticated ? (
                    "Login to Book Service"
                  ) : !appointmentDate || !appointmentTime ? (
                    "Select Date & Time"
                  ) : !serviceLocation ? (
                    "Add Service Location"
                  ) : (
                    `Book & Pay Now - ${formatPrice(totalPrice)}`
                  )}
                </Button>

                <div className="text-xs text-gray-500 text-center">
                  No cart needed â€¢ Direct checkout â€¢ Secure payment
                </div>

                {/* Contact Info */}
                <div className="mt-6 pt-6 border-t">
                  <h4 className="font-semibold mb-3">Need Help?</h4>
                  <div className="space-y-2 text-sm">
                    <div className="flex items-center gap-2 text-gray-600">
                      <Phone className="w-4 h-4" />
                      <span>+234 123 456 7890</span>
                    </div>
                    <div className="flex items-center gap-2 text-gray-600">
                      <Mail className="w-4 h-4" />
                      <span>support@buylock.com</span>
                    </div>
                    <div className="flex items-center gap-2 text-gray-600">
                      <MessageSquare className="w-4 h-4" />
                      <span>Live chat available</span>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

