import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useLocation, useSearch } from "wouter";
import { Header } from "@/components/header";
import { Footer } from "@/components/footer";
import { ProductCard } from "@/components/product-card";
import { ServiceCard } from "@/components/service-card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Slider } from "@/components/ui/slider";
import { Separator } from "@/components/ui/separator";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Search, Filter, Star, X, MapPin } from "lucide-react";
import type { Product, Service, Category } from "@shared/schema";

interface CustomerLocation {
  latitude: number;
  longitude: number;
  description: string;
}

export default function Shop() {
  const search = useSearch();
  const [, setLocation] = useLocation();
  const params = new URLSearchParams(search);

  const [searchQuery, setSearchQuery] = useState(params.get("search") || "");
  const [debouncedSearch, setDebouncedSearch] = useState("");
  const [selectedTypes, setSelectedTypes] = useState<string[]>(["products", "services"]);
  const [selectedCategories, setSelectedCategories] = useState<string[]>([]);
  const [selectedBrand, setSelectedBrand] = useState<string>("all");
  const [priceRange, setPriceRange] = useState([0, 500000]);
  const [selectedRating, setSelectedRating] = useState<number | null>(null);
  const [inStock, setInStock] = useState(false);
  const [availableToday, setAvailableToday] = useState(false);
  const [sortBy, setSortBy] = useState("relevance");
  const [showFilters, setShowFilters] = useState(false);
  const [customerLocation, setCustomerLocation] = useState<CustomerLocation | null>(null);
  const [nearestToMeEnabled, setNearestToMeEnabled] = useState(false);

  // Load customer location from localStorage
  useEffect(() => {
    const savedLocation = localStorage.getItem("buylock_customer_location");
    if (savedLocation) {
      try {
        setCustomerLocation(JSON.parse(savedLocation));
      } catch (error) {
        console.error("Error parsing saved location:", error);
      }
    }
  }, []);

  // Debounce search query
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedSearch(searchQuery);
    }, 300);
    return () => clearTimeout(timer);
  }, [searchQuery]);

  const { data: categories = [] } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
  });

  // Handle URL parameters for category, search, and type
  useEffect(() => {
    const categoryParam = params.get("category");
    if (categoryParam && categories.length > 0) {
      // Find category by slug (formatted as lowercase with hyphens)
      const category = categories.find(cat => 
        cat.slug === categoryParam || 
        cat.name.toLowerCase().replace(/ & /g, "-").replace(/ /g, "-") === categoryParam
      );
      if (category) {
        setSelectedCategories([category.id]);
      }
    }

    const searchParam = params.get("search");
    if (searchParam) {
      setSearchQuery(searchParam);
      setDebouncedSearch(searchParam);
    }

    const productTypeParam = params.get("product_type");
    if (productTypeParam) {
      if (productTypeParam === "products") {
        setSelectedTypes(["products"]);
      } else if (productTypeParam === "services") {
        setSelectedTypes(["services"]);
      }
    }
  }, [search, categories]);

  const { data: products = [], isLoading: productsLoading } = useQuery<Product[]>({
    queryKey: [
      "/api/products",
      {
        search: debouncedSearch || undefined,
        categoryIds: selectedCategories.length > 0 ? selectedCategories.join(",") : undefined,
        customerLat: customerLocation?.latitude,
        customerLng: customerLocation?.longitude,
        sortByProximity: (nearestToMeEnabled && customerLocation) || undefined,
        limit: 50,
      }
    ],
    enabled: selectedTypes.includes("products"),

  });

  const { data: services = [], isLoading: servicesLoading } = useQuery<Service[]>({
    queryKey: [
      "/api/services",
      {
        search: debouncedSearch || undefined,
        categoryIds: selectedCategories.length > 0 ? selectedCategories.join(",") : undefined,
        availableToday: availableToday || undefined,
        customerLat: customerLocation?.latitude,
        customerLng: customerLocation?.longitude,
        sortByProximity: (nearestToMeEnabled && customerLocation) || undefined,
        limit: 50,
      }
    ],
    enabled: selectedTypes.includes("services"),
  });

  // Get unique brands from products (note: using brandId since brand doesn't exist on Product type)
  const brands: string[] = [];

  // Filter and sort logic
  const filteredProducts = (products as Product[]).filter(product => {
    const productPrice = typeof product.price === 'string' ? parseFloat(product.price) : product.price;
    if (productPrice < priceRange[0] || productPrice > priceRange[1]) return false;
    if (selectedRating && parseFloat(product.rating || "0") < selectedRating) return false;
    if (inStock && (product.stock || 0) <= 0) return false;
    return true;
  });

  // Debug the raw products data
  useEffect(() => {
    if (products.length > 0 && nearestToMeEnabled) {
      console.log("Raw products from API:", products.slice(0, 2).map(p => ({ name: p.name, distance: p.distance })));
    }
  }, [products, nearestToMeEnabled]);

  const filteredServices = (services as Service[]).filter(service => {
    const servicePrice = typeof service.price === 'string' ? parseFloat(service.price) : service.price;
    if (servicePrice < priceRange[0] || servicePrice > priceRange[1]) return false;
    if (selectedRating && parseFloat(service.rating || "0") < selectedRating) return false;
    return true;
  });

  const sortItems = (items: (Product | Service)[]) => {
    switch (sortBy) {
      case "price-low":
        return [...items].sort((a, b) => {
          const aPrice = typeof a.price === 'string' ? parseFloat(a.price) : a.price;
          const bPrice = typeof b.price === 'string' ? parseFloat(b.price) : b.price;
          return aPrice - bPrice;
        });
      case "price-high":
        return [...items].sort((a, b) => {
          const aPrice = typeof a.price === 'string' ? parseFloat(a.price) : a.price;
          const bPrice = typeof b.price === 'string' ? parseFloat(b.price) : b.price;
          return bPrice - aPrice;
        });
      case "rating":
        return [...items].sort((a, b) => {
          const aRating = parseFloat(a.rating || "0");
          const bRating = parseFloat(b.rating || "0");
          return bRating - aRating;
        });
      case "newest":
        return [...items].sort((a, b) => {
          const aDate = a.createdAt ? new Date(a.createdAt).getTime() : 0;
          const bDate = b.createdAt ? new Date(b.createdAt).getTime() : 0;
          return bDate - aDate;
        });
      default:
        return items;
    }
  };

  const allItems = selectedTypes.includes("products") && selectedTypes.includes("services") ? 
                   [...filteredProducts, ...filteredServices] :
                   selectedTypes.includes("products") ? filteredProducts : filteredServices;

  const sortedItems = sortItems(allItems);

  // Debug sorted items
  useEffect(() => {
    if (sortedItems.length > 0 && nearestToMeEnabled) {
      console.log("Sorted items before rendering:", sortedItems.slice(0, 2).map(p => ({ name: p.name, distance: p.distance })));
    }
  }, [sortedItems, nearestToMeEnabled]);

  const handleCategoryToggle = (categoryId: string) => {
    setSelectedCategories(prev => {
      const isSelected = prev.includes(categoryId);
      if (isSelected) {
        return prev.filter(c => c !== categoryId);
      } else {
        return [...prev, categoryId];
      }
    });
  };

  const clearAllCategories = () => {
    setSelectedCategories([]);
  };

  const clearFilters = () => {
    setSelectedTypes(["products", "services"]);
    setSelectedCategories([]);
    setSelectedBrand("all");
    setPriceRange([0, 500000]);
    setSelectedRating(null);
    setInStock(false);
    setAvailableToday(false);
    setNearestToMeEnabled(false);
  };

  const renderStarRating = (rating: number) => {
    return (
      <div className="flex items-center space-x-1">
        {[1, 2, 3, 4, 5].map((star) => (
          <Star
            key={star}
            className={`w-4 h-4 ${star <= rating ? "fill-yellow-400 text-yellow-400" : "text-gray-300"}`}
          />
        ))}
        <span className="text-sm text-gray-600">& up</span>
      </div>
    );
  };

  const activeFiltersCount = [
    selectedTypes.length < 2, // Only count if not all types selected
    selectedCategories.length > 0,
    selectedBrand && selectedBrand !== "all",
    selectedRating,
    inStock,
    availableToday,
    priceRange[0] > 0 || priceRange[1] < 500000,
    nearestToMeEnabled,
  ].filter(Boolean).length;

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8">
        {/* Page Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">Shop</h1>
        </div>

        <div className="flex flex-col lg:flex-row gap-8">
          {/* Filters Sidebar */}
          <div className={`lg:w-80 ${showFilters ? "block" : "hidden lg:block"}`}>
            {/* Nearest To Me Toggle */}
            {customerLocation && (
              <div className="mb-6">
                <Card>
                  <CardHeader className="pb-3">
                    <CardTitle className="text-base flex items-center gap-2">
                      <MapPin className="w-4 h-4 text-buylock-primary" />
                      Nearest To Me
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="flex items-center justify-between">
                      <div className="flex flex-col">
                        <span className="text-sm text-gray-700">Show closest vendors first</span>
                        <span className="text-xs text-gray-500">{customerLocation.description}</span>
                      </div>
                      <Switch
                        checked={nearestToMeEnabled}
                        onCheckedChange={setNearestToMeEnabled}
                      />
                    </div>
                  </CardContent>
                </Card>
              </div>
            )}

            {/* Type Filter Above Filters */}
            <div className="mb-6">
              <Card>
                <CardHeader className="pb-3">
                  <CardTitle className="text-base">Product Type</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-2">
                    <div className="flex items-center space-x-2">
                      <Checkbox
                        id="type-products"
                        checked={selectedTypes.includes("products")}
                        onCheckedChange={(checked) => {
                          if (checked) {
                            setSelectedTypes(prev => [...prev, "products"]);
                          } else {
                            setSelectedTypes(prev => prev.filter(t => t !== "products"));
                          }
                        }}
                      />
                      <label htmlFor="type-products" className="text-sm text-gray-700 cursor-pointer">
                        Products
                      </label>
                    </div>
                    <div className="flex items-center space-x-2">
                      <Checkbox
                        id="type-services"
                        checked={selectedTypes.includes("services")}
                        onCheckedChange={(checked) => {
                          if (checked) {
                            setSelectedTypes(prev => [...prev, "services"]);
                          } else {
                            setSelectedTypes(prev => prev.filter(t => t !== "services"));
                          }
                        }}
                      />
                      <label htmlFor="type-services" className="text-sm text-gray-700 cursor-pointer">
                        Services
                      </label>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>

            <Card className="sticky top-4">
              <CardHeader>
                <div className="flex items-center justify-between">
                  <CardTitle className="text-lg">Filters</CardTitle>
                  <div className="flex items-center space-x-2">
                    {activeFiltersCount > 0 && (
                      <Badge variant="secondary" className="text-xs">
                        {activeFiltersCount}
                      </Badge>
                    )}
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={clearFilters}
                      className="text-buylock-primary hover:text-buylock-primary/80"
                    >
                      Clear All
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-6">
                {/* Category Filter */}
                <div>
                  <div className="flex items-center justify-between mb-3">
                    <h3 className="font-semibold text-gray-900">Category</h3>
                    {selectedCategories.length > 0 && (
                      <Button 
                        variant="ghost" 
                        size="sm" 
                        onClick={clearAllCategories}
                        className="text-xs text-buylock-primary hover:text-buylock-primary/80"
                      >
                        Clear
                      </Button>
                    )}
                  </div>
                  <div className="space-y-2 max-h-48 overflow-y-auto">
                    {categories.map((category) => {
                      const categoryProducts = filteredProducts.filter(p => p.categoryId === category.id);
                      const categoryServices = filteredServices.filter(s => s.categoryId === category.id);
                      const categoryTotal = categoryProducts.length + categoryServices.length;
                      const isSelected = selectedCategories.includes(category.id);

                      return (
                        <div 
                          key={category.id}
                          className="flex items-center space-x-3 p-2 rounded-lg hover:bg-gray-50 transition-colors"
                        >
                          <Checkbox
                            id={`category-${category.id}`}
                            checked={isSelected}
                            onCheckedChange={() => handleCategoryToggle(category.id)}
                          />
                          <label 
                            htmlFor={`category-${category.id}`}
                            className="flex-1 cursor-pointer"
                          >
                            <div className="flex items-center justify-between">
                              <div className="flex flex-col">
                                <span className="text-sm font-medium text-gray-700">
                                  {category.name}
                                </span>
                                {categoryProducts.length > 0 && categoryServices.length > 0 && (
                                  <span className="text-xs text-gray-500">
                                    {categoryProducts.length}p, {categoryServices.length}s
                                  </span>
                                )}
                              </div>
                              <Badge 
                                variant={categoryTotal > 0 ? "secondary" : "outline"} 
                                className={`text-xs ${categoryTotal === 0 ? "text-gray-400" : ""}`}
                              >
                                {categoryTotal}
                              </Badge>
                            </div>
                          </label>
                        </div>
                      );
                    })}
                  </div>
                </div>

                <Separator />

                {/* Brand Filter (only for products) */}
                {selectedTypes.includes("products") && brands.length > 0 && (
                  <>
                    <div>
                      <h3 className="font-semibold text-gray-900 mb-3">Brand</h3>
                      <Select value={selectedBrand} onValueChange={setSelectedBrand}>
                        <SelectTrigger>
                          <SelectValue placeholder="All Brands" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="all">All Brands</SelectItem>
                          {brands.map((brand) => (
                            <SelectItem key={brand} value={brand}>
                              {brand}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                    <Separator />
                  </>
                )}

                {/* Price Range */}
                <div>
                  <h3 className="font-semibold text-gray-900 mb-3">Price Range</h3>
                  <div className="space-y-4">
                    <Slider
                      value={priceRange}
                      onValueChange={setPriceRange}
                      max={500000}
                      min={0}
                      step={1000}
                      className="w-full"
                    />
                    <div className="flex justify-between text-sm text-gray-600">
                      <span>KES {priceRange[0].toLocaleString()}</span>
                      <span>KES {priceRange[1].toLocaleString()}</span>
                    </div>
                  </div>
                </div>

                <Separator />

                {/* Rating Filter */}
                <div>
                  <h3 className="font-semibold text-gray-900 mb-3">Customer Rating</h3>
                  <div className="space-y-2">
                    {[4, 3, 2, 1].map((rating) => (
                      <div key={rating} className="flex items-center space-x-2">
                        <Checkbox
                          id={`rating-${rating}`}
                          checked={selectedRating === rating}
                          onCheckedChange={(checked) => setSelectedRating(checked ? rating : null)}
                        />
                        <label htmlFor={`rating-${rating}`} className="cursor-pointer">
                          {renderStarRating(rating)}
                        </label>
                      </div>
                    ))}
                  </div>
                </div>

                <Separator />

                {/* Availability Filters */}
                <div>
                  <h3 className="font-semibold text-gray-900 mb-3">Availability</h3>
                  <div className="space-y-2">
                    {selectedTypes.includes("products") && (
                      <div className="flex items-center space-x-2">
                        <Checkbox
                          id="in-stock"
                          checked={inStock}
                          onCheckedChange={(checked) => setInStock(checked === true)}
                        />
                        <label htmlFor="in-stock" className="text-sm text-gray-700 cursor-pointer">
                          In Stock
                        </label>
                      </div>
                    )}
                    {selectedTypes.includes("services") && (
                      <div className="flex items-center space-x-2">
                        <Checkbox
                          id="available-today"
                          checked={availableToday}
                          onCheckedChange={(checked) => setAvailableToday(checked === true)}
                        />
                        <label htmlFor="available-today" className="text-sm text-gray-700 cursor-pointer">
                          Available Today
                        </label>
                      </div>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Results Area */}
          <div className="flex-1">
            {/* Search Bar Above Results */}
            <div className="mb-6">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                <Input
                  type="text"
                  placeholder="Search products and services..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10 pr-4 py-3 w-full border border-gray-300 rounded-lg focus:ring-2 focus:ring-buylock-primary focus:border-transparent"
                />
              </div>
            </div>

            {/* Controls Bar with Results Counter and Sort */}
            <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-6 gap-4">
              <div className="flex items-center space-x-4">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setShowFilters(!showFilters)}
                  className="lg:hidden"
                >
                  <Filter className="w-4 h-4 mr-2" />
                  Filters
                  {activeFiltersCount > 0 && (
                    <Badge variant="secondary" className="ml-2 text-xs">
                      {activeFiltersCount}
                    </Badge>
                  )}
                </Button>

                <p className="text-sm text-gray-600">
                  {sortedItems.length} result{sortedItems.length !== 1 ? "s" : ""} found
                </p>
              </div>

              {/* Sort Filter */}
              <div className="w-full sm:w-auto sm:min-w-[200px]">
                <Select value={sortBy} onValueChange={setSortBy}>
                  <SelectTrigger className="w-full">
                    <SelectValue placeholder="Sort by" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="relevance">Relevance</SelectItem>
                    <SelectItem value="price-low">Price: Low to High</SelectItem>
                    <SelectItem value="price-high">Price: High to Low</SelectItem>
                    <SelectItem value="rating">Highest Rated</SelectItem>
                    <SelectItem value="newest">Newest</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            {/* Results Grid */}
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
              {(productsLoading || servicesLoading) ? (
                Array.from({ length: 12 }).map((_, i) => (
                  <div key={i} className="bg-white rounded-xl shadow-sm border">
                    <Skeleton className="w-full h-48 rounded-t-xl" />
                    <div className="p-4 space-y-3">
                      <Skeleton className="h-4 w-3/4" />
                      <Skeleton className="h-4 w-1/2" />
                      <Skeleton className="h-4 w-1/3" />
                      <Skeleton className="h-10 w-full" />
                    </div>
                  </div>
                ))
              ) : sortedItems.length === 0 ? (
                <div className="col-span-full text-center py-12">
                  <p className="text-gray-500 text-lg">No items found</p>
                  <p className="text-gray-400 text-sm mt-2">Try adjusting your search or filters</p>
                </div>
              ) : (
                sortedItems.map((item) => {
                  // Debug log item before passing to component
                  if (nearestToMeEnabled) {
                    console.log(`Rendering item: ${item.name}, distance: ${item.distance}`);
                  }
                  return "priceType" in item ? (
                    <ServiceCard key={item.id} service={item as Service} showDistanceBadge={nearestToMeEnabled} />
                  ) : (
                    <ProductCard key={item.id} product={item as Product} showDistanceBadge={nearestToMeEnabled} />
                  )
                })
              )}
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

client/src/pages/test-delivery-workflow..content:
import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  CheckCircle,
  Clock,
  Truck,
  Package,
  ArrowRight,
  AlertCircle,
  Play,
  RotateCcw,
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { Header } from "@/components/header";
import { Footer } from "@/components/footer";

interface Order {
  id: string;
  status: string;
  totalAmount: string;
  deliveryAddress: string;
  deliveryFee: string;
  courierId?: string;
  courierName?: string;
  trackingNumber?: string;
  createdAt: string;
  orderItems: Array<{
    id: string;
    productName?: string;
    serviceName?: string;
    quantity: number;
    price: string;
  }>;
}

interface DeliveryProvider {
  id: string;
  name: string;
  type: string;
  estimatedDeliveryTime: string;
}

export default function TestDeliveryWorkflow() {
  const [testOrderId, setTestOrderId] = useState<string>('');
  const [selectedProvider, setSelectedProvider] = useState('');
  const [trackingId, setTrackingId] = useState('');
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Get delivery providers
  const { data: providers = [] } = useQuery<DeliveryProvider[]>({
    queryKey: ['/api/delivery/providers'],
  });

  // Get test orders
  const { data: orders = [] } = useQuery<Order[]>({
    queryKey: ['/api/admin/orders'],
    refetchInterval: 2000, // Auto refresh every 2 seconds
  });

  // Create test order mutation
  const createTestOrderMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest('/api/orders/test', {
        method: 'POST',
        body: {
          vendorId: '74bf6c33-7f09-4844-903d-72bff3849c95',
          customerId: '37410516',
          totalAmount: Math.floor(Math.random() * 5000 + 500).toString() + '.00',
          deliveryFee: '150.00',
          deliveryAddress: 'Nairobi CBD, Kenya - Test Address',
          status: 'paid',
          orderItems: [
            {
              productId: '82c07c4e-70f2-4c1d-b688-57df2b234567',
              quantity: 1,
              price: Math.floor(Math.random() * 3000 + 300).toString() + '.00'
            }
          ]
        },
      });
    },
    onSuccess: (newOrder) => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/orders'] });
      setTestOrderId(newOrder.id);
      toast({
        title: "Test Order Created",
        description: `Order ${newOrder.id.slice(-8)} created successfully`,
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create test order",
        variant: "destructive",
      });
    },
  });

  // Confirm order mutation
  const confirmOrderMutation = useMutation({
    mutationFn: async (orderId: string) => {
      return await apiRequest(`/api/orders/${orderId}/confirm`, {
        method: 'POST',
        body: { vendorNotes: 'Test order confirmed via delivery workflow demo' },
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/orders'] });
      toast({
        title: "Order Confirmed",
        description: "Order has been confirmed and is ready for dispatch",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to confirm order",
        variant: "destructive",
      });
    },
  });

  // Dispatch order mutation
  const dispatchOrderMutation = useMutation({
    mutationFn: async (data: { orderId: string; providerId: string; trackingId?: string }) => {
      return await apiRequest(`/api/orders/${data.orderId}/dispatch`, {
        method: 'POST',
        body: {
          providerId: data.providerId,
          trackingId: data.trackingId || undefined
        },
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/orders'] });
      toast({
        title: "Order Dispatched",
        description: "Order has been assigned to courier for delivery",
      });
      setSelectedProvider('');
      setTrackingId('');
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to dispatch order",
        variant: "destructive",
      });
    },
  });

  // Update delivery status mutation
  const updateDeliveryStatusMutation = useMutation({
    mutationFn: async (data: { orderId: string; status: string }) => {
      return await apiRequest(`/api/orders/${data.orderId}/delivery-status`, {
        method: 'POST',
        body: { status: data.status },
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/orders'] });
      toast({
        title: "Status Updated",
        description: "Order delivery status updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update delivery status",
        variant: "destructive",
      });
    },
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'paid':
        return 'bg-blue-100 text-blue-800';
      case 'confirmed':
        return 'bg-green-100 text-green-800';
      case 'awaiting_dispatch':
        return 'bg-yellow-100 text-yellow-800';
      case 'in_delivery':
        return 'bg-purple-100 text-purple-800';
      case 'delivered':
        return 'bg-emerald-100 text-emerald-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'paid':
        return <Clock className="w-4 h-4" />;
      case 'confirmed':
        return <CheckCircle className="w-4 h-4" />;
      case 'awaiting_dispatch':
        return <Package className="w-4 h-4" />;
      case 'in_delivery':
        return <Truck className="w-4 h-4" />;
      case 'delivered':
        return <CheckCircle className="w-4 h-4" />;
      default:
        return <AlertCircle className="w-4 h-4" />;
    }
  };

  const getNextStatus = (currentStatus: string) => {
    switch (currentStatus) {
      case 'paid':
        return 'confirmed';
      case 'confirmed':
        return 'awaiting_dispatch';
      case 'awaiting_dispatch':
        return 'in_delivery';
      case 'in_delivery':
        return 'delivered';
      default:
        return null;
    }
  };

  const getActionLabel = (currentStatus: string) => {
    switch (currentStatus) {
      case 'paid':
        return 'Confirm Order';
      case 'confirmed':
        return 'Dispatch Order';
      case 'awaiting_dispatch':
        return 'Mark In Delivery';
      case 'in_delivery':
        return 'Mark Delivered';
      default:
        return 'Complete';
    }
  };

  const testOrders = orders.slice(0, 3); // Show only recent test orders

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Delivery Workflow Testing
          </h1>
          <p className="text-gray-600">
            Demonstrate the sophisticated delivery workflow: Paid â†’ Confirmed â†’ Awaiting Dispatch â†’ In Delivery â†’ Delivered
          </p>
        </div>

        {/* Control Panel */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle>Workflow Control Panel</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex space-x-4">
              <Button
                onClick={() => createTestOrderMutation.mutate()}
                disabled={createTestOrderMutation.isPending}
                className="flex items-center space-x-2"
              >
                <Play className="w-4 h-4" />
                <span>
                  {createTestOrderMutation.isPending ? 'Creating...' : 'Create Test Order'}
                </span>
              </Button>

              <Button
                variant="outline"
                onClick={() => {
                  queryClient.invalidateQueries({ queryKey: ['/api/admin/orders'] });
                }}
                className="flex items-center space-x-2"
              >
                <RotateCcw className="w-4 h-4" />
                <span>Refresh Orders</span>
              </Button>
            </div>

            {testOrders.length > 0 && (
              <div className="border rounded-lg p-4 bg-blue-50">
                <h4 className="font-medium text-blue-900 mb-2">Active Test Orders</h4>
                <div className="space-y-2">
                  {testOrders.map((order) => (
                    <div key={order.id} className="flex items-center justify-between text-sm">
                      <span className="text-blue-800">
                        Order #{order.id.slice(-8)} - KES {Number(order.totalAmount).toLocaleString()}
                      </span>
                      <Badge className={getStatusColor(order.status)}>
                        <div className="flex items-center space-x-1">
                          {getStatusIcon(order.status)}
                          <span className="capitalize">{order.status.replace('_', ' ')}</span>
                        </div>
                      </Badge>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Workflow Demonstration */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {testOrders.map((order) => (
            <Card key={order.id} className="">
              <CardHeader>
                <div className="flex items-center justify-between">
                  <CardTitle className="text-lg">
                    Order #{order.id.slice(-8)}
                  </CardTitle>
                  <Badge className={getStatusColor(order.status)}>
                    <div className="flex items-center space-x-1">
                      {getStatusIcon(order.status)}
                      <span className="capitalize">{order.status.replace('_', ' ')}</span>
                    </div>
                  </Badge>
                </div>
              </CardHeader>
              <CardContent className="space-y-6">
                {/* Order Details */}
                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <span className="text-gray-600">Total Amount</span>
                    <p className="font-medium">KES {Number(order.totalAmount).toLocaleString()}</p>
                  </div>
                  <div>
                    <span className="text-gray-600">Delivery Fee</span>
                    <p className="font-medium">KES {Number(order.deliveryFee).toLocaleString()}</p>
                  </div>
                  <div className="col-span-2">
                    <span className="text-gray-600">Address</span>
                    <p className="font-medium text-xs">{order.deliveryAddress}</p>
                  </div>
                </div>

                {/* Workflow Progress */}
                <div>
                  <h4 className="font-medium text-sm mb-3">Workflow Progress</h4>
                  <div className="space-y-2">
                    {['paid', 'confirmed', 'awaiting_dispatch', 'in_delivery', 'delivered'].map((status, index) => (
                      <div key={status} className={`flex items-center space-x-3 p-2 rounded-lg ${
                        order.status === status ? 'bg-buylock-primary/10 border border-buylock-primary' :
                        ['confirmed', 'awaiting_dispatch', 'in_delivery', 'delivered'].includes(order.status) && 
                        ['paid', 'confirmed', 'awaiting_dispatch', 'in_delivery'].includes(status) &&
                        ['paid', 'confirmed', 'awaiting_dispatch', 'in_delivery', 'delivered'].indexOf(status) < 
                        ['paid', 'confirmed', 'awaiting_dispatch', 'in_delivery', 'delivered'].indexOf(order.status)
                        ? 'bg-green-50 border border-green-200' :
                        'bg-gray-50 border border-gray-200'
                      }`}>
                        <div className={`flex-shrink-0 w-6 h-6 rounded-full flex items-center justify-center text-xs ${
                          ['confirmed', 'awaiting_dispatch', 'in_delivery', 'delivered'].includes(order.status) && 
                          ['paid', 'confirmed', 'awaiting_dispatch', 'in_delivery'].includes(status) &&
                          ['paid', 'confirmed', 'awaiting_dispatch', 'in_delivery', 'delivered'].indexOf(status) < 
                          ['paid', 'confirmed', 'awaiting_dispatch', 'in_delivery', 'delivered'].indexOf(order.status)
                          ? 'bg-green-500 text-white' :
                          order.status === status ? 'bg-buylock-primary text-white' :
                          'bg-gray-300 text-gray-600'
                        }`}>
                          {getStatusIcon(status)}
                        </div>
                        <div className="flex-1">
                          <div className="font-medium text-xs capitalize">
                            {status.replace('_', ' ')}
                          </div>
                        </div>
                        {order.status === status && (
                          <ArrowRight className="w-4 h-4 text-buylock-primary" />
                        )}
                      </div>
                    ))}
                  </div>
                </div>

                {/* Action Buttons */}
                {order.status !== 'delivered' && (
                  <div className="space-y-3">
                    {order.status === 'paid' && (
                      <Button 
                        onClick={() => confirmOrderMutation.mutate(order.id)}
                        disabled={confirmOrderMutation.isPending}
                        className="w-full"
                      >
                        {confirmOrderMutation.isPending ? 'Confirming...' : 'Confirm Order'}
                      </Button>
                    )}

                    {order.status === 'confirmed' && (
                      <div className="space-y-3">
                        <div className="grid grid-cols-2 gap-2">
                          <div>
                            <Label htmlFor={`provider-${order.id}`} className="text-xs">Courier</Label>
                            <Select 
                              value={selectedProvider} 
                              onValueChange={setSelectedProvider}
                            >
                              <SelectTrigger className="h-8 text-xs">
                                <SelectValue placeholder="Select courier" />
                              </SelectTrigger>
                              <SelectContent>
                                {providers.map((provider) => (
                                  <SelectItem key={provider.id} value={provider.id}>
                                    {provider.name}
                                  </SelectItem>
                                ))}
                              </SelectContent>
                            </Select>
                          </div>
                          <div>
                            <Label htmlFor={`tracking-${order.id}`} className="text-xs">Tracking</Label>
                            <Input
                              id={`tracking-${order.id}`}
                              value={trackingId}
                              onChange={(e) => setTrackingId(e.target.value)}
                              placeholder="Optional"
                              className="h-8 text-xs"
                            />
                          </div>
                        </div>
                        <Button 
                          onClick={() => dispatchOrderMutation.mutate({ 
                            orderId: order.id, 
                            providerId: selectedProvider,
                            trackingId: trackingId || undefined
                          })}
                          disabled={dispatchOrderMutation.isPending || !selectedProvider}
                          className="w-full"
                        >
                          {dispatchOrderMutation.isPending ? 'Dispatching...' : 'Dispatch Order'}
                        </Button>
                      </div>
                    )}

                    {(order.status === 'awaiting_dispatch' || order.status === 'in_delivery') && (
                      <Button 
                        onClick={() => updateDeliveryStatusMutation.mutate({ 
                          orderId: order.id, 
                          status: getNextStatus(order.status)! 
                        })}
                        disabled={updateDeliveryStatusMutation.isPending}
                        className="w-full"
                      >
                        {updateDeliveryStatusMutation.isPending ? 'Updating...' : getActionLabel(order.status)}
                      </Button>
                    )}
                  </div>
                )}

                {order.status === 'delivered' && (
                  <div className="bg-green-50 p-3 rounded-lg text-center">
                    <CheckCircle className="w-6 h-6 text-green-600 mx-auto mb-1" />
                    <p className="text-green-800 font-medium text-sm">Order Completed!</p>
                    <p className="text-green-600 text-xs">Workflow demonstration successful</p>
                  </div>
                )}
              </CardContent>
            </Card>
          ))}
        </div>

        {/* Courier Status */}
        <Card className="mt-8">
          <CardHeader>
            <CardTitle>Active Delivery Providers</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              {providers.map((provider) => (
                <div key={provider.id} className="bg-gray-50 p-3 rounded-lg">
                  <div className="flex items-center justify-between mb-2">
                    <span className="font-medium text-sm">{provider.name}</span>
                    <Badge variant={provider.type === 'dispatch' ? 'default' : 'secondary'}>
                      {provider.type}
                    </Badge>
                  </div>
                  <p className="text-xs text-gray-600">{provider.estimatedDeliveryTime}</p>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>

      <Footer />
    </div>
  );
}

client/src/pages/vendor-tasks.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Header } from "@/components/header";
import { Footer } from "@/components/footer";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { 
  Clock, 
  MapPin, 
  CheckCircle, 
  AlertCircle,
  User,
  Phone,
  Navigation,
  FileText,
  Calendar,
  DollarSign,
  Eye,
  Edit
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { isUnauthorizedError } from "@/lib/authUtils";
import { format } from "date-fns";

// Using the vendor ID from our test data
const VENDOR_ID = "74bf6c33-7f09-4844-903d-72bff3849c95";

interface Task {
  id: string;
  userId: string;
  status: string;
  totalAmount: string;
  deliveryAddress: string;
  notes?: string;
  createdAt: string;
  type: string;
  paymentStatus: string;
}

interface TaskDetails {
  order: Task;
  items: Array<{
    id: string;
    serviceId: string;
    name: string;
    duration?: number;
    appointmentDate?: string;
    appointmentTime?: string;
    serviceNotes?: string;
    serviceLatitude?: number;
    serviceLongitude?: number;
    serviceAddress?: string;
    serviceInstructions?: string;
    serviceTaskStatus?: string;
    price: string;
  }>;
}

export default function VendorTasks() {
  const [selectedTask, setSelectedTask] = useState<string | null>(null);
  const [vendorNotes, setVendorNotes] = useState("");
  const { toast } = useToast();
  const { isAuthenticated } = useAuth();
  const queryClient = useQueryClient();

  // Fetch vendor tasks
  const { data: tasks, isLoading } = useQuery<Task[]>({
    queryKey: ["/api/vendor/tasks", VENDOR_ID],
    queryFn: async () => {
      const response = await fetch(`/api/vendor/tasks?vendorId=${VENDOR_ID}`);
      if (!response.ok) throw new Error("Failed to fetch tasks");
      return response.json();
    },
    enabled: !!isAuthenticated,
  });

  // Fetch task details
  const { data: taskDetails } = useQuery<TaskDetails>({
    queryKey: ["/api/vendor/tasks", selectedTask],
    queryFn: async () => {
      const response = await fetch(`/api/vendor/tasks/${selectedTask}`);
      if (!response.ok) throw new Error("Failed to fetch task details");
      return response.json();
    },
    enabled: !!selectedTask,
  });

  // Update task status mutation
  const updateStatusMutation = useMutation({
    mutationFn: async ({ orderId, status, notes }: { orderId: string; status: string; notes?: string }) => {
      return apiRequest(`/api/vendor/tasks/${orderId}/status`, "PATCH", {
        status,
        vendorNotes: notes,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/vendor/tasks"] });
      toast({
        title: "Status updated",
        description: "Task status has been updated successfully",
      });
      setVendorNotes("");
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "Please log in to continue",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }

      toast({
        title: "Update failed",
        description: error.message || "Failed to update task status",
        variant: "destructive",
      });
    },
  });

  const formatPrice = (price: string | number) => {
    const numPrice = typeof price === 'string' ? parseFloat(price) : price;
    return new Intl.NumberFormat('en-KE', {
      style: 'currency',
      currency: 'KES',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(numPrice);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending_acceptance':
        return 'bg-yellow-100 text-yellow-800';
      case 'accepted':
        return 'bg-blue-100 text-blue-800';
      case 'in_progress':
        return 'bg-purple-100 text-purple-800';
      case 'completed':
        return 'bg-green-100 text-green-800';
      case 'cancelled':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'pending_acceptance':
        return <AlertCircle className="w-4 h-4" />;
      case 'accepted':
      case 'in_progress':
        return <Clock className="w-4 h-4" />;
      case 'completed':
        return <CheckCircle className="w-4 h-4" />;
      default:
        return <AlertCircle className="w-4 h-4" />;
    }
  };

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-16 text-center">
          <h1 className="text-2xl font-bold mb-4">Access Denied</h1>
          <p className="text-gray-600 mb-8">Please log in to access vendor tasks</p>
          <Button onClick={() => window.location.href = "/api/login"}>
            Log In
          </Button>
        </div>
        <Footer />
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-8">
          <div className="text-center">Loading tasks...</div>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Service Tasks</h1>
          <p className="text-gray-600">Manage your service appointments and track customer requests</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Tasks List */}
          <div className="lg:col-span-2">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <FileText className="w-5 h-5" />
                  Service Requests ({tasks?.length || 0})
                </CardTitle>
              </CardHeader>
              <CardContent>
                {!tasks || tasks.length === 0 ? (
                  <div className="text-center py-8">
                    <FileText className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                    <h3 className="text-lg font-medium text-gray-900 mb-2">No service requests</h3>
                    <p className="text-gray-600">Service bookings will appear here once customers request your services.</p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {tasks.map((task) => (
                      <div 
                        key={task.id}
                        className={`p-4 border rounded-lg cursor-pointer transition-colors ${
                          selectedTask === task.id ? 'border-buylock-primary bg-orange-50' : 'border-gray-200 hover:border-gray-300'
                        }`}
                        onClick={() => setSelectedTask(task.id)}
                      >
                        <div className="flex justify-between items-start mb-3">
                          <div className="flex items-center gap-3">
                            <Badge className={getStatusColor(task.status)}>
                              {getStatusIcon(task.status)}
                              <span className="ml-1 capitalize">{task.status.replace('_', ' ')}</span>
                            </Badge>
                            <span className="text-sm text-gray-500">
                              {format(new Date(task.createdAt), "MMM d, yyyy")}
                            </span>
                          </div>
                          <span className="font-semibold text-buylock-primary">
                            {formatPrice(task.totalAmount)}
                          </span>
                        </div>

                        <div className="flex items-center gap-2 text-sm text-gray-600 mb-2">
                          <MapPin className="w-4 h-4" />
                          <span className="truncate">{task.deliveryAddress}</span>
                        </div>

                        {task.notes && (
                          <p className="text-sm text-gray-600 truncate">
                            Note: {task.notes}
                          </p>
                        )}
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Task Details */}
          <div className="lg:col-span-1">
            {selectedTask && taskDetails ? (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Eye className="w-5 h-5" />
                    Task Details
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-6">
                  {taskDetails.items.map((item) => (
                    <div key={item.id} className="space-y-4">
                      <div>
                        <h4 className="font-semibold text-lg">{item.name}</h4>
                        <p className="text-sm text-gray-600">
                          {item.duration} hours â€¢ {formatPrice(item.price)}
                        </p>
                      </div>

                      {/* Appointment Details */}
                      {item.appointmentDate && (
                        <div className="flex items-center gap-2 text-sm">
                          <Calendar className="w-4 h-4 text-gray-500" />
                          <span>
                            {format(new Date(item.appointmentDate), "PPP")} at {item.appointmentTime}
                          </span>
                        </div>
                      )}

                      {/* Location */}
                      <div className="space-y-2">
                        <Label className="text-sm font-medium">Service Location</Label>
                        <div className="flex items-start gap-2 text-sm">
                          <MapPin className="w-4 h-4 text-gray-500 mt-0.5" />
                          <span>{item.serviceAddress}</span>
                        </div>
                        {item.serviceLatitude && item.serviceLongitude && (
                          <div className="flex items-center gap-2 text-xs text-green-600">
                            <Navigation className="w-3 h-3" />
                            <span>Pin: {item.serviceLatitude?.toFixed(6)}, {item.serviceLongitude?.toFixed(6)}</span>
                          </div>
                        )}
                      </div>

                      {/* Detailed Instructions */}
                      {item.serviceInstructions && (
                        <div className="space-y-2">
                          <Label className="text-sm font-medium">Service Instructions</Label>
                          <div className="p-3 bg-blue-50 rounded text-sm">
                            {item.serviceInstructions}
                          </div>
                        </div>
                      )}

                      {/* Additional Notes */}
                      {item.serviceNotes && (
                        <div className="space-y-2">
                          <Label className="text-sm font-medium">Customer Notes</Label>
                          <div className="p-3 bg-gray-50 rounded text-sm">
                            {item.serviceNotes}
                          </div>
                        </div>
                      )}

                      <Separator />

                      {/* Status Update */}
                      <div className="space-y-4">
                        <Label className="text-sm font-medium">Update Status</Label>

                        <Select 
                          onValueChange={(status) => {
                            updateStatusMutation.mutate({ 
                              orderId: taskDetails.order.id, 
                              status,
                              notes: vendorNotes || undefined
                            });
                          }}
                          disabled={updateStatusMutation.isPending}
                        >
                          <SelectTrigger>
                            <SelectValue placeholder="Select new status" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="accepted">Accept Task</SelectItem>
                            <SelectItem value="in_progress">Mark In Progress</SelectItem>
                            <SelectItem value="completed">Mark Completed</SelectItem>
                            <SelectItem value="cancelled">Cancel Task</SelectItem>
                          </SelectContent>
                        </Select>

                        <div className="space-y-2">
                          <Label htmlFor="vendorNotes" className="text-sm">
                            Vendor Notes (Optional)
                          </Label>
                          <Textarea
                            id="vendorNotes"
                            placeholder="Add any notes for the customer..."
                            value={vendorNotes}
                            onChange={(e) => setVendorNotes(e.target.value)}
                            rows={3}
                          />
                        </div>
                      </div>
                    </div>
                  ))}
                </CardContent>
              </Card>
            ) : (
              <Card>
                <CardContent className="py-16 text-center">
                  <Eye className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">Select a task</h3>
                  <p className="text-gray-600">Choose a task from the list to view details and manage status.</p>
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

