import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Store, Eye, EyeOff } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

export default function VendorLogin() {
  const [, setLocation] = useLocation();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const { toast } = useToast();

  const loginMutation = useMutation({
    mutationFn: async (data: { email: string; password: string }) => {
      const response = await fetch("/api/vendor/login", {
        method: "POST",
        body: JSON.stringify(data),
        headers: { "Content-Type": "application/json" },
      });
      if (!response.ok) {
        throw new Error("Login failed");
      }
      return response.json();
    },
    onSuccess: (vendorData) => {
      // Store vendor data in localStorage for session management
      localStorage.setItem('vendorData', JSON.stringify(vendorData));
      toast({
        title: "Login successful",
        description: "Welcome to your vendor dashboard!",
      });
      setLocation("/vendor-dashboard");
    },
    onError: (error: any) => {
      console.error("Login error:", error);

      // Handle approval status error specifically
      if (error.message && error.message.includes("403")) {
        toast({
          title: "Account Not Approved",
          description: "Your vendor account is pending approval by our admin team. You will be notified once your account is verified.",
          variant: "destructive",
        });
      } else {
        toast({
          title: "Login failed",
          description: error.message || "Please check your credentials and try again.",
          variant: "destructive",
        });
      }
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!email || !password) {
      toast({
        title: "Missing fields",
        description: "Please enter both email and password.",
        variant: "destructive",
      });
      return;
    }
    loginMutation.mutate({ email, password });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-buylock-primary/10 via-white to-buylock-secondary/20 flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="space-y-1 text-center">
          <div className="flex items-center justify-center mb-4">
            <div className="bg-buylock-primary text-white p-3 rounded-xl">
              <Store className="w-8 h-8" />
            </div>
          </div>
          <CardTitle className="text-2xl font-bold">Vendor Login</CardTitle>
          <p className="text-gray-600">Access your BuyLock vendor dashboard</p>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="email">Email Address</Label>
              <Input
                id="email"
                type="email"
                placeholder="vendor@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="password">Password</Label>
              <div className="relative">
                <Input
                  id="password"
                  type={showPassword ? "text" : "password"}
                  placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-2 top-1/2 transform -translate-y-1/2 h-auto p-1"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? (
                    <EyeOff className="w-4 h-4" />
                  ) : (
                    <Eye className="w-4 h-4" />
                  )}
                </Button>
              </div>
            </div>

            <Alert className="bg-blue-50 border-blue-200">
              <AlertDescription>
                <strong>Demo Account:</strong><br />
                Email: vendor@buylock.com<br />
                Password: password123
              </AlertDescription>
            </Alert>

            <Button
              type="submit"
              className="w-full bg-buylock-primary hover:bg-buylock-primary/90"
              disabled={loginMutation.isPending}
            >
              {loginMutation.isPending ? "Signing in..." : "Sign In"}
            </Button>
          </form>

          <div className="mt-6 text-center space-y-2">
            <p className="text-sm text-gray-600">
              Don't have a vendor account?
            </p>
            <Button
              variant="outline"
              className="w-full"
              onClick={() => setLocation("/vendor/registration")}
            >
              Register as Vendor
            </Button>
          </div>

          <div className="mt-4 text-center">
            <Button
              variant="ghost"
              className="text-sm text-gray-600"
              onClick={() => setLocation("/")}
            >
              â† Back to Main Site
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

vendor-dashboard/client/src/components/v.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { 
  Store, 
  Package, 
  Users, 
  Settings, 
  LogOut,
  Menu,
  X,
  Home
} from "lucide-react";
import { apiRequest } from "@/lib/api";
import type { Vendor } from "@shared/schema";

interface VendorLayoutProps {
  children: React.ReactNode;
}

const navigation = [
  { name: "Dashboard", href: "/", icon: Home },
  { name: "Products", href: "/products", icon: Package },
  { name: "Services", href: "/services", icon: Users },
  { name: "Settings", href: "/settings", icon: Settings },
];

export default function VendorLayout({ children }: VendorLayoutProps) {
  const [location, setLocation] = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const queryClient = useQueryClient();

  const { data: vendor } = useQuery<Vendor>({
    queryKey: ["/api/auth/vendor"],
  });

  const logoutMutation = useMutation({
    mutationFn: () => apiRequest("/api/auth/logout", { method: "POST" }),
    onSuccess: () => {
      queryClient.clear();
      setLocation("/");
    },
  });

  const handleLogout = () => {
    logoutMutation.mutate();
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Mobile sidebar */}
