          <button
            onClick={() => window.location.href = "/"}
            className="inline-flex items-center text-sm text-gray-600 hover:text-buylock-primary"
          >
            <ArrowLeft className="w-4 h-4 mr-1" />
            Back to BuyLock Marketplace
          </button>
        </div>
      </div>
    </div>
  );
}

