import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import VendorLayout from "@/components/vendor-layout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Users, Plus, Edit, Trash2, Save, X } from "lucide-react";
import { apiRequest } from "@/lib/api";
import type { Service, Category, InsertServiceInput } from "@shared/schema";

export default function Services() {
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingService, setEditingService] = useState<string | null>(null);
  const queryClient = useQueryClient();

  const { data: services = [], isLoading } = useQuery<Service[]>({
    queryKey: ["/api/services"],
  });

  const { data: categories = [] } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
  });

  const [formData, setFormData] = useState<InsertServiceInput>({
    name: "",
    description: "",
    price: "0",
    categoryId: "",
    duration: "",
    location: "",
    images: [],
    availableToday: false,
    featured: false,
    active: true,
  });

  const createMutation = useMutation({
    mutationFn: (data: InsertServiceInput) =>
      apiRequest("/api/services", {
        method: "POST",
        body: JSON.stringify(data),
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/services"] });
      setShowAddForm(false);
      resetForm();
    },
  });

  const updateMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: Partial<InsertServiceInput> }) =>
      apiRequest(`/api/services/${id}`, {
        method: "PUT",
        body: JSON.stringify(data),
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/services"] });
      setEditingService(null);
      resetForm();
    },
  });

  const deleteMutation = useMutation({
    mutationFn: (id: string) =>
      apiRequest(`/api/services/${id}`, { method: "DELETE" }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/services"] });
    },
  });

  const resetForm = () => {
    setFormData({
      name: "",
      description: "",
      price: "0",
      categoryId: "",
      duration: "",
      location: "",
      images: [],
      availableToday: false,
      featured: false,
      active: true,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (editingService) {
      updateMutation.mutate({ id: editingService, data: formData });
    } else {
      createMutation.mutate(formData);
    }
  };

  const handleEdit = (service: Service) => {
    setFormData({
      name: service.name,
      description: service.description || "",
      price: service.price,
      categoryId: service.categoryId,
      duration: service.duration || "",
      location: service.location || "",
      images: service.images || [],
      availableToday: service.availableToday || false,
      featured: service.featured || false,
      active: service.active || true,
    });
    setEditingService(service.id);
    setShowAddForm(true);
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value, type } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === "checkbox" ? (e.target as HTMLInputElement).checked : value
    }));
  };

  if (isLoading) {
    return (
      <VendorLayout>
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-buylock-primary"></div>
        </div>
      </VendorLayout>
    );
  }

  return (
    <VendorLayout>
      <div className="space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Services</h1>
            <p className="text-gray-600 mt-1">Manage your service offerings</p>
          </div>
          <Button onClick={() => setShowAddForm(true)} disabled={showAddForm}>
            <Plus className="w-4 h-4 mr-2" />
            Add Service
          </Button>
        </div>

        {/* Add/Edit Form */}
        {showAddForm && (
          <Card>
            <CardHeader>
              <CardTitle>
                {editingService ? "Edit Service" : "Add New Service"}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="name">Service Name</Label>
                    <Input
                      id="name"
                      name="name"
                      value={formData.name}
                      onChange={handleChange}
                      required
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="categoryId">Category</Label>
                    <select
                      id="categoryId"
                      name="categoryId"
                      value={formData.categoryId}
                      onChange={handleChange}
                      className="flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm"
                      required
                    >
                      <option value="">Select Category</option>
                      {categories.map((category) => (
                        <option key={category.id} value={category.id}>
                          {category.name}
                        </option>
                      ))}
                    </select>
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="price">Price (KES)</Label>
                    <Input
                      id="price"
                      name="price"
                      type="number"
                      step="0.01"
                      value={formData.price}
                      onChange={handleChange}
                      required
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="duration">Duration</Label>
                    <Input
                      id="duration"
                      name="duration"
                      placeholder="e.g., 2 hours, 1 day, 1 week"
                      value={formData.duration}
                      onChange={handleChange}
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="location">Location</Label>
                    <Input
                      id="location"
                      name="location"
                      placeholder="e.g., Remote, On-site, Lagos"
                      value={formData.location}
                      onChange={handleChange}
                    />
                  </div>

                  <div className="space-y-2">
                    <Label>Availability & Status</Label>
                    <div className="flex items-center space-x-4">
                      <label className="flex items-center">
                        <input
                          type="checkbox"
                          name="active"
                          checked={formData.active}
                          onChange={handleChange}
                          className="mr-2"
                        />
                        Active
                      </label>
                      <label className="flex items-center">
                        <input
                          type="checkbox"
                          name="featured"
                          checked={formData.featured}
                          onChange={handleChange}
                          className="mr-2"
                        />
                        Featured
                      </label>
                      <label className="flex items-center">
                        <input
                          type="checkbox"
                          name="availableToday"
                          checked={formData.availableToday}
                          onChange={handleChange}
                          className="mr-2"
                        />
                        Available Today
                      </label>
                    </div>
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="description">Description</Label>
                  <textarea
                    id="description"
                    name="description"
                    rows={3}
                    value={formData.description}
                    onChange={handleChange}
                    className="flex w-full rounded-md border border-input bg-background px-3 py-2 text-sm"
                  />
                </div>

                <div className="flex justify-end space-x-2">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => {
                      setShowAddForm(false);
                      setEditingService(null);
                      resetForm();
                    }}
                  >
                    <X className="w-4 h-4 mr-2" />
                    Cancel
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={createMutation.isPending || updateMutation.isPending}
                  >
                    <Save className="w-4 h-4 mr-2" />
                    {editingService ? "Update" : "Create"} Service
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        )}

        {/* Services List */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {services.length > 0 ? (
            services.map((service) => (
              <Card key={service.id}>
                <CardContent className="p-6">
                  <div className="flex items-start justify-between mb-4">
                    <div className="flex-1 min-w-0">
                      <h3 className="font-semibold text-gray-900 truncate">
                        {service.name}
                      </h3>
                      <p className="text-sm text-gray-600">
                        {categories.find(c => c.id === service.categoryId)?.name}
                      </p>
                    </div>
                    <div className="flex space-x-1">
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={() => handleEdit(service)}
                      >
                        <Edit className="w-4 h-4" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={() => deleteMutation.mutate(service.id)}
                        disabled={deleteMutation.isPending}
                      >
                        <Trash2 className="w-4 h-4 text-red-500" />
                      </Button>
                    </div>
                  </div>

                  <div className="space-y-2">
                    <p className="text-2xl font-bold text-buylock-primary">
                      KES {Number(service.price).toLocaleString()}
                    </p>

                    {service.duration && (
                      <p className="text-sm text-gray-600">Duration: {service.duration}</p>
                    )}

                    {service.location && (
                      <p className="text-sm text-gray-600">Location: {service.location}</p>
                    )}

                    <div className="flex flex-wrap gap-2">
                      <span className={`inline-block px-2 py-1 text-xs rounded-full ${
                        service.active ? "bg-green-100 text-green-800" : "bg-red-100 text-red-800"
                      }`}>
                        {service.active ? "Active" : "Inactive"}
                      </span>
                      {service.featured && (
                        <span className="inline-block px-2 py-1 text-xs rounded-full bg-yellow-100 text-yellow-800">
                          Featured
                        </span>
                      )}
                      {service.availableToday && (
                        <span className="inline-block px-2 py-1 text-xs rounded-full bg-blue-100 text-blue-800">
                          Available Today
                        </span>
                      )}
                    </div>

                    {service.description && (
                      <p className="text-sm text-gray-600 line-clamp-2">
                        {service.description}
                      </p>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))
          ) : (
            <div className="col-span-full text-center py-12">
              <Users className="w-16 h-16 mx-auto mb-4 text-gray-300" />
              <h3 className="text-lg font-semibold text-gray-900 mb-2">No services yet</h3>
              <p className="text-gray-600 mb-4">Get started by adding your first service</p>
              <Button onClick={() => setShowAddForm(true)}>
                <Plus className="w-4 h-4 mr-2" />
                Add Service
              </Button>
            </div>
          )}
        </div>
      </div>
    </VendorLayout>
  );
}

client/src/pages/admin/components/Commis.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { Settings, Percent, Save } from "lucide-react";
import { adminApiRequest, queryClient, getAdminQueryFn } from "@/lib/queryClient";

interface CommissionSettings {
  platformCommissionPercentage: number;
  vendorCommissionPercentage: number;
}

export function CommissionSettings() {
  const { toast } = useToast();
  const [newCommission, setNewCommission] = useState<string>("");

  // Fetch current commission settings
  const { data: commissionSettings, isLoading } = useQuery<CommissionSettings>({
    queryKey: ['/api/admin/commission-settings'],
    queryFn: getAdminQueryFn({ on401: "returnNull" }),
    staleTime: 5 * 60 * 1000, // 5 minutes
  });

  // Update commission settings mutation
  const updateCommissionMutation = useMutation({
    mutationFn: async (platformCommissionPercentage: number) => {
      return await adminApiRequest('/api/admin/commission-settings', 'POST', {
        platformCommissionPercentage
      });
    },
    onSuccess: (data) => {
      toast({
        title: "Commission Updated",
        description: `Platform commission set to ${data.platformCommissionPercentage}% (vendors get ${data.vendorCommissionPercentage}%)`,
      });
      setNewCommission("");
      queryClient.invalidateQueries({ queryKey: ['/api/admin/commission-settings'] });
      // Also refresh other admin data that might be affected
      queryClient.invalidateQueries({ queryKey: ['/api/admin/platform-earnings'] });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/vendor-earnings'] });
    },
    onError: (error: any) => {
      toast({
        title: "Update Failed",
        description: error.message || "Failed to update commission settings",
        variant: "destructive",
      });
    },
  });

  const handleUpdateCommission = () => {
    const commission = parseFloat(newCommission);

    if (isNaN(commission) || commission < 0 || commission > 100) {
      toast({
        title: "Invalid Commission",
        description: "Commission must be a number between 0 and 100",
        variant: "destructive",
      });
      return;
    }

    updateCommissionMutation.mutate(commission);
  };

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Settings className="h-5 w-5" />
            Commission Settings
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="animate-pulse space-y-4">
            <div className="h-4 bg-gray-200 rounded w-3/4"></div>
            <div className="h-10 bg-gray-200 rounded"></div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Settings className="h-5 w-5" />
          Commission Settings
        </CardTitle>
        <CardDescription>
          Configure platform and vendor commission percentages
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Current Settings Display */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div className="space-y-2">
            <Label className="text-sm font-medium">Platform Commission</Label>
            <div className="flex items-center space-x-2">
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-3 flex-1">
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">Platform Gets</span>
                  <div className="flex items-center space-x-1">
                    <span className="text-2xl font-bold text-blue-600">
                      {commissionSettings?.platformCommissionPercentage}
                    </span>
                    <Percent className="h-4 w-4 text-blue-600" />
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div className="space-y-2">
            <Label className="text-sm font-medium">Vendor Commission</Label>
            <div className="flex items-center space-x-2">
              <div className="bg-green-50 border border-green-200 rounded-lg p-3 flex-1">
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">Vendors Get</span>
                  <div className="flex items-center space-x-1">
                    <span className="text-2xl font-bold text-green-600">
                      {commissionSettings?.vendorCommissionPercentage}
                    </span>
                    <Percent className="h-4 w-4 text-green-600" />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Update Commission Form */}
        <div className="border-t pt-6">
          <div className="space-y-4">
            <Label className="text-sm font-medium">Update Platform Commission Percentage</Label>
            <div className="flex items-center space-x-2">
              <div className="flex-1 relative">
                <Input
                  type="number"
                  placeholder="Enter new commission percentage"
                  value={newCommission}
                  onChange={(e) => setNewCommission(e.target.value)}
                  min="0"
                  max="100"
                  step="0.1"
                  className="pr-8"
                />
                <Percent className="absolute right-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
              </div>
              <Button
                onClick={handleUpdateCommission}
                disabled={updateCommissionMutation.isPending || !newCommission}
                className="flex items-center space-x-2"
              >
                {updateCommissionMutation.isPending ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                    <span>Updating...</span>
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4" />
                    <span>Update</span>
                  </>
                )}
              </Button>
            </div>
            <p className="text-xs text-gray-500">
              Setting platform commission to {newCommission || '0'}% means vendors will receive {newCommission ? (100 - parseFloat(newCommission || '0')).toFixed(1) : '100'}%
            </p>
          </div>
        </div>

        {/* Commission Impact Information */}
        <div className="bg-gray-50 border border-gray-200 rounded-lg p-4">
          <h4 className="text-sm font-medium text-gray-900 mb-2">Commission Impact</h4>
          <div className="space-y-1 text-xs text-gray-600">
            <p>â€¢ Platform commission applies to all completed orders</p>
            <p>â€¢ Vendor earnings are calculated automatically based on these settings</p>
            <p>â€¢ Changes apply to future orders and earnings calculations</p>
            <p>â€¢ Existing payout requests are not affected by commission changes</p>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

