import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { formatCurrency } from "@/lib/utils";
import { CheckCircle, XCircle, Clock, AlertCircle, DollarSign, TrendingUp, Users, CreditCard } from "lucide-react";

interface PayoutRequest {
  id: string;
  vendorId: string;
  businessName: string;
  contactEmail: string;
  amount: string;
  status: string;
  bankAccountDetails: string;
  requestDate: string;
  processedDate?: string;
  paymentReference?: string;
  failureReason?: string;
}

const statusColors = {
  pending: "bg-yellow-100 text-yellow-800",
  processing: "bg-blue-100 text-blue-800",
  completed: "bg-green-100 text-green-800",
  rejected: "bg-red-100 text-red-800",
  failed: "bg-red-100 text-red-800"
};

const statusIcons = {
  pending: Clock,
  processing: TrendingUp,
  completed: CheckCircle,
  rejected: XCircle,
  failed: AlertCircle
};

function ProcessPayoutDialog({ 
  payoutRequest, 
  isOpen, 
  onClose 
}: { 
  payoutRequest: PayoutRequest | null;
  isOpen: boolean;
  onClose: () => void;
}) {
  const [action, setAction] = useState<'approve' | 'reject'>('approve');
  const [paymentReference, setPaymentReference] = useState('');
  const [notes, setNotes] = useState('');
  const { toast } = useToast();

  const processMutation = useMutation({
    mutationFn: async ({ requestId, action, paymentReference, notes }: {
      requestId: string;
      action: 'approve' | 'reject';
      paymentReference?: string;
      notes?: string;
    }) => {
      return await apiRequest('POST', `/api/admin/payout-requests/${requestId}/process`, {
        action,
        paymentReference,
        notes
      });
    },
    onSuccess: () => {
      toast({
        title: "Payout Processed",
        description: `Payout request has been ${action}d successfully.`
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/payout-requests'] });
      onClose();
      resetForm();
    },
    onError: (error) => {
      toast({
        title: "Processing Failed",
        description: `Failed to ${action} payout request: ${error.message}`,
        variant: "destructive"
      });
    }
  });

  const resetForm = () => {
    setAction('approve');
    setPaymentReference('');
    setNotes('');
  };

  const handleSubmit = () => {
    if (!payoutRequest) return;

    if (action === 'approve' && !paymentReference.trim()) {
      toast({
        title: "Payment Reference Required",
        description: "Please provide a payment reference for approval.",
        variant: "destructive"
      });
      return;
    }

    processMutation.mutate({
      requestId: payoutRequest.id,
      action,
      paymentReference: paymentReference.trim() || undefined,
      notes: notes.trim() || undefined
    });
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Process Payout Request</DialogTitle>
          <DialogDescription>
            {payoutRequest && (
              <>
                Vendor: {payoutRequest.businessName}<br />
                Amount: {formatCurrency(parseFloat(payoutRequest.amount))}
              </>
            )}
          </DialogDescription>
        </DialogHeader>

        <div className="grid gap-4 py-4">
          <div className="space-y-2">
            <Label htmlFor="action">Action</Label>
            <Select value={action} onValueChange={(value: 'approve' | 'reject') => setAction(value)}>
              <SelectTrigger>
                <SelectValue placeholder="Select action" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="approve">Approve</SelectItem>
                <SelectItem value="reject">Reject</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {action === 'approve' && (
            <div className="space-y-2">
              <Label htmlFor="paymentReference">Payment Reference *</Label>
              <Input
                id="paymentReference"
                value={paymentReference}
                onChange={(e) => setPaymentReference(e.target.value)}
                placeholder="Enter payment reference number"
                required
              />
            </div>
          )}

          <div className="space-y-2">
            <Label htmlFor="notes">Notes (Optional)</Label>
            <Textarea
              id="notes"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              placeholder="Add any additional notes..."
              rows={3}
            />
          </div>

          {payoutRequest && (
            <div className="rounded-lg bg-gray-50 p-3">
              <p className="text-sm font-medium">Bank Details:</p>
              <p className="text-sm text-gray-600">{payoutRequest.bankAccountDetails}</p>
            </div>
          )}
        </div>

        <DialogFooter>
          <Button variant="outline" onClick={onClose}>
            Cancel
          </Button>
          <Button 
            onClick={handleSubmit}
            disabled={processMutation.isPending}
            className={action === 'reject' ? 'bg-red-600 hover:bg-red-700' : ''}
          >
            {processMutation.isPending ? 'Processing...' : `${action === 'approve' ? 'Approve' : 'Reject'} Payout`}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

export default function PayoutManagement() {
  const [selectedStatus, setSelectedStatus] = useState<string>('all');
  const [selectedPayout, setSelectedPayout] = useState<PayoutRequest | null>(null);
  const [isProcessDialogOpen, setIsProcessDialogOpen] = useState(false);
  const { toast } = useToast();

  const { data: payoutRequests = [], isLoading } = useQuery({
    queryKey: ['/api/admin/payout-requests', selectedStatus],
    queryFn: async () => {
      const params = selectedStatus !== 'all' ? `?status=${selectedStatus}` : '';
      const response = await apiRequest('GET', `/api/admin/payout-requests${params}`);
      return response.json();
    }
  });

  const completeMutation = useMutation({
    mutationFn: async ({ requestId, paymentReference }: {
      requestId: string;
      paymentReference: string;
    }) => {
      return await apiRequest('POST', `/api/admin/payout-requests/${requestId}/complete`, {
        paymentReference
      });
    },
    onSuccess: () => {
      toast({
        title: "Payout Completed",
        description: "Payout request marked as completed successfully."
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/payout-requests'] });
    },
    onError: (error) => {
      toast({
        title: "Completion Failed",
        description: `Failed to complete payout: ${error.message}`,
        variant: "destructive"
      });
    }
  });

  const handleProcessPayout = (payoutRequest: PayoutRequest) => {
    setSelectedPayout(payoutRequest);
    setIsProcessDialogOpen(true);
  };

  const getStatusSummary = () => {
    const summary = payoutRequests.reduce((acc: any, request: PayoutRequest) => {
      acc[request.status] = (acc[request.status] || 0) + 1;
      acc.totalAmount = (acc.totalAmount || 0) + parseFloat(request.amount);
      return acc;
    }, {});

    return {
      pending: summary.pending || 0,
      processing: summary.processing || 0,
      completed: summary.completed || 0,
      rejected: summary.rejected || 0,
      total: payoutRequests.length,
      totalAmount: summary.totalAmount || 0
    };
  };

  const statusSummary = getStatusSummary();

  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">Vendor Payout Management</h2>
          <p className="text-gray-600">Manage vendor disbursement requests and earnings</p>
        </div>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="flex items-center p-6">
            <Clock className="h-8 w-8 text-yellow-600" />
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Pending</p>
              <div className="text-2xl font-bold">{statusSummary.pending}</div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="flex items-center p-6">
            <TrendingUp className="h-8 w-8 text-blue-600" />
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Processing</p>
              <div className="text-2xl font-bold">{statusSummary.processing}</div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="flex items-center p-6">
            <CheckCircle className="h-8 w-8 text-green-600" />
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Completed</p>
              <div className="text-2xl font-bold">{statusSummary.completed}</div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="flex items-center p-6">
            <DollarSign className="h-8 w-8 text-purple-600" />
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Total Amount</p>
              <div className="text-2xl font-bold">{formatCurrency(statusSummary.totalAmount)}</div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filter Tabs */}
      <Tabs value={selectedStatus} onValueChange={setSelectedStatus}>
        <TabsList>
          <TabsTrigger value="all">All ({statusSummary.total})</TabsTrigger>
          <TabsTrigger value="pending">Pending ({statusSummary.pending})</TabsTrigger>
          <TabsTrigger value="processing">Processing ({statusSummary.processing})</TabsTrigger>
          <TabsTrigger value="completed">Completed ({statusSummary.completed})</TabsTrigger>
          <TabsTrigger value="rejected">Rejected ({statusSummary.rejected})</TabsTrigger>
        </TabsList>

        <TabsContent value={selectedStatus} className="mt-6">
          <Card>
            <CardHeader>
              <CardTitle>Payout Requests</CardTitle>
              <CardDescription>
                {selectedStatus === 'all' 
                  ? 'All payout requests from vendors'
                  : `${selectedStatus.charAt(0).toUpperCase() + selectedStatus.slice(1)} payout requests`
                }
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Vendor</TableHead>
                    <TableHead>Amount</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Request Date</TableHead>
                    <TableHead>Bank Details</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {payoutRequests.map((request: PayoutRequest) => {
                    const StatusIcon = statusIcons[request.status as keyof typeof statusIcons];
                    return (
                      <TableRow key={request.id}>
                        <TableCell>
                          <div>
                            <div className="font-medium">{request.businessName}</div>
                            <div className="text-sm text-gray-500">{request.contactEmail}</div>
                          </div>
                        </TableCell>
                        <TableCell className="font-medium">
                          {formatCurrency(parseFloat(request.amount))}
                        </TableCell>
                        <TableCell>
                          <Badge className={`${statusColors[request.status as keyof typeof statusColors]} flex items-center gap-1 w-fit`}>
                            <StatusIcon className="h-3 w-3" />
                            {request.status.charAt(0).toUpperCase() + request.status.slice(1)}
                          </Badge>
                        </TableCell>
                        <TableCell>
                          {new Date(request.requestDate).toLocaleDateString()}
                        </TableCell>
                        <TableCell>
                          <div className="max-w-xs truncate" title={request.bankAccountDetails}>
                            {request.bankAccountDetails}
                          </div>
                        </TableCell>
                        <TableCell>
                          <div className="flex gap-2">
                            {request.status === 'pending' && (
                              <Button
                                size="sm"
                                onClick={() => handleProcessPayout(request)}
                              >
                                Process
                              </Button>
                            )}
                            {request.status === 'processing' && (
                              <Button
                                size="sm"
                                variant="outline"
                                onClick={() => {
                                  const ref = prompt('Enter payment reference to complete:');
                                  if (ref?.trim()) {
                                    completeMutation.mutate({
                                      requestId: request.id,
                                      paymentReference: ref.trim()
                                    });
                                  }
                                }}
                              >
                                Complete
                              </Button>
                            )}
                          </div>
                        </TableCell>
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>

              {payoutRequests.length === 0 && (
                <div className="text-center py-8">
                  <CreditCard className="mx-auto h-12 w-12 text-gray-400" />
                  <h3 className="mt-2 text-sm font-medium text-gray-900">No payout requests</h3>
                  <p className="mt-1 text-sm text-gray-500">
                    {selectedStatus === 'all' 
                      ? 'No payout requests have been submitted yet.'
                      : `No ${selectedStatus} payout requests found.`
                    }
                  </p>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      <ProcessPayoutDialog
        payoutRequest={selectedPayout}
        isOpen={isProcessDialogOpen}
        onClose={() => {
          setIsProcessDialogOpen(false);
          setSelectedPayout(null);
        }}
      />
    </div>
  );
}

client/src/pages/admin-portal/components.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface AddUserModalProps {
  isOpen: boolean;
  onClose: () => void;
}

interface UserFormData {
  email: string;
  firstName: string;
  lastName: string;
  password: string;
}

export function AddUserModal({ isOpen, onClose }: AddUserModalProps) {
  const [formData, setFormData] = useState<UserFormData>({
    email: "",
    firstName: "",
    lastName: "",
    password: "",
  });
  const [errors, setErrors] = useState<Partial<UserFormData>>({});

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const createUserMutation = useMutation({
    mutationFn: async (userData: UserFormData) => {
      const response = await apiRequest("POST", "/api/admin/users", userData);
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to create user");
      }
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "User created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
      setFormData({ email: "", firstName: "", lastName: "", password: "" });
      setErrors({});
      onClose();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const validateForm = () => {
    const newErrors: Partial<UserFormData> = {};

    if (!formData.email.trim()) {
      newErrors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = "Invalid email format";
    }

    if (!formData.firstName.trim()) {
      newErrors.firstName = "First name is required";
    }

    if (!formData.lastName.trim()) {
      newErrors.lastName = "Last name is required";
    }

    if (!formData.password.trim()) {
      newErrors.password = "Password is required";
    } else if (formData.password.length < 6) {
      newErrors.password = "Password must be at least 6 characters";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (validateForm()) {
      createUserMutation.mutate(formData);
    }
  };

  const handleInputChange = (field: keyof UserFormData, value: string) => {
    setFormData({ ...formData, [field]: value });
    if (errors[field]) {
      setErrors({ ...errors, [field]: undefined });
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle>Add New User</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="firstName">First Name</Label>
              <Input
                id="firstName"
                type="text"
                value={formData.firstName}
                onChange={(e) => handleInputChange("firstName", e.target.value)}
                className={errors.firstName ? "border-red-500" : ""}
              />
              {errors.firstName && (
                <p className="text-sm text-red-500">{errors.firstName}</p>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="lastName">Last Name</Label>
              <Input
                id="lastName"
                type="text"
                value={formData.lastName}
                onChange={(e) => handleInputChange("lastName", e.target.value)}
                className={errors.lastName ? "border-red-500" : ""}
              />
              {errors.lastName && (
                <p className="text-sm text-red-500">{errors.lastName}</p>
              )}
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="email">Email</Label>
            <Input
              id="email"
              type="email"
              value={formData.email}
              onChange={(e) => handleInputChange("email", e.target.value)}
              className={errors.email ? "border-red-500" : ""}
            />
            {errors.email && (
              <p className="text-sm text-red-500">{errors.email}</p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor="password">Password</Label>
            <Input
              id="password"
              type="password"
              value={formData.password}
              onChange={(e) => handleInputChange("password", e.target.value)}
              className={errors.password ? "border-red-500" : ""}
            />
            {errors.password && (
              <p className="text-sm text-red-500">{errors.password}</p>
            )}
          </div>

          <div className="flex justify-end space-x-2 pt-4">
            <Button type="button" variant="outline" onClick={onClose}>
              Cancel
            </Button>
            <Button 
              type="submit" 
              disabled={createUserMutation.isPending}
            >
              {createUserMutation.isPending ? "Creating..." : "Create User"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface AddVendorModalProps {
  isOpen: boolean;
  onClose: () => void;
}

interface VendorFormData {
  email: string;
  password: string;
  businessName: string;
  contactName: string;
  contactPhone: string;
  businessAddress: string;
  businessDescription: string;
  registrationType: "registered" | "non-registered";
  nationalIdNumber?: string;
  taxPinNumber?: string;
}

export function AddVendorModal({ isOpen, onClose }: AddVendorModalProps) {
  const [formData, setFormData] = useState<VendorFormData>({
    email: "",
    password: "",
    businessName: "",
    contactName: "",
    contactPhone: "",
    businessAddress: "",
    businessDescription: "",
    registrationType: "non-registered",
    nationalIdNumber: "",
    taxPinNumber: "",
  });
  const [errors, setErrors] = useState<Partial<VendorFormData>>({});

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const createVendorMutation = useMutation({
    mutationFn: async (vendorData: VendorFormData) => {
      const response = await apiRequest("POST", "/api/admin/vendors", vendorData);
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to create vendor");
      }
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Vendor created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/vendors"] });
      resetForm();
      onClose();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      email: "",
      password: "",
      businessName: "",
      contactName: "",
      contactPhone: "",
      businessAddress: "",
      businessDescription: "",
      registrationType: "non-registered",
      nationalIdNumber: "",
      taxPinNumber: "",
    });
    setErrors({});
  };

  const validateForm = () => {
    const newErrors: Partial<VendorFormData> = {};

    if (!formData.email.trim()) {
      newErrors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = "Invalid email format";
    }

    if (!formData.password.trim()) {
      newErrors.password = "Password is required";
    } else if (formData.password.length < 6) {
      newErrors.password = "Password must be at least 6 characters";
    }

    if (!formData.businessName.trim()) {
      newErrors.businessName = "Business name is required";
    }

    if (!formData.contactName.trim()) {
      newErrors.contactName = "Contact name is required";
    }

    if (!formData.contactPhone.trim()) {
      newErrors.contactPhone = "Contact phone is required";
    }

    if (!formData.businessAddress.trim()) {
      newErrors.businessAddress = "Business address is required";
    }

    if (!formData.businessDescription.trim()) {
      newErrors.businessDescription = "Business description is required";
    }

    if (!formData.nationalIdNumber?.trim()) {
      newErrors.nationalIdNumber = "National ID number is required";
    }

    if (formData.registrationType === "registered" && !formData.taxPinNumber?.trim()) {
      newErrors.taxPinNumber = "Tax PIN number is required for registered businesses";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (validateForm()) {
      createVendorMutation.mutate(formData);
    }
  };

  const handleInputChange = (field: keyof VendorFormData, value: string) => {
    setFormData({ ...formData, [field]: value });
    if (errors[field]) {
      setErrors({ ...errors, [field]: undefined });
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[600px] max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Add New Vendor</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="email">Email *</Label>
              <Input
                id="email"
                type="email"
                value={formData.email}
                onChange={(e) => handleInputChange("email", e.target.value)}
                className={errors.email ? "border-red-500" : ""}
              />
              {errors.email && (
                <p className="text-sm text-red-500">{errors.email}</p>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="password">Password *</Label>
              <Input
                id="password"
                type="password"
                value={formData.password}
                onChange={(e) => handleInputChange("password", e.target.value)}
                className={errors.password ? "border-red-500" : ""}
              />
              {errors.password && (
                <p className="text-sm text-red-500">{errors.password}</p>
              )}
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="businessName">Business Name *</Label>
            <Input
              id="businessName"
              type="text"
              value={formData.businessName}
              onChange={(e) => handleInputChange("businessName", e.target.value)}
              className={errors.businessName ? "border-red-500" : ""}
            />
            {errors.businessName && (
              <p className="text-sm text-red-500">{errors.businessName}</p>
            )}
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="contactName">Contact Name *</Label>
              <Input
                id="contactName"
                type="text"
                value={formData.contactName}
                onChange={(e) => handleInputChange("contactName", e.target.value)}
                className={errors.contactName ? "border-red-500" : ""}
              />
              {errors.contactName && (
                <p className="text-sm text-red-500">{errors.contactName}</p>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="contactPhone">Contact Phone *</Label>
              <Input
                id="contactPhone"
                type="tel"
                value={formData.contactPhone}
                onChange={(e) => handleInputChange("contactPhone", e.target.value)}
                className={errors.contactPhone ? "border-red-500" : ""}
              />
              {errors.contactPhone && (
                <p className="text-sm text-red-500">{errors.contactPhone}</p>
              )}
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="businessAddress">Business Address *</Label>
            <Input
              id="businessAddress"
              type="text"
              value={formData.businessAddress}
              onChange={(e) => handleInputChange("businessAddress", e.target.value)}
              className={errors.businessAddress ? "border-red-500" : ""}
            />
            {errors.businessAddress && (
              <p className="text-sm text-red-500">{errors.businessAddress}</p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor="businessDescription">Business Description *</Label>
            <Textarea
              id="businessDescription"
              value={formData.businessDescription}
              onChange={(e) => handleInputChange("businessDescription", e.target.value)}
              className={errors.businessDescription ? "border-red-500" : ""}
              rows={3}
            />
            {errors.businessDescription && (
              <p className="text-sm text-red-500">{errors.businessDescription}</p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor="registrationType">Registration Type *</Label>
            <Select
              value={formData.registrationType}
              onValueChange={(value: "registered" | "non-registered") => handleInputChange("registrationType", value)}
            >
              <SelectTrigger>
                <SelectValue placeholder="Select registration type" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="non-registered">Non-Registered Business</SelectItem>
                <SelectItem value="registered">Registered Business</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
            <Label htmlFor="nationalIdNumber">National ID Number *</Label>
            <Input
              id="nationalIdNumber"
              type="text"
              value={formData.nationalIdNumber}
              onChange={(e) => handleInputChange("nationalIdNumber", e.target.value)}
              className={errors.nationalIdNumber ? "border-red-500" : ""}
            />
            {errors.nationalIdNumber && (
              <p className="text-sm text-red-500">{errors.nationalIdNumber}</p>
            )}
          </div>

          {formData.registrationType === "registered" && (
            <div className="space-y-2">
              <Label htmlFor="taxPinNumber">Tax PIN Number *</Label>
              <Input
                id="taxPinNumber"
                type="text"
                value={formData.taxPinNumber}
                onChange={(e) => handleInputChange("taxPinNumber", e.target.value)}
                className={errors.taxPinNumber ? "border-red-500" : ""}
              />
              {errors.taxPinNumber && (
                <p className="text-sm text-red-500">{errors.taxPinNumber}</p>
              )}
            </div>
          )}

          <div className="flex justify-end space-x-2 pt-4">
            <Button type="button" variant="outline" onClick={onClose}>
              Cancel
            </Button>
            <Button 
              type="submit" 
              disabled={createVendorMutation.isPending}
            >
              {createVendorMutation.isPending ? "Creating..." : "Create Vendor"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Textarea } from "@/components/ui/textarea";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { 
  Plus,
  Search,
  Edit,
  Trash2,
  Tag,
  Folder,
  Package,
  Settings,
  Users,
  BarChart3,
  RefreshCw,
  FolderOpen,
  Archive
} from "lucide-react";

interface Category {
  id: string;
  name: string;
  slug: string;
  description?: string;
  imageUrl?: string;
  isActive: boolean;
  createdAt: string;
  productCount?: number;
  serviceCount?: number;
}

interface Subcategory {
  id: string;
  categoryId: string;
  name: string;
  slug: string;
  description?: string;
  imageUrl?: string;
  isActive: boolean;
  createdAt: string;
  productCount?: number;
}

interface Product {
  id: string;
  name: string;
  categoryId: string;
  subcategoryId?: string;
}

interface Service {
  id: string;
  name: string;
  categoryId: string;
}

const categorySchema = z.object({
  name: z.string().min(1, "Category name is required"),
  description: z.string().optional(),
  imageUrl: z.string().url().optional().or(z.literal(""))
});

const subcategorySchema = z.object({
  name: z.string().min(1, "Subcategory name is required"),
  categoryId: z.string().min(1, "Parent category is required"),
  description: z.string().optional(),
  imageUrl: z.string().url().optional().or(z.literal(""))
});

export default function CategoriesManagement() {
  const [searchTerm, setSearchTerm] = useState("");
  const [activeTab, setActiveTab] = useState("categories");
  const [showAddCategory, setShowAddCategory] = useState(false);
  const [showAddSubcategory, setShowAddSubcategory] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState<Category | null>(null);
  const [editingCategory, setEditingCategory] = useState<Category | null>(null);
  const [editingSubcategory, setEditingSubcategory] = useState<Subcategory | null>(null);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch categories
  const { data: categories = [], isLoading: categoriesLoading } = useQuery({
    queryKey: ['/api/categories'],
    retry: false,
  });

  // Fetch subcategories
  const { data: subcategories = [], isLoading: subcategoriesLoading } = useQuery({
    queryKey: ['/api/subcategories'],
    retry: false,
  });

  // Fetch products to get category usage statistics
  const { data: products = [] } = useQuery({
    queryKey: ['/api/products?limit=1000'],
    retry: false,
  });

  // Fetch services to get category usage statistics
  const { data: services = [] } = useQuery({
    queryKey: ['/api/services?limit=1000'],
    retry: false,
  });

  // Calculate category statistics
  const categoriesWithStats = (categories as Category[]).map(category => {
    const productCount = (products as Product[]).filter(p => p.categoryId === category.id).length;
    const serviceCount = (services as Service[]).filter(s => s.categoryId === category.id).length;
    return { ...category, productCount, serviceCount };
  });

  // Calculate subcategory statistics
  const subcategoriesWithStats = (subcategories as Subcategory[]).map(subcategory => {
    const productCount = (products as Product[]).filter(p => p.subcategoryId === subcategory.id).length;
    return { ...subcategory, productCount };
  });

  // Form for adding/editing categories
  const categoryForm = useForm({
    resolver: zodResolver(categorySchema),
    defaultValues: {
      name: "",
      description: "",
      imageUrl: ""
    }
  });

  // Form for adding/editing subcategories
  const subcategoryForm = useForm({
    resolver: zodResolver(subcategorySchema),
    defaultValues: {
      name: "",
      categoryId: "",
      description: "",
      imageUrl: ""
    }
  });

  // Create category mutation
  const createCategoryMutation = useMutation({
    mutationFn: async (data: z.infer<typeof categorySchema>) => {
      return await apiRequest('/api/admin/categories', 'POST', data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/categories'] });
      setShowAddCategory(false);
      categoryForm.reset();
      toast({
        title: "Success",
        description: "Category created successfully"
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive"
      });
    }
  });

  // Create subcategory mutation
  const createSubcategoryMutation = useMutation({
    mutationFn: async (data: z.infer<typeof subcategorySchema>) => {
      return await apiRequest('/api/admin/subcategories', 'POST', data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/subcategories'] });
      setShowAddSubcategory(false);
      subcategoryForm.reset();
      toast({
        title: "Success",
        description: "Subcategory created successfully"
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive"
      });
    }
  });

  // Update category mutation
  const updateCategoryMutation = useMutation({
    mutationFn: async (data: { id: string } & z.infer<typeof categorySchema>) => {
      const { id, ...updateData } = data;
      return await apiRequest(`/api/admin/categories/${id}`, 'PUT', updateData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/categories'] });
      setEditingCategory(null);
      categoryForm.reset();
      toast({
        title: "Success",
        description: "Category updated successfully"
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive"
      });
    }
  });

  // Delete category mutation
  const deleteCategoryMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest(`/api/admin/categories/${id}`, 'DELETE');
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/categories'] });
      toast({
        title: "Success",
        description: "Category deleted successfully"
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive"
      });
    }
  });

  const filteredCategories = categoriesWithStats.filter(category =>
    category.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    category.description?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const filteredSubcategories = subcategoriesWithStats.filter(subcategory =>
    subcategory.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    subcategory.description?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleEditCategory = (category: Category) => {
    setEditingCategory(category);
    categoryForm.setValue('name', category.name);
    categoryForm.setValue('description', category.description || '');
    categoryForm.setValue('imageUrl', category.imageUrl || '');
  };

  const handleEditSubcategory = (subcategory: Subcategory) => {
    setEditingSubcategory(subcategory);
    subcategoryForm.setValue('name', subcategory.name);
    subcategoryForm.setValue('categoryId', subcategory.categoryId);
    subcategoryForm.setValue('description', subcategory.description || '');
    subcategoryForm.setValue('imageUrl', subcategory.imageUrl || '');
  };

  const onCategorySubmit = (data: z.infer<typeof categorySchema>) => {
    if (editingCategory) {
      updateCategoryMutation.mutate({ id: editingCategory.id, ...data });
    } else {
      createCategoryMutation.mutate(data);
    }
  };

  const onSubcategorySubmit = (data: z.infer<typeof subcategorySchema>) => {
    createSubcategoryMutation.mutate(data);
  };

  const getCategoryStats = () => {
    const totalCategories = categoriesWithStats.length;
    const activeCategories = categoriesWithStats.filter(c => c.isActive).length;
    const totalProducts = categoriesWithStats.reduce((sum, c) => sum + (c.productCount || 0), 0);
    const totalServices = categoriesWithStats.reduce((sum, c) => sum + (c.serviceCount || 0), 0);
    const totalSubcategories = subcategoriesWithStats.length;
    const usedCategories = categoriesWithStats.filter(c => (c.productCount || 0) > 0 || (c.serviceCount || 0) > 0).length;

    return { totalCategories, activeCategories, totalProducts, totalServices, totalSubcategories, usedCategories };
  };

  const stats = getCategoryStats();

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h3 className="text-xl font-semibold text-gray-900">Categories Management</h3>
          <p className="text-gray-600">Manage product and service categories and subcategories</p>
        </div>
        <Button variant="ghost" size="sm" onClick={() => window.location.reload()}>
          <RefreshCw className="w-4 h-4 mr-2" />
          Refresh
        </Button>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-6 gap-4">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-blue-100 p-3 rounded-full">
                <Folder className="w-6 h-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Categories</p>
                <p className="text-2xl font-bold text-gray-900">{stats.totalCategories}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-green-100 p-3 rounded-full">
                <FolderOpen className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Active</p>
                <p className="text-2xl font-bold text-gray-900">{stats.activeCategories}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-purple-100 p-3 rounded-full">
                <Package className="w-6 h-6 text-purple-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Products</p>
                <p className="text-2xl font-bold text-gray-900">{stats.totalProducts}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-orange-100 p-3 rounded-full">
                <Users className="w-6 h-6 text-orange-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Services</p>
                <p className="text-2xl font-bold text-gray-900">{stats.totalServices}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-yellow-100 p-3 rounded-full">
                <Tag className="w-6 h-6 text-yellow-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Subcategories</p>
                <p className="text-2xl font-bold text-gray-900">{stats.totalSubcategories}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-indigo-100 p-3 rounded-full">
                <BarChart3 className="w-6 h-6 text-indigo-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Used Categories</p>
                <p className="text-2xl font-bold text-gray-900">{stats.usedCategories}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Search and Actions */}
      <Card>
        <CardContent className="p-6">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                <Input 
                  placeholder="Search categories or subcategories..." 
                  className="pl-10" 
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            </div>
            <div className="flex gap-2">
              <Dialog open={showAddCategory} onOpenChange={setShowAddCategory}>
                <DialogTrigger asChild>
                  <Button>
                    <Plus className="w-4 h-4 mr-2" />
                    Add Category
                  </Button>
                </DialogTrigger>
                <DialogContent className="max-w-md">
                  <DialogHeader>
                    <DialogTitle>
                      {editingCategory ? 'Edit Category' : 'Add New Category'}
                    </DialogTitle>
                  </DialogHeader>
                  <Form {...categoryForm}>
                    <form onSubmit={categoryForm.handleSubmit(onCategorySubmit)} className="space-y-4">
                      <FormField
                        control={categoryForm.control}
                        name="name"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Category Name</FormLabel>
                            <FormControl>
                              <Input placeholder="Enter category name" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={categoryForm.control}
                        name="description"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Description (Optional)</FormLabel>
                            <FormControl>
                              <Textarea placeholder="Enter category description" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={categoryForm.control}
                        name="imageUrl"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Image URL (Optional)</FormLabel>
                            <FormControl>
                              <Input placeholder="Enter image URL" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <div className="flex justify-end space-x-2">
                        <Button 
                          type="button" 
                          variant="outline" 
                          onClick={() => {
                            setShowAddCategory(false);
                            setEditingCategory(null);
                            categoryForm.reset();
                          }}
                        >
                          Cancel
                        </Button>
                        <Button 
                          type="submit" 
                          disabled={createCategoryMutation.isPending || updateCategoryMutation.isPending}
                        >
