  status: string;
  description: string;
  location?: string;
  timestamp: string;
  source: string;
}

interface DeliveryAnalytics {
  summary: {
    totalDeliveries: number;
    successfulDeliveries: number;
    failedDeliveries: number;
    pendingDeliveries: number;
    successRate: string;
    averageDeliveryTime: number;
  };
  courierPerformance: Record<string, {
    total: number;
    delivered: number;
    failed: number;
    pending: number;
  }>;
}

export default function DeliveryManagement() {
  const [deliveries, setDeliveries] = useState<Delivery[]>([]);
  const [analytics, setAnalytics] = useState<DeliveryAnalytics | null>(null);
  const [loading, setLoading] = useState(true);
  const [selectedDelivery, setSelectedDelivery] = useState<Delivery | null>(null);
  const [filters, setFilters] = useState({
    status: 'all',
    courier: 'all',
    search: '',
  });
  const [reassignData, setReassignData] = useState({
    newProviderId: '',
    reason: '',
  });
  const { toast } = useToast();

  useEffect(() => {
    fetchDeliveries();
    fetchAnalytics();
  }, [filters]);

  const fetchDeliveries = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      if (filters.status !== 'all') params.append('status', filters.status);
      if (filters.courier !== 'all') params.append('providerId', filters.courier);

      const response = await fetch(`/api/deliveries?${params}`);
      if (response.ok) {
        const data = await response.json();
        setDeliveries(data);
      }
    } catch (error) {
      console.error('Error fetching deliveries:', error);
      toast({
        title: "Error",
        description: "Failed to fetch deliveries",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const fetchAnalytics = async () => {
    try {
      const response = await fetch('/api/delivery/analytics');
      if (response.ok) {
        const data = await response.json();
        setAnalytics(data);
      }
    } catch (error) {
      console.error('Error fetching analytics:', error);
    }
  };

  const getStatusBadge = (status: string) => {
    const statusMap = {
      pending: { variant: 'secondary' as const, icon: Clock, label: 'Pending' },
      pickup_scheduled: { variant: 'default' as const, icon: Calendar, label: 'Pickup Scheduled' },
      picked_up: { variant: 'default' as const, icon: Package, label: 'Picked Up' },
      in_transit: { variant: 'default' as const, icon: Truck, label: 'In Transit' },
      out_for_delivery: { variant: 'default' as const, icon: Truck, label: 'Out for Delivery' },
      delivered: { variant: 'default' as const, icon: CheckCircle, label: 'Delivered' },
      failed: { variant: 'destructive' as const, icon: XCircle, label: 'Failed' },
      cancelled: { variant: 'destructive' as const, icon: XCircle, label: 'Cancelled' },
    };

    const config = statusMap[status as keyof typeof statusMap] || statusMap.pending;
    const Icon = config.icon;

    return (
      <Badge variant={config.variant} className="flex items-center gap-1">
        <Icon className="w-3 h-3" />
        {config.label}
      </Badge>
    );
  };

  const formatPrice = (price: string | number) => {
    const amount = typeof price === 'string' ? parseFloat(price) : price;
    return new Intl.NumberFormat('en-KE', {
      style: 'currency',
      currency: 'KES',
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-KE', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const handleReassignDelivery = async (deliveryId: string) => {
    try {
      const response = await fetch(`/api/deliveries/${deliveryId}/reassign`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(reassignData),
      });

      if (response.ok) {
        toast({
          title: "Success",
          description: "Delivery reassigned successfully",
        });
        fetchDeliveries();
        setSelectedDelivery(null);
        setReassignData({ newProviderId: '', reason: '' });
      } else {
        throw new Error('Failed to reassign delivery');
      }
    } catch (error) {
      console.error('Error reassigning delivery:', error);
      toast({
        title: "Error",
        description: "Failed to reassign delivery",
        variant: "destructive",
      });
    }
  };

  const filteredDeliveries = deliveries.filter(delivery => {
    const matchesSearch = delivery.orderId.toLowerCase().includes(filters.search.toLowerCase()) ||
                         delivery.deliveryAddress.toLowerCase().includes(filters.search.toLowerCase());
    return matchesSearch;
  });

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-[#FF4605]"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Analytics Cards */}
      {analytics && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="bg-blue-100 p-3 rounded-full">
                  <Package className="w-6 h-6 text-blue-600" />
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Total Deliveries</p>
                  <p className="text-2xl font-bold text-gray-900">{analytics.summary.totalDeliveries}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="bg-green-100 p-3 rounded-full">
                  <CheckCircle className="w-6 h-6 text-green-600" />
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Success Rate</p>
                  <p className="text-2xl font-bold text-gray-900">{analytics.summary.successRate}%</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="bg-yellow-100 p-3 rounded-full">
                  <Clock className="w-6 h-6 text-yellow-600" />
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Pending</p>
                  <p className="text-2xl font-bold text-gray-900">{analytics.summary.pendingDeliveries}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="bg-orange-100 p-3 rounded-full">
                  <TrendingUp className="w-6 h-6 text-orange-600" />
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Avg. Time</p>
                  <p className="text-2xl font-bold text-gray-900">{analytics.summary.averageDeliveryTime}m</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Filter className="w-5 h-5 mr-2" />
            Delivery Filters
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <Label htmlFor="search">Search</Label>
              <Input
                id="search"
                placeholder="Search by order ID or address..."
                value={filters.search}
                onChange={(e) => setFilters({ ...filters, search: e.target.value })}
                className="mt-1"
              />
            </div>
            <div>
              <Label htmlFor="status">Status</Label>
              <Select value={filters.status} onValueChange={(value) => setFilters({ ...filters, status: value })}>
                <SelectTrigger className="mt-1">
                  <SelectValue placeholder="Select status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Statuses</SelectItem>
                  <SelectItem value="pending">Pending</SelectItem>
                  <SelectItem value="picked_up">Picked Up</SelectItem>
                  <SelectItem value="in_transit">In Transit</SelectItem>
                  <SelectItem value="delivered">Delivered</SelectItem>
                  <SelectItem value="failed">Failed</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="courier">Courier</Label>
              <Select value={filters.courier} onValueChange={(value) => setFilters({ ...filters, courier: value })}>
                <SelectTrigger className="mt-1">
                  <SelectValue placeholder="Select courier" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Couriers</SelectItem>
                  <SelectItem value="g4s">G4S Courier</SelectItem>
                  <SelectItem value="fargo_courier">Fargo Courier</SelectItem>
                  <SelectItem value="pickup-mtaani">Pickup Mtaani</SelectItem>
                  <SelectItem value="speed-af">Speed AF</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Deliveries List */}
      <Card>
        <CardHeader>
          <CardTitle>All Deliveries</CardTitle>
          <CardDescription>
            Manage and track all delivery operations across the platform
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {filteredDeliveries.map((delivery) => (
              <div key={delivery.id} className="border rounded-lg p-4">
                <div className="flex items-start justify-between">
                  <div className="space-y-2">
                    <div className="flex items-center space-x-4">
                      <h3 className="font-semibold">Order #{delivery.orderId.slice(0, 8)}</h3>
                      {getStatusBadge(delivery.status)}
                      <Badge variant="outline">{delivery.providerId}</Badge>
                    </div>
                    <div className="flex items-center space-x-4 text-sm text-gray-600">
                      <div className="flex items-center">
                        <MapPin className="w-4 h-4 mr-1" />
                        {delivery.deliveryAddress}
                      </div>
                      {delivery.customerPhone && (
                        <div className="flex items-center">
                          <Phone className="w-4 h-4 mr-1" />
                          {delivery.customerPhone}
                        </div>
                      )}
                      <div className="flex items-center">
                        <Calendar className="w-4 h-4 mr-1" />
                        {formatDate(delivery.createdAt)}
                      </div>
                    </div>
                    <p className="text-sm text-gray-600">{delivery.packageDescription}</p>
                    {delivery.failureReason && (
                      <div className="flex items-center text-red-600 text-sm">
                        <AlertTriangle className="w-4 h-4 mr-1" />
                        {delivery.failureReason}
                      </div>
                    )}
                  </div>
                  <div className="flex items-center space-x-2">
                    <p className="font-semibold">{formatPrice(delivery.deliveryFee)}</p>
                    <Dialog>
                      <DialogTrigger asChild>
                        <Button 
                          variant="outline" 
                          size="sm"
                          onClick={() => setSelectedDelivery(delivery)}
                        >
                          View Details
                        </Button>
                      </DialogTrigger>
                      <DialogContent className="max-w-2xl">
                        <DialogHeader>
                          <DialogTitle>Delivery Details</DialogTitle>
                          <DialogDescription>
                            Complete information and tracking for delivery {delivery.id.slice(0, 8)}
                          </DialogDescription>
                        </DialogHeader>
                        {selectedDelivery && (
                          <div className="space-y-6">
                            {/* Delivery Info */}
                            <div className="grid grid-cols-2 gap-4">
                              <div>
                                <Label className="text-sm font-medium">Order ID</Label>
                                <p className="text-sm">{selectedDelivery.orderId}</p>
                              </div>
                              <div>
                                <Label className="text-sm font-medium">Status</Label>
                                <div className="mt-1">{getStatusBadge(selectedDelivery.status)}</div>
                              </div>
                              <div>
                                <Label className="text-sm font-medium">Courier</Label>
                                <p className="text-sm">{selectedDelivery.providerId}</p>
                              </div>
                              <div>
                                <Label className="text-sm font-medium">Delivery Fee</Label>
                                <p className="text-sm">{formatPrice(selectedDelivery.deliveryFee)}</p>
                              </div>
                            </div>

                            {/* Addresses */}
                            <div className="space-y-4">
                              <div>
                                <Label className="text-sm font-medium">Pickup Address</Label>
                                <p className="text-sm text-gray-600">{selectedDelivery.pickupAddress}</p>
                              </div>
                              <div>
                                <Label className="text-sm font-medium">Delivery Address</Label>
                                <p className="text-sm text-gray-600">{selectedDelivery.deliveryAddress}</p>
                              </div>
                            </div>

                            {/* Tracking Updates */}
                            {selectedDelivery.updates && selectedDelivery.updates.length > 0 && (
                              <div>
                                <Label className="text-sm font-medium">Tracking Updates</Label>
                                <div className="mt-2 space-y-2 max-h-40 overflow-y-auto">
                                  {selectedDelivery.updates.map((update) => (
                                    <div key={update.id} className="text-sm border-l-2 border-gray-200 pl-3">
                                      <div className="flex items-center justify-between">
                                        <span className="font-medium">{update.status}</span>
                                        <span className="text-gray-500">{formatDate(update.timestamp)}</span>
                                      </div>
                                      <p className="text-gray-600">{update.description}</p>
                                      {update.location && (
                                        <p className="text-gray-500 text-xs">{update.location}</p>
                                      )}
                                    </div>
                                  ))}
                                </div>
                              </div>
                            )}

                            {/* Reassign Option */}
                            {selectedDelivery.status !== 'delivered' && (
                              <div className="border-t pt-4">
                                <Label className="text-sm font-medium text-red-600">Reassign Delivery</Label>
                                <div className="mt-2 space-y-3">
                                  <Select
                                    value={reassignData.newProviderId}
                                    onValueChange={(value) => setReassignData({ ...reassignData, newProviderId: value })}
                                  >
                                    <SelectTrigger>
                                      <SelectValue placeholder="Select new courier" />
                                    </SelectTrigger>
                                    <SelectContent>
                                      <SelectItem value="g4s">G4S Courier</SelectItem>
                                      <SelectItem value="fargo_courier">Fargo Courier</SelectItem>
                                      <SelectItem value="pickup-mtaani">Pickup Mtaani</SelectItem>
                                      <SelectItem value="speed-af">Speed AF</SelectItem>
                                    </SelectContent>
                                  </Select>
                                  <Textarea
                                    placeholder="Reason for reassignment..."
                                    value={reassignData.reason}
                                    onChange={(e) => setReassignData({ ...reassignData, reason: e.target.value })}
                                  />
                                  <Button
                                    onClick={() => handleReassignDelivery(selectedDelivery.id)}
                                    disabled={!reassignData.newProviderId}
                                    variant="destructive"
                                    size="sm"
                                  >
                                    Reassign Delivery
                                  </Button>
                                </div>
                              </div>
                            )}
                          </div>
                        )}
                      </DialogContent>
                    </Dialog>
                  </div>
                </div>
              </div>
            ))}

            {filteredDeliveries.length === 0 && (
              <div className="text-center py-8">
                <Package className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-500">No deliveries found matching your filters</p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { adminApiRequest, queryClient, getAdminQueryFn } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { 
  DollarSign, 
  TrendingUp, 
  Users, 
  CheckCircle, 
  Clock, 
  XCircle,
  Eye,
  Filter,
  Search,
  ArrowUpDown
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface VendorEarnings {
  vendorId: string;
  businessName: string;
  totalEarnings: number;
  availableBalance: number;
  pendingBalance: number;
  confirmedOrders: number;
  pendingOrders: number;
  disputedOrders: number;
  lastPayoutDate?: string;
  lastPayoutAmount?: number;
}

interface PayoutRequest {
  id: string;
  vendorId: string;
  vendorName: string;
  amount: number;
  requestDate: string;
  status: 'pending' | 'processing' | 'completed' | 'failed';
  bankAccount: string;
  processedDate?: string;
  failureReason?: string;
}

interface PlatformEarnings {
  totalPlatformEarnings: number;
  totalVendorEarnings: number;
  platformFeePercentage: number;
  totalOrders: number;
  avgOrderValue: number;
  topEarningVendors: Array<{
    vendorId: string;
    businessName: string;
    earnings: number;
  }>;
}

export default function EarningsManagementAdmin() {
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [selectedPeriod, setSelectedPeriod] = useState("month");

  // Fetch platform earnings overview
  const { data: platformEarnings, isLoading: platformLoading } = useQuery({
    queryKey: ['/api/admin/platform-earnings', { period: selectedPeriod }],
    queryFn: getAdminQueryFn({ on401: "returnNull" }),
  });

  // Fetch vendor earnings data
  const { data: vendorEarnings = [], isLoading: vendorLoading } = useQuery({
    queryKey: ['/api/admin/vendor-earnings'],
    queryFn: getAdminQueryFn({ on401: "returnNull" }),
  });

  // Fetch payout requests
  const { data: payoutRequests = [], isLoading: payoutLoading } = useQuery({
    queryKey: ['/api/admin/payout-requests', { status: statusFilter !== 'all' ? statusFilter : '' }],
    queryFn: getAdminQueryFn({ on401: "returnNull" }),
  });

  // Process payout request mutation
  const processPayoutMutation = useMutation({
    mutationFn: async ({ requestId, action, reason }: { requestId: string; action: 'approve' | 'reject'; reason?: string }) => {
      return adminApiRequest('/api/admin/process-payout', 'POST', { requestId, action, reason });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/payout-requests'] });
      toast({
        title: "Success",
        description: "Payout request processed successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to process payout request",
        variant: "destructive",
      });
    },
  });

  const formatPrice = (amount: number) => {
    return new Intl.NumberFormat('en-KE', {
      style: 'currency',
      currency: 'KES',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const handleProcessPayout = (requestId: string, action: 'approve' | 'reject', reason?: string) => {
    processPayoutMutation.mutate({ requestId, action, reason });
  };

  const getStatusBadgeVariant = (status: string) => {
    switch (status) {
      case 'completed': return 'default';
      case 'processing': return 'secondary';
      case 'pending': return 'outline';
      case 'failed': return 'destructive';
      default: return 'outline';
    }
  };

  // Filter payout requests - with safe property access
  const filteredPayouts = payoutRequests.filter((request: any) => {
    const vendorName = request.vendorName || request.vendor?.businessName || request.businessName || '';
    const requestId = request.id || '';

    const matchesSearch = searchTerm === '' || 
                         vendorName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         requestId.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || request.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  if (platformLoading || vendorLoading || payoutLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          {[...Array(4)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardContent className="p-6">
                <div className="h-20 bg-gray-200 rounded"></div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Earnings Management</h2>
          <p className="text-gray-600">Monitor platform earnings and manage vendor payouts</p>
        </div>
        <Select value={selectedPeriod} onValueChange={setSelectedPeriod}>
          <SelectTrigger className="w-48">
            <SelectValue placeholder="Select period" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="week">This Week</SelectItem>
            <SelectItem value="month">This Month</SelectItem>
            <SelectItem value="quarter">This Quarter</SelectItem>
            <SelectItem value="year">This Year</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* Platform Overview */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-green-100 p-3 rounded-full">
                <DollarSign className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Platform Revenue</p>
                <p className="text-2xl font-bold text-gray-900">
                  {formatPrice(platformEarnings?.totalPlatformEarnings || 0)}
                </p>
                <p className="text-sm text-gray-500">
                  {platformEarnings?.platformFeePercentage || 20}% platform fee
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-blue-100 p-3 rounded-full">
                <TrendingUp className="w-6 h-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Vendor Earnings</p>
                <p className="text-2xl font-bold text-gray-900">
                  {formatPrice(platformEarnings?.totalVendorEarnings || 0)}
                </p>
                <p className="text-sm text-gray-500">
                  {platformEarnings?.totalOrders || 0} orders
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-purple-100 p-3 rounded-full">
                <Users className="w-6 h-6 text-purple-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Active Vendors</p>
                <p className="text-2xl font-bold text-gray-900">{vendorEarnings.length}</p>
                <p className="text-sm text-gray-500">Earning vendors</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-yellow-100 p-3 rounded-full">
                <CheckCircle className="w-6 h-6 text-yellow-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Avg Order Value</p>
                <p className="text-2xl font-bold text-gray-900">
                  {formatPrice(platformEarnings?.avgOrderValue || 0)}
                </p>
                <p className="text-sm text-gray-500">Per transaction</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Top Earning Vendors */}
      <Card>
        <CardHeader>
          <CardTitle>Top Earning Vendors</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {platformEarnings?.topEarningVendors?.length > 0 ? (
              platformEarnings.topEarningVendors.map((vendor: any, index: number) => (
                <div key={vendor.vendorId} className="flex items-center justify-between p-4 border rounded-lg">
                  <div className="flex items-center space-x-4">
                    <div className="flex items-center justify-center w-8 h-8 bg-buylock-primary text-white rounded-full text-sm font-bold">
                      {index + 1}
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-900">{vendor.businessName}</h3>
                      <p className="text-sm text-gray-600">Vendor ID: {vendor.vendorId.slice(0, 8)}</p>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className="font-bold text-buylock-primary text-lg">{formatPrice(vendor.earnings)}</p>
                    <Badge variant="outline">Top {index + 1}</Badge>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center py-8 text-gray-500">
                <TrendingUp className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                <p>No vendor earnings data available</p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Payout Requests */}
      <Card>
        <CardHeader>
          <CardTitle>Payout Requests</CardTitle>
          <div className="flex space-x-4">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <Input
                placeholder="Search vendors or request ID..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 w-64"
              />
            </div>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-48">
                <Filter className="w-4 h-4 mr-2" />
                <SelectValue placeholder="Filter by status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Status</SelectItem>
                <SelectItem value="pending">Pending</SelectItem>
                <SelectItem value="processing">Processing</SelectItem>
                <SelectItem value="completed">Completed</SelectItem>
                <SelectItem value="failed">Failed</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {filteredPayouts.length > 0 ? (
              filteredPayouts.map((request: PayoutRequest) => (
                <div key={request.id} className="flex items-center justify-between p-4 border rounded-lg">
                  <div className="flex items-center space-x-4">
                    <div className="p-2 rounded-full bg-blue-100">
                      <DollarSign className="w-5 h-5 text-blue-600" />
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-900">{request.vendorName}</h3>
                      <p className="text-sm text-gray-600">Request ID: {request.id}</p>
                      <p className="text-sm text-gray-500">
                        Requested: {formatDate(request.requestDate)}
                      </p>
                      <p className="text-xs text-gray-500">{request.bankAccount}</p>
                    </div>
                  </div>
                  <div className="flex items-center space-x-4">
                    <div className="text-right">
                      <p className="font-bold text-buylock-primary text-lg">
                        {formatPrice(request.amount)}
                      </p>
                      <Badge variant={getStatusBadgeVariant(request.status)}>
                        {request.status.charAt(0).toUpperCase() + request.status.slice(1)}
                      </Badge>
                      {request.processedDate && (
                        <p className="text-xs text-gray-500 mt-1">
                          Processed: {formatDate(request.processedDate)}
                        </p>
                      )}
                    </div>
                    {request.status === 'pending' && (
                      <div className="flex space-x-2">
                        <Button
                          size="sm"
                          onClick={() => handleProcessPayout(request.id, 'approve')}
                          disabled={processPayoutMutation.isPending}
                          className="bg-green-600 hover:bg-green-700"
                        >
                          <CheckCircle className="w-4 h-4 mr-1" />
                          Approve
                        </Button>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => handleProcessPayout(request.id, 'reject', 'Administrative review required')}
                          disabled={processPayoutMutation.isPending}
                        >
                          <XCircle className="w-4 h-4 mr-1" />
                          Reject
                        </Button>
                      </div>
                    )}
                    <Button variant="outline" size="sm">
                      <Eye className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center py-8 text-gray-500">
                <Clock className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                <p>No payout requests found</p>
                <p className="text-sm">Payout requests will appear here when vendors request payouts</p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Calendar, 
  Search, 
  Eye, 
  Clock, 
  MapPin,
  User,
  Phone,
  Mail,
  DollarSign,
  CheckCircle,
  XCircle,
  AlertTriangle,
  Filter,
  Store,
  FileText,
  RefreshCw
} from "lucide-react";

interface Appointment {
  id: string;
  orderId: string;
  serviceId: string;
  serviceName: string;
  customerId: string;
  customerName: string;
  customerEmail: string;
  vendorId: string;
  vendorName: string;
  vendorBusinessName: string;
  appointmentDate: string;
  appointmentTime: string;
  duration: number;
  price: string;
  status: string;
  notes?: string;
  vendorNotes?: string;
  serviceLocation: string;
  locationCoordinates?: string;
  detailedInstructions?: string;
  createdAt: string;
  updatedAt: string;
}

export default function ManageAppointments() {
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [dateFilter, setDateFilter] = useState("all");
  const [selectedAppointment, setSelectedAppointment] = useState<Appointment | null>(null);
  const [showDetails, setShowDetails] = useState(false);
  const [activeTab, setActiveTab] = useState("active");

  // Fetch all appointments from API
  const { data: appointments = [], isLoading } = useQuery({
    queryKey: ['/api/admin/appointments'],
    retry: false,
  });

  // Filter appointments into active and past
  const today = new Date();
  today.setHours(0, 0, 0, 0);

  const activeAppointments = (appointments as Appointment[]).filter(appointment => {
    const appointmentDate = new Date(appointment.appointmentDate);
    appointmentDate.setHours(0, 0, 0, 0);
    return appointmentDate >= today && !['completed', 'cancelled'].includes(appointment.status);
  });

  const pastAppointments = (appointments as Appointment[]).filter(appointment => {
    const appointmentDate = new Date(appointment.appointmentDate);
    appointmentDate.setHours(0, 0, 0, 0);
    return appointmentDate < today || ['completed', 'cancelled'].includes(appointment.status);
  });

  const currentAppointments = activeTab === "active" ? activeAppointments : pastAppointments;

  const filteredAppointments = currentAppointments.filter(appointment => {
    const matchesSearch = appointment.customerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         appointment.serviceName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (appointment.vendorBusinessName && appointment.vendorBusinessName.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesStatus = statusFilter === "all" || appointment.status === statusFilter;

    if (dateFilter === "all") return matchesSearch && matchesStatus;

    const appointmentDate = new Date(appointment.appointmentDate);
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(today.getDate() + 1);

    const matchesDate = dateFilter === "all" || 
      (dateFilter === "today" && appointmentDate.toDateString() === today.toDateString()) ||
      (dateFilter === "tomorrow" && appointmentDate.toDateString() === tomorrow.toDateString()) ||
      (dateFilter === "this_week" && appointmentDate >= today && appointmentDate <= new Date(today.getTime() + 7 * 24 * 60 * 60 * 1000));

    return matchesSearch && matchesStatus && matchesDate;
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending_acceptance': case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'accepted': case 'starting_job': case 'in_progress': return 'bg-blue-100 text-blue-800';
      case 'completed': return 'bg-green-100 text-green-800';
      case 'declined': case 'cancelled': return 'bg-red-100 text-red-800';
      case 'delayed': case 'almost_done': return 'bg-orange-100 text-orange-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'pending_acceptance': case 'pending': return <Clock className="w-4 h-4" />;
      case 'accepted': case 'starting_job': case 'in_progress': return <CheckCircle className="w-4 h-4" />;
      case 'completed': return <CheckCircle className="w-4 h-4" />;
      case 'declined': case 'cancelled': return <XCircle className="w-4 h-4" />;
      case 'delayed': return <AlertTriangle className="w-4 h-4" />;
      default: return <Clock className="w-4 h-4" />;
    }
  };

  const handleViewDetails = (appointment: Appointment) => {
    setSelectedAppointment(appointment);
    setShowDetails(true);
  };

  const getAppointmentStats = () => {
    const total = currentAppointments.length;
    const pending = currentAppointments.filter(a => ['pending_acceptance', 'pending'].includes(a.status)).length;
    const accepted = currentAppointments.filter(a => ['accepted', 'starting_job', 'in_progress'].includes(a.status)).length;
    const completed = currentAppointments.filter(a => a.status === 'completed').length;
    const totalRevenue = currentAppointments
      .filter(a => a.status === 'completed')
      .reduce((sum, a) => sum + (parseFloat(a.price) || 0), 0);

    return { total, pending, accepted, completed, totalRevenue };
  };

  const stats = getAppointmentStats();

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-KE');
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-KE', {
      style: 'currency',
      currency: 'KES'
    }).format(amount);
  };

  if (showDetails && selectedAppointment) {
    return (
      <div className="space-y-6">
        {/* Header */}
        <div className="flex items-center space-x-4">
          <Button variant="ghost" onClick={() => setShowDetails(false)}>
            â† Back to Appointments
          </Button>
          <div className="flex-1">
            <h2 className="text-2xl font-bold text-gray-900">Appointment Details</h2>
            <p className="text-gray-600">#{selectedAppointment.id}</p>
          </div>
          <Badge className={`${getStatusColor(selectedAppointment.status)} flex items-center gap-1`}>
            {getStatusIcon(selectedAppointment.status)}
            {selectedAppointment.status.replace('_', ' ')}
          </Badge>
        </div>

        {/* Appointment Details */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <User className="w-5 h-5 mr-2" />
                Customer Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium text-gray-500">Name</label>
                <p className="text-gray-900">{selectedAppointment.customerName}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Email</label>
                <p className="text-gray-900">{selectedAppointment.customerEmail}</p>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Store className="w-5 h-5 mr-2" />
                Vendor Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium text-gray-500">Business Name</label>
                <p className="text-gray-900">{selectedAppointment.vendorBusinessName || selectedAppointment.vendorName}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Vendor ID</label>
                <p className="text-gray-900">{selectedAppointment.vendorId}</p>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Calendar className="w-5 h-5 mr-2" />
                Appointment Details
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium text-gray-500">Service</label>
                <p className="text-gray-900">{selectedAppointment.serviceName}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Date & Time</label>
                <p className="text-gray-900">{formatDate(selectedAppointment.appointmentDate)} at {selectedAppointment.appointmentTime}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Duration</label>
                <p className="text-gray-900">{selectedAppointment.duration} hour(s)</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Total Amount</label>
                <p className="text-gray-900 font-semibold">{formatCurrency(parseFloat(selectedAppointment.price) || 0)}</p>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <MapPin className="w-5 h-5 mr-2" />
                Location Details
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium text-gray-500">Service Location</label>
                <p className="text-gray-900">{selectedAppointment.serviceLocation}</p>
              </div>
              {selectedAppointment.detailedInstructions && (
                <div>
                  <label className="text-sm font-medium text-gray-500">Instructions</label>
                  <p className="text-gray-900">{selectedAppointment.detailedInstructions}</p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {(selectedAppointment.notes || selectedAppointment.vendorNotes) && (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <FileText className="w-5 h-5 mr-2" />
                Notes
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              {selectedAppointment.notes && (
                <div>
                  <label className="text-sm font-medium text-gray-500">Customer Notes</label>
                  <p className="text-gray-900">{selectedAppointment.notes}</p>
                </div>
              )}
              {selectedAppointment.vendorNotes && (
                <div>
                  <label className="text-sm font-medium text-gray-500">Vendor Notes</label>
                  <p className="text-gray-900">{selectedAppointment.vendorNotes}</p>
                </div>
              )}
            </CardContent>
          </Card>
        )}
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h3 className="text-xl font-semibold text-gray-900">Manage Appointments</h3>
          <p className="text-gray-600">Monitor all service appointments across the platform</p>
        </div>
        <Button variant="ghost" size="sm" onClick={() => window.location.reload()}>
          <RefreshCw className="w-4 h-4 mr-2" />
          Refresh
        </Button>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-blue-100 p-3 rounded-full">
                <Calendar className="w-6 h-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Appointments</p>
                <p className="text-2xl font-bold text-gray-900">{stats.total}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-yellow-100 p-3 rounded-full">
                <Clock className="w-6 h-6 text-yellow-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Pending</p>
                <p className="text-2xl font-bold text-gray-900">{stats.pending}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-blue-100 p-3 rounded-full">
                <CheckCircle className="w-6 h-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">In Progress</p>
                <p className="text-2xl font-bold text-gray-900">{stats.accepted}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-green-100 p-3 rounded-full">
                <DollarSign className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">{activeTab === "active" ? "Pending Revenue" : "Total Revenue"}</p>
                <p className="text-2xl font-bold text-gray-900">{formatCurrency(stats.totalRevenue)}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-6">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                <Input 
                  placeholder="Search by customer, service, or vendor..." 
                  className="pl-10" 
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            </div>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Filter by status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Statuses</SelectItem>
                <SelectItem value="pending_acceptance">Pending Acceptance</SelectItem>
                <SelectItem value="accepted">Accepted</SelectItem>
                <SelectItem value="in_progress">In Progress</SelectItem>
                <SelectItem value="completed">Completed</SelectItem>
                <SelectItem value="cancelled">Cancelled</SelectItem>
                <SelectItem value="declined">Declined</SelectItem>
              </SelectContent>
            </Select>
            <Select value={dateFilter} onValueChange={setDateFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Filter by date" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Dates</SelectItem>
                <SelectItem value="today">Today</SelectItem>
                <SelectItem value="tomorrow">Tomorrow</SelectItem>
                <SelectItem value="this_week">This Week</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Appointments Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="active">Active Appointments ({activeAppointments.length})</TabsTrigger>
          <TabsTrigger value="past">Past Appointments ({pastAppointments.length})</TabsTrigger>
        </TabsList>

        <TabsContent value="active" className="mt-6">
          <Card>
            <CardHeader>
              <CardTitle>Active Appointments</CardTitle>
            </CardHeader>
            <CardContent>
              {isLoading ? (
                <div className="space-y-4">
                  {Array.from({ length: 3 }).map((_, i) => (
                    <div key={i} className="flex items-center justify-between p-4 border rounded-lg">
                      <div className="flex items-center space-x-4">
                        <div className="w-12 h-12 bg-gray-200 rounded-full animate-pulse"></div>
                        <div className="space-y-2">
                          <div className="h-4 bg-gray-200 rounded animate-pulse w-32"></div>
                          <div className="h-3 bg-gray-200 rounded animate-pulse w-48"></div>
                        </div>
                      </div>
                      <div className="h-8 bg-gray-200 rounded animate-pulse w-20"></div>
                    </div>
                  ))}
                </div>
              ) : filteredAppointments.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <Calendar className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                  <p>No active appointments found matching your criteria</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {filteredAppointments.map((appointment) => (
                    <div key={appointment.id} className="flex items-center justify-between p-4 border rounded-lg hover:bg-gray-50 transition-colors">
                      <div className="flex items-center space-x-4">
                        <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                          <Calendar className="w-6 h-6 text-blue-600" />
                        </div>
                        <div>
                          <h3 className="font-semibold text-gray-900">{appointment.serviceName}</h3>
                          <p className="text-sm text-gray-600">
                            {appointment.customerName} â€¢ {appointment.vendorBusinessName || appointment.vendorName}
                          </p>
                          <div className="flex items-center space-x-4 text-xs text-gray-500 mt-1">
                            <span>{formatDate(appointment.appointmentDate)} at {appointment.appointmentTime}</span>
                            <span>â€¢</span>
                            <span>{appointment.duration}h</span>
                            <span>â€¢</span>
                            <span>{formatCurrency(parseFloat(appointment.price) || 0)}</span>
                          </div>
                        </div>
                      </div>

                      <div className="flex items-center space-x-4">
                        <Badge className={getStatusColor(appointment.status)}>
                          {appointment.status.replace('_', ' ')}
                        </Badge>
                        <Button 
                          variant="ghost" 
                          size="sm"
                          onClick={() => handleViewDetails(appointment)}
                        >
                          <Eye className="w-4 h-4 mr-2" />
                          View Details
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="past" className="mt-6">
          <Card>
            <CardHeader>
              <CardTitle>Past Appointments</CardTitle>
            </CardHeader>
            <CardContent>
              {isLoading ? (
                <div className="space-y-4">
                  {Array.from({ length: 3 }).map((_, i) => (
                    <div key={i} className="flex items-center justify-between p-4 border rounded-lg">
                      <div className="flex items-center space-x-4">
                        <div className="w-12 h-12 bg-gray-200 rounded-full animate-pulse"></div>
                        <div className="space-y-2">
                          <div className="h-4 bg-gray-200 rounded animate-pulse w-32"></div>
                          <div className="h-3 bg-gray-200 rounded animate-pulse w-48"></div>
                        </div>
                      </div>
                      <div className="h-8 bg-gray-200 rounded animate-pulse w-20"></div>
                    </div>
                  ))}
                </div>
              ) : filteredAppointments.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <Calendar className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                  <p>No past appointments found matching your criteria</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {filteredAppointments.map((appointment) => (
                    <div key={appointment.id} className="flex items-center justify-between p-4 border rounded-lg hover:bg-gray-50 transition-colors">
                      <div className="flex items-center space-x-4">
                        <div className="w-12 h-12 bg-gray-100 rounded-full flex items-center justify-center">
                          <Calendar className="w-6 h-6 text-gray-600" />
                        </div>
                        <div>
                          <h3 className="font-semibold text-gray-900">{appointment.serviceName}</h3>
                          <p className="text-sm text-gray-600">
                            {appointment.customerName} â€¢ {appointment.vendorBusinessName || appointment.vendorName}
                          </p>
                          <div className="flex items-center space-x-4 text-xs text-gray-500 mt-1">
                            <span>{formatDate(appointment.appointmentDate)} at {appointment.appointmentTime}</span>
                            <span>â€¢</span>
                            <span>{appointment.duration}h</span>
                            <span>â€¢</span>
                            <span>{formatCurrency(parseFloat(appointment.price) || 0)}</span>
                          </div>
                        </div>
                      </div>

                      <div className="flex items-center space-x-4">
                        <Badge className={getStatusColor(appointment.status)}>
                          {appointment.status.replace('_', ' ')}
                        </Badge>
                        <Button 
                          variant="ghost" 
                          size="sm"
                          onClick={() => handleViewDetails(appointment)}
                        >
                          <Eye className="w-4 h-4 mr-2" />
                          View Details
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

