interface Order {
  id: string;
  userId: string;
  status: string;
  totalAmount: string;
  paymentMethod?: string;
  paymentStatus: string;
  createdAt: string;
  updatedAt: string;
  deliveryAddress?: string;
  deliveryFee?: string;
  trackingNumber?: string;
  notes?: string;
  user?: {
    id: string;
    email: string;
    firstName?: string;
    lastName?: string;
  };
