interface Category {
  id: string;
  name: string;
  slug: string;
  description?: string;
  imageUrl?: string;
  isActive: boolean;
  createdAt: string;
  subcategories?: Subcategory[];
}

interface Subcategory {
  id: string;
  name: string;
  slug: string;
  description?: string;
  categoryId: string;
  imageUrl?: string;
  isActive: boolean;
  createdAt: string;
}

interface Brand {
  id: string;
  name: string;
  slug: string;
  description?: string;
  logoUrl?: string;
  isActive: boolean;
  createdAt: string;
}

interface ProductAttribute {
  id: string;
  name: string;
  type: 'text' | 'number' | 'boolean' | 'select';
  options?: string[];
  categoryId?: string;
  subcategoryId?: string;
  isRequired: boolean;
  isFilterable: boolean;
  displayOrder: number;
  isActive: boolean;
  createdAt: string;
}

