export default function ProductsManagement() {
  const [searchTerm, setSearchTerm] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("all");
  const [statusFilter, setStatusFilter] = useState("all");
  const [approvalFilter, setApprovalFilter] = useState("all");
  const [featuredFilter, setFeaturedFilter] = useState("all");
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [showDetails, setShowDetails] = useState(false);
  const [activeTab, setActiveTab] = useState("all");

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch all products from API (admin endpoint)
  const { data: products = [], isLoading: productsLoading } = useQuery({
    queryKey: ['/api/admin/products'],
    retry: false,
  });

  // Fetch categories for filter
  const { data: categories = [] } = useQuery({
    queryKey: ['/api/categories'],
    retry: false,
  });

  // Toggle admin approval mutation
  const toggleApprovalMutation = useMutation({
    mutationFn: async ({ productId, approved }: { productId: string; approved: boolean }) => {
      return apiRequest("PUT", `/api/admin/products/${productId}/approval`, { approved });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/products'] });
      toast({
        title: "Success",
        description: "Product approval status updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update approval status",
        variant: "destructive",
      });
    },
  });

  // Filter products based on status
  const approvedProducts = (products as Product[]).filter(product => product.adminApproved);
  const pendingProducts = (products as Product[]).filter(product => !product.adminApproved);
  const activeProducts = (products as Product[]).filter(product => product.isActive);
  const inactiveProducts = (products as Product[]).filter(product => !product.isActive);
  const outOfStockProducts = (products as Product[]).filter(product => product.stock === 0);

  const currentProducts = activeTab === "all" ? products as Product[] :
                         activeTab === "approved" ? approvedProducts :
                         activeTab === "pending" ? pendingProducts :
                         activeTab === "active" ? activeProducts :
                         activeTab === "inactive" ? inactiveProducts : outOfStockProducts;

  const filteredProducts = currentProducts.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (product.vendorBusinessName && product.vendorBusinessName.toLowerCase().includes(searchTerm.toLowerCase())) ||
                         (product.description && product.description.toLowerCase().includes(searchTerm.toLowerCase()));

    const matchesCategory = categoryFilter === "all" || product.categoryId === categoryFilter;

    const matchesStatus = statusFilter === "all" || 
                         (statusFilter === "active" && product.isActive) ||
                         (statusFilter === "inactive" && !product.isActive) ||
                         (statusFilter === "out_of_stock" && product.stock === 0);

    const matchesApproval = approvalFilter === "all" ||
                           (approvalFilter === "approved" && product.adminApproved) ||
                           (approvalFilter === "pending" && !product.adminApproved);

    const matchesFeatured = featuredFilter === "all" ||
                           (featuredFilter === "featured" && product.isFeatured) ||
                           (featuredFilter === "not_featured" && !product.isFeatured);

    return matchesSearch && matchesCategory && matchesStatus && matchesApproval && matchesFeatured;
  });

  const getStatusColor = (product: Product) => {
    if (product.stock === 0) return 'bg-red-100 text-red-800';
    if (!product.isActive) return 'bg-gray-100 text-gray-800';
    return 'bg-green-100 text-green-800';
  };

  const getStatusText = (product: Product) => {
    if (product.stock === 0) return 'Out of Stock';
    if (!product.isActive) return 'Inactive';
    return 'Active';
  };

  const formatCurrency = (amount: string) => {
    return new Intl.NumberFormat('en-KE', {
      style: 'currency',
      currency: 'KES'
    }).format(parseFloat(amount));
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-KE');
  };

  const getProductStats = () => {
    const total = currentProducts.length;
    const approved = currentProducts.filter(p => p.adminApproved).length;
    const pending = currentProducts.filter(p => !p.adminApproved).length;
    const active = currentProducts.filter(p => p.isActive && p.stock > 0).length;
    const outOfStock = currentProducts.filter(p => p.stock === 0).length;
    const featured = currentProducts.filter(p => p.isFeatured).length;
    const totalValue = currentProducts.reduce((sum, p) => sum + (parseFloat(p.price) * p.stock), 0);
    const totalStock = currentProducts.reduce((sum, p) => sum + p.stock, 0);

    return { total, approved, pending, active, outOfStock, featured, totalValue, totalStock };
  };

  const stats = getProductStats();

  const handleViewDetails = (product: Product) => {
    setSelectedProduct(product);
    setShowDetails(true);
  };

  const handleToggleApproval = (product: Product) => {
    toggleApprovalMutation.mutate({
      productId: product.id,
      approved: !product.adminApproved
    });
  };

  if (showDetails && selectedProduct) {
    return (
      <div className="space-y-6">
        {/* Header */}
        <div className="flex items-center space-x-4">
          <Button variant="ghost" onClick={() => setShowDetails(false)}>
            â† Back to Products
          </Button>
          <div className="flex-1">
            <h2 className="text-2xl font-bold text-gray-900">Product Details</h2>
            <p className="text-gray-600">#{selectedProduct.id}</p>
          </div>
          <div className="flex items-center space-x-2">
            <Badge className={getStatusColor(selectedProduct)}>
              {getStatusText(selectedProduct)}
            </Badge>
            {selectedProduct.isFeatured && (
              <Badge className="bg-yellow-100 text-yellow-800">
                <Star className="w-3 h-3 mr-1" />
                Featured
              </Badge>
            )}
          </div>
        </div>

        {/* Product Details */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Package className="w-5 h-5 mr-2" />
                Product Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium text-gray-500">Name</label>
                <p className="text-gray-900 font-semibold">{selectedProduct.name}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Description</label>
                <p className="text-gray-900">{selectedProduct.description}</p>
              </div>
              {selectedProduct.shortDescription && (
                <div>
                  <label className="text-sm font-medium text-gray-500">Short Description</label>
                  <p className="text-gray-900">{selectedProduct.shortDescription}</p>
                </div>
              )}
              <div>
                <label className="text-sm font-medium text-gray-500">Slug</label>
                <p className="text-gray-900">{selectedProduct.slug}</p>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <DollarSign className="w-5 h-5 mr-2" />
                Pricing & Stock
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium text-gray-500">Price</label>
                <p className="text-gray-900 font-semibold text-lg">{formatCurrency(selectedProduct.price)}</p>
              </div>
              {selectedProduct.originalPrice && (
                <div>
                  <label className="text-sm font-medium text-gray-500">Original Price</label>
                  <p className="text-gray-900 line-through">{formatCurrency(selectedProduct.originalPrice)}</p>
                </div>
              )}
              <div>
                <label className="text-sm font-medium text-gray-500">Stock</label>
                <p className="text-gray-900">{selectedProduct.stock} units</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Stock Value</label>
                <p className="text-gray-900 font-semibold">{formatCurrency((parseFloat(selectedProduct.price) * selectedProduct.stock).toString())}</p>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Store className="w-5 h-5 mr-2" />
                Vendor Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium text-gray-500">Vendor ID</label>
                <p className="text-gray-900">{selectedProduct.vendorId}</p>
              </div>
              {selectedProduct.vendorBusinessName && (
                <div>
                  <label className="text-sm font-medium text-gray-500">Business Name</label>
                  <p className="text-gray-900">{selectedProduct.vendorBusinessName}</p>
                </div>
              )}
              {selectedProduct.categoryName && (
                <div>
                  <label className="text-sm font-medium text-gray-500">Category</label>
                  <p className="text-gray-900">{selectedProduct.categoryName}</p>
                </div>
              )}
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Star className="w-5 h-5 mr-2" />
                Performance & Status
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium text-gray-500">Rating</label>
                <p className="text-gray-900">{selectedProduct.rating}/5.0 ({selectedProduct.reviewCount} reviews)</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Status</label>
                <div className="flex items-center space-x-2">
                  <Badge className={getStatusColor(selectedProduct)}>
                    {getStatusText(selectedProduct)}
                  </Badge>
                  {selectedProduct.isFeatured && (
                    <Badge className="bg-yellow-100 text-yellow-800">Featured</Badge>
                  )}
                </div>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Created</label>
                <p className="text-gray-900">{formatDate(selectedProduct.createdAt)}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Last Updated</label>
                <p className="text-gray-900">{formatDate(selectedProduct.updatedAt)}</p>
              </div>
            </CardContent>
          </Card>
        </div>

        {selectedProduct.imageUrl && (
          <Card>
            <CardHeader>
              <CardTitle>Product Images</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <img 
                  src={selectedProduct.imageUrl} 
                  alt={selectedProduct.name}
                  className="w-full h-48 object-cover rounded-lg"
                />
                {selectedProduct.imageUrls?.map((url, index) => (
                  <img 
                    key={index}
                    src={url} 
                    alt={`${selectedProduct.name} ${index + 1}`}
                    className="w-full h-48 object-cover rounded-lg"
                  />
                ))}
              </div>
            </CardContent>
          </Card>
        )}

        {selectedProduct.tags && selectedProduct.tags.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Tag className="w-5 h-5 mr-2" />
                Tags
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex flex-wrap gap-2">
                {selectedProduct.tags.map((tag, index) => (
                  <Badge key={index} variant="outline">
                    {tag}
                  </Badge>
                ))}
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h3 className="text-xl font-semibold text-gray-900">Product Management</h3>
          <p className="text-gray-600">Monitor and manage all products across the platform</p>
        </div>
        <Button variant="ghost" size="sm" onClick={() => window.location.reload()}>
          <RefreshCw className="w-4 h-4 mr-2" />
          Refresh
        </Button>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-6 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-blue-100 p-3 rounded-full">
                <Package className="w-6 h-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Products</p>
                <p className="text-2xl font-bold text-gray-900">{stats.total}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-green-100 p-3 rounded-full">
                <TrendingUp className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Active</p>
                <p className="text-2xl font-bold text-gray-900">{stats.active}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-red-100 p-3 rounded-full">
                <ShoppingCart className="w-6 h-6 text-red-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Out of Stock</p>
                <p className="text-2xl font-bold text-gray-900">{stats.outOfStock}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-yellow-100 p-3 rounded-full">
                <Star className="w-6 h-6 text-yellow-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Featured</p>
                <p className="text-2xl font-bold text-gray-900">{stats.featured}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-green-100 p-3 rounded-full">
                <CheckCircle className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Approved</p>
                <p className="text-2xl font-bold text-gray-900">{stats.approved}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-yellow-100 p-3 rounded-full">
                <Clock className="w-6 h-6 text-yellow-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Pending</p>
                <p className="text-2xl font-bold text-gray-900">{stats.pending}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-6">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                <Input 
                  placeholder="Search by product name, vendor, or description..." 
                  className="pl-10" 
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            </div>
            <Select value={categoryFilter} onValueChange={setCategoryFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Filter by category" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Categories</SelectItem>
                {(categories as Category[]).map(category => (
                  <SelectItem key={category.id} value={category.id}>
                    {category.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Filter by status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Statuses</SelectItem>
                <SelectItem value="active">Active</SelectItem>
                <SelectItem value="inactive">Inactive</SelectItem>
                <SelectItem value="out_of_stock">Out of Stock</SelectItem>
              </SelectContent>
            </Select>
            <Select value={approvalFilter} onValueChange={setApprovalFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Filter by approval" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Approval</SelectItem>
                <SelectItem value="approved">Approved</SelectItem>
                <SelectItem value="pending">Pending</SelectItem>
              </SelectContent>
            </Select>
            <Select value={featuredFilter} onValueChange={setFeaturedFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Filter by featured" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Products</SelectItem>
                <SelectItem value="featured">Featured Only</SelectItem>
                <SelectItem value="not_featured">Not Featured</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Product Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="grid w-full grid-cols-6">
          <TabsTrigger value="all">All Products ({(products as Product[]).length})</TabsTrigger>
          <TabsTrigger value="approved">Approved ({approvedProducts.length})</TabsTrigger>
          <TabsTrigger value="pending">Pending ({pendingProducts.length})</TabsTrigger>
          <TabsTrigger value="active">Active ({activeProducts.length})</TabsTrigger>
          <TabsTrigger value="inactive">Inactive ({inactiveProducts.length})</TabsTrigger>
          <TabsTrigger value="out_of_stock">Out of Stock ({outOfStockProducts.length})</TabsTrigger>
        </TabsList>

        <TabsContent value={activeTab} className="mt-6">
          <Card>
            <CardHeader>
              <CardTitle>
                {activeTab === "all" ? "All Products" : 
                 activeTab === "active" ? "Active Products" :
                 activeTab === "inactive" ? "Inactive Products" : "Out of Stock Products"}
              </CardTitle>
            </CardHeader>
            <CardContent>
              {productsLoading ? (
                <div className="space-y-4">
                  {Array.from({ length: 5 }).map((_, i) => (
                    <div key={i} className="flex items-center justify-between p-4 border rounded-lg">
                      <div className="flex items-center space-x-4">
                        <div className="w-16 h-16 bg-gray-200 rounded-lg animate-pulse"></div>
                        <div className="space-y-2">
                          <div className="h-4 bg-gray-200 rounded animate-pulse w-48"></div>
                          <div className="h-3 bg-gray-200 rounded animate-pulse w-32"></div>
                        </div>
                      </div>
                      <div className="h-8 bg-gray-200 rounded animate-pulse w-20"></div>
                    </div>
                  ))}
                </div>
              ) : filteredProducts.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <Package className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                  <p>No products found matching your criteria</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {filteredProducts.map((product) => (
                    <div key={product.id} className="flex items-center justify-between p-4 border rounded-lg hover:bg-gray-50 transition-colors">
                      <div className="flex items-center space-x-4">
                        <div className="w-16 h-16 bg-gray-100 rounded-lg flex items-center justify-center overflow-hidden">
                          {product.imageUrl ? (
                            <img 
                              src={product.imageUrl} 
                              alt={product.name}
                              className="w-full h-full object-cover"
                            />
                          ) : (
                            <Package className="w-8 h-8 text-gray-400" />
                          )}
                        </div>
                        <div>
                          <div className="flex items-center space-x-2">
                            <h3 className="font-semibold text-gray-900">{product.name}</h3>
                            {product.isFeatured && (
                              <Badge className="bg-yellow-100 text-yellow-800">
                                <Star className="w-3 h-3 mr-1" />
                                Featured
                              </Badge>
                            )}
                            <Badge className={product.adminApproved ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'}>
                              {product.adminApproved ? 'Approved' : 'Pending'}
                            </Badge>
                          </div>
                          <p className="text-sm text-gray-600">
                            Vendor: {product.vendorBusinessName || product.vendorId} â€¢ Stock: {product.stock}
                          </p>
                          <div className="flex items-center space-x-4 text-xs text-gray-500 mt-1">
                            <span>{formatCurrency(product.price)}</span>
                            {product.originalPrice && (
                              <>
                                <span>â€¢</span>
                                <span className="line-through">{formatCurrency(product.originalPrice)}</span>
                              </>
                            )}
                            <span>â€¢</span>
                            <span>{product.rating}/5 ({product.reviewCount} reviews)</span>
                          </div>
                        </div>
                      </div>

                      <div className="flex items-center space-x-4">
                        <Badge className={getStatusColor(product)}>
                          {getStatusText(product)}
                        </Badge>
                        <div className="flex items-center space-x-2">
                          <span className="text-xs text-gray-500">Admin Approval:</span>
                          <Switch
                            checked={product.adminApproved}
                            onCheckedChange={() => handleToggleApproval(product)}
                            disabled={toggleApprovalMutation.isPending}
                          />
                        </div>
                        <Button 
                          variant="ghost" 
                          size="sm"
                          onClick={() => handleViewDetails(product)}
                        >
                          <Eye className="w-4 h-4 mr-2" />
                          View Details
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Switch } from "@/components/ui/switch";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { 
  Settings,
  Search,
  Eye,
  TrendingUp,
  CheckCircle,
  XCircle,
  Star,
  DollarSign,
  Users,
  Filter,
  RefreshCw,
  Store,
  Calendar,
  Tag,
  Clock,
  MapPin,
  ThumbsUp,
  ThumbsDown
} from "lucide-react";

interface Service {
  id: string;
  name: string;
  slug: string;
  description: string;
  shortDescription?: string;
  price: string;
  priceType: string;
  imageUrl?: string;
  imageUrls?: string[];
  categoryId: string;
  providerId: string;
  rating: string;
  reviewCount: number;
  isActive: boolean;
  isFeatured: boolean;
  adminApproved: boolean;
  tags?: string[];
  location?: string;
  isAvailableToday: boolean;
  createdAt: string;
  updatedAt: string;
  // Populated fields from relations
  providerName?: string;
  providerBusinessName?: string;
  categoryName?: string;
}

interface Category {
  id: string;
  name: string;
  slug: string;
}

export default function ServicesManagement() {
  const [searchTerm, setSearchTerm] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("all");
  const [statusFilter, setStatusFilter] = useState("all");
  const [approvalFilter, setApprovalFilter] = useState("all");
  const [featuredFilter, setFeaturedFilter] = useState("all");
  const [selectedService, setSelectedService] = useState<Service | null>(null);
  const [showDetails, setShowDetails] = useState(false);
  const [activeTab, setActiveTab] = useState("all");

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch all services from API (admin endpoint)
  const { data: services = [], isLoading: servicesLoading } = useQuery({
    queryKey: ['/api/admin/services'],
    retry: false,
  });

  // Fetch categories for filter
  const { data: categories = [] } = useQuery({
    queryKey: ['/api/categories'],
    retry: false,
  });

  // Toggle admin approval mutation
  const toggleApprovalMutation = useMutation({
    mutationFn: async ({ serviceId, approved }: { serviceId: string; approved: boolean }) => {
      return apiRequest("PUT", `/api/admin/services/${serviceId}/approval`, { approved });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/services'] });
      toast({
        title: "Success",
        description: "Service approval status updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update approval status",
        variant: "destructive",
      });
    },
  });

  // Filter services based on status
  const approvedServices = (services as Service[]).filter(service => service.adminApproved);
  const pendingServices = (services as Service[]).filter(service => !service.adminApproved);
  const activeServices = (services as Service[]).filter(service => service.isActive);
  const inactiveServices = (services as Service[]).filter(service => !service.isActive);

  const currentServices = activeTab === "all" ? services as Service[] :
                         activeTab === "approved" ? approvedServices :
                         activeTab === "pending" ? pendingServices :
                         activeTab === "active" ? activeServices : inactiveServices;

  const filteredServices = currentServices.filter(service => {
    const matchesSearch = service.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (service.providerBusinessName && service.providerBusinessName.toLowerCase().includes(searchTerm.toLowerCase())) ||
                         (service.description && service.description.toLowerCase().includes(searchTerm.toLowerCase()));

    const matchesCategory = categoryFilter === "all" || service.categoryId === categoryFilter;

    const matchesStatus = statusFilter === "all" || 
                         (statusFilter === "active" && service.isActive) ||
                         (statusFilter === "inactive" && !service.isActive);

    const matchesApproval = approvalFilter === "all" ||
                           (approvalFilter === "approved" && service.adminApproved) ||
                           (approvalFilter === "pending" && !service.adminApproved);

    const matchesFeatured = featuredFilter === "all" ||
                           (featuredFilter === "featured" && service.isFeatured) ||
                           (featuredFilter === "not_featured" && !service.isFeatured);

    return matchesSearch && matchesCategory && matchesStatus && matchesApproval && matchesFeatured;
  });

  const getStatusColor = (service: Service) => {
    if (!service.adminApproved) return 'bg-yellow-100 text-yellow-800';
    if (!service.isActive) return 'bg-gray-100 text-gray-800';
    return 'bg-green-100 text-green-800';
  };

  const getStatusText = (service: Service) => {
    if (!service.adminApproved) return 'Pending Approval';
    if (!service.isActive) return 'Inactive';
    return 'Active';
  };

  const formatCurrency = (amount: string) => {
    return new Intl.NumberFormat('en-KE', {
      style: 'currency',
      currency: 'KES'
    }).format(parseFloat(amount));
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-KE');
  };

  const getServiceStats = () => {
    const total = currentServices.length;
    const approved = currentServices.filter(s => s.adminApproved).length;
    const pending = currentServices.filter(s => !s.adminApproved).length;
    const active = currentServices.filter(s => s.isActive).length;
    const featured = currentServices.filter(s => s.isFeatured).length;

    return { total, approved, pending, active, featured };
  };

  const stats = getServiceStats();

  const handleViewDetails = (service: Service) => {
    setSelectedService(service);
    setShowDetails(true);
  };

  const handleToggleApproval = (service: Service) => {
    toggleApprovalMutation.mutate({
      serviceId: service.id,
      approved: !service.adminApproved
    });
  };

  if (showDetails && selectedService) {
    return (
      <div className="space-y-6">
        {/* Header */}
        <div className="flex items-center space-x-4">
          <Button variant="ghost" onClick={() => setShowDetails(false)}>
            â† Back to Services
          </Button>
          <div className="flex-1">
            <h2 className="text-2xl font-bold text-gray-900">Service Details</h2>
            <p className="text-gray-600">#{selectedService.id}</p>
          </div>
          <div className="flex items-center space-x-2">
            <Badge className={getStatusColor(selectedService)}>
              {getStatusText(selectedService)}
            </Badge>
            {selectedService.isFeatured && (
              <Badge className="bg-yellow-100 text-yellow-800">
                <Star className="w-3 h-3 mr-1" />
                Featured
              </Badge>
            )}
            <Button
              onClick={() => handleToggleApproval(selectedService)}
              disabled={toggleApprovalMutation.isPending}
              className={selectedService.adminApproved ? "bg-red-600 hover:bg-red-700" : "bg-green-600 hover:bg-green-700"}
            >
              {selectedService.adminApproved ? (
                <>
                  <ThumbsDown className="w-4 h-4 mr-2" />
                  Revoke Approval
                </>
              ) : (
                <>
                  <ThumbsUp className="w-4 h-4 mr-2" />
                  Approve Service
                </>
              )}
            </Button>
          </div>
        </div>

        {/* Service Details */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Settings className="w-5 h-5 mr-2" />
                Service Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium text-gray-500">Name</label>
                <p className="text-gray-900 font-semibold">{selectedService.name}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Description</label>
                <p className="text-gray-900">{selectedService.description}</p>
              </div>
              {selectedService.shortDescription && (
                <div>
                  <label className="text-sm font-medium text-gray-500">Short Description</label>
                  <p className="text-gray-900">{selectedService.shortDescription}</p>
                </div>
              )}
              <div>
                <label className="text-sm font-medium text-gray-500">Slug</label>
                <p className="text-gray-900">{selectedService.slug}</p>
              </div>
              {selectedService.location && (
                <div>
                  <label className="text-sm font-medium text-gray-500">Service Location</label>
                  <p className="text-gray-900 flex items-center">
                    <MapPin className="w-4 h-4 mr-2" />
                    {selectedService.location}
                  </p>
                </div>
              )}
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <DollarSign className="w-5 h-5 mr-2" />
                Pricing & Availability
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium text-gray-500">Price</label>
                <p className="text-gray-900 font-semibold text-lg">
                  {formatCurrency(selectedService.price)}
                  {selectedService.priceType === 'hourly' && '/hour'}
                  {selectedService.priceType === 'per_service' && '/service'}
                </p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Price Type</label>
                <p className="text-gray-900 capitalize">{selectedService.priceType}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Available Today</label>
                <p className="text-gray-900 flex items-center">
                  <Clock className="w-4 h-4 mr-2" />
                  {selectedService.isAvailableToday ? 'Yes' : 'No'}
                </p>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Store className="w-5 h-5 mr-2" />
                Provider Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium text-gray-500">Provider ID</label>
                <p className="text-gray-900">{selectedService.providerId}</p>
              </div>
              {selectedService.providerBusinessName && (
                <div>
                  <label className="text-sm font-medium text-gray-500">Business Name</label>
                  <p className="text-gray-900">{selectedService.providerBusinessName}</p>
                </div>
              )}
              {selectedService.categoryName && (
                <div>
                  <label className="text-sm font-medium text-gray-500">Category</label>
                  <p className="text-gray-900">{selectedService.categoryName}</p>
                </div>
              )}
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Star className="w-5 h-5 mr-2" />
                Performance & Status
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium text-gray-500">Rating</label>
                <p className="text-gray-900">{selectedService.rating}/5.0 ({selectedService.reviewCount} reviews)</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Admin Approval</label>
                <div className="flex items-center space-x-2">
                  <Badge className={selectedService.adminApproved ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'}>
                    {selectedService.adminApproved ? 'Approved' : 'Pending'}
                  </Badge>
                </div>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Status</label>
                <div className="flex items-center space-x-2">
                  <Badge className={getStatusColor(selectedService)}>
                    {getStatusText(selectedService)}
                  </Badge>
                  {selectedService.isFeatured && (
                    <Badge className="bg-yellow-100 text-yellow-800">Featured</Badge>
                  )}
                </div>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Created</label>
                <p className="text-gray-900">{formatDate(selectedService.createdAt)}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-500">Last Updated</label>
                <p className="text-gray-900">{formatDate(selectedService.updatedAt)}</p>
              </div>
            </CardContent>
          </Card>
        </div>

        {selectedService.imageUrl && (
          <Card>
            <CardHeader>
              <CardTitle>Service Images</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <img 
                  src={selectedService.imageUrl} 
                  alt={selectedService.name}
                  className="w-full h-48 object-cover rounded-lg"
                />
                {selectedService.imageUrls?.map((url, index) => (
                  <img 
                    key={index}
                    src={url} 
                    alt={`${selectedService.name} ${index + 1}`}
                    className="w-full h-48 object-cover rounded-lg"
                  />
                ))}
              </div>
            </CardContent>
          </Card>
        )}

        {selectedService.tags && selectedService.tags.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Tag className="w-5 h-5 mr-2" />
                Tags
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex flex-wrap gap-2">
                {selectedService.tags.map((tag, index) => (
                  <Badge key={index} variant="outline">
                    {tag}
                  </Badge>
                ))}
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h3 className="text-xl font-semibold text-gray-900">Services Management</h3>
          <p className="text-gray-600">Monitor and manage all services across the platform</p>
        </div>
        <Button variant="ghost" size="sm" onClick={() => window.location.reload()}>
          <RefreshCw className="w-4 h-4 mr-2" />
          Refresh
        </Button>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-blue-100 p-3 rounded-full">
                <Settings className="w-6 h-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Services</p>
                <p className="text-2xl font-bold text-gray-900">{stats.total}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-green-100 p-3 rounded-full">
                <CheckCircle className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Approved</p>
                <p className="text-2xl font-bold text-gray-900">{stats.approved}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-yellow-100 p-3 rounded-full">
                <Clock className="w-6 h-6 text-yellow-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Pending</p>
                <p className="text-2xl font-bold text-gray-900">{stats.pending}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-purple-100 p-3 rounded-full">
                <TrendingUp className="w-6 h-6 text-purple-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Active</p>
                <p className="text-2xl font-bold text-gray-900">{stats.active}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-yellow-100 p-3 rounded-full">
                <Star className="w-6 h-6 text-yellow-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Featured</p>
                <p className="text-2xl font-bold text-gray-900">{stats.featured}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList>
          <TabsTrigger value="all">All Services</TabsTrigger>
          <TabsTrigger value="approved">Approved</TabsTrigger>
          <TabsTrigger value="pending">Pending</TabsTrigger>
          <TabsTrigger value="active">Active</TabsTrigger>
          <TabsTrigger value="inactive">Inactive</TabsTrigger>
        </TabsList>

        <TabsContent value={activeTab} className="space-y-6">
          {/* Filters */}
          <Card>
            <CardContent className="p-6">
              <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
                <div>
                  <label className="text-sm font-medium mb-2 block">Search</label>
                  <div className="relative">
                    <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                    <Input
                      placeholder="Search services..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      className="pl-10"
                    />
                  </div>
                </div>

                <div>
                  <label className="text-sm font-medium mb-2 block">Category</label>
                  <Select value={categoryFilter} onValueChange={setCategoryFilter}>
                    <SelectTrigger>
                      <SelectValue placeholder="All Categories" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Categories</SelectItem>
                      {(categories as Category[]).map((category: Category) => (
                        <SelectItem key={category.id} value={category.id}>
                          {category.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium mb-2 block">Status</label>
                  <Select value={statusFilter} onValueChange={setStatusFilter}>
                    <SelectTrigger>
                      <SelectValue placeholder="All Status" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Status</SelectItem>
                      <SelectItem value="active">Active</SelectItem>
                      <SelectItem value="inactive">Inactive</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium mb-2 block">Approval</label>
                  <Select value={approvalFilter} onValueChange={setApprovalFilter}>
                    <SelectTrigger>
                      <SelectValue placeholder="All Approval" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Approval</SelectItem>
                      <SelectItem value="approved">Approved</SelectItem>
                      <SelectItem value="pending">Pending</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="text-sm font-medium mb-2 block">Featured</label>
                  <Select value={featuredFilter} onValueChange={setFeaturedFilter}>
                    <SelectTrigger>
                      <SelectValue placeholder="All Featured" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Featured</SelectItem>
                      <SelectItem value="featured">Featured</SelectItem>
                      <SelectItem value="not_featured">Not Featured</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Services List */}
          <Card>
            <CardHeader>
              <CardTitle>Services ({filteredServices.length})</CardTitle>
            </CardHeader>
            <CardContent>
              {servicesLoading ? (
                <div className="text-center py-8">
                  <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full mx-auto"></div>
                  <p className="text-gray-600 mt-2">Loading services...</p>
                </div>
              ) : filteredServices.length === 0 ? (
                <div className="text-center py-8">
                  <Settings className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                  <p className="text-gray-600">No services found</p>
                </div>
              ) : (
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead>
                      <tr className="border-b">
                        <th className="text-left py-3 px-4">Service</th>
                        <th className="text-left py-3 px-4">Provider</th>
                        <th className="text-left py-3 px-4">Category</th>
                        <th className="text-left py-3 px-4">Price</th>
                        <th className="text-left py-3 px-4">Rating</th>
                        <th className="text-left py-3 px-4">Status</th>
                        <th className="text-left py-3 px-4">Approval</th>
                        <th className="text-left py-3 px-4">Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {filteredServices.map((service: Service) => (
                        <tr key={service.id} className="border-b hover:bg-gray-50">
                          <td className="py-3 px-4">
                            <div className="flex items-center space-x-3">
                              {service.imageUrl && (
                                <img 
                                  src={service.imageUrl} 
                                  alt={service.name}
                                  className="w-10 h-10 rounded-lg object-cover"
                                />
                              )}
                              <div>
                                <p className="font-medium text-gray-900">{service.name}</p>
                                <p className="text-sm text-gray-500">{service.shortDescription}</p>
                                {service.isFeatured && (
                                  <Badge className="bg-yellow-100 text-yellow-800 text-xs">
                                    <Star className="w-3 h-3 mr-1" />
                                    Featured
                                  </Badge>
                                )}
                              </div>
                            </div>
                          </td>
                          <td className="py-3 px-4">
                            <div>
                              <p className="text-sm text-gray-900">{service.providerBusinessName || 'N/A'}</p>
                              <p className="text-xs text-gray-500">{service.providerId}</p>
                            </div>
                          </td>
                          <td className="py-3 px-4">
                            <p className="text-sm text-gray-900">{service.categoryName || 'N/A'}</p>
                          </td>
                          <td className="py-3 px-4">
                            <div>
                              <p className="font-medium text-gray-900">{formatCurrency(service.price)}</p>
                              <p className="text-xs text-gray-500 capitalize">{service.priceType}</p>
                            </div>
                          </td>
                          <td className="py-3 px-4">
                            <div className="flex items-center space-x-1">
                              <Star className="w-4 h-4 text-yellow-400 fill-current" />
                              <span className="text-sm">{service.rating}</span>
                              <span className="text-xs text-gray-500">({service.reviewCount})</span>
                            </div>
                          </td>
                          <td className="py-3 px-4">
                            <Badge className={getStatusColor(service)}>
                              {getStatusText(service)}
                            </Badge>
                          </td>
                          <td className="py-3 px-4">
                            <div className="flex items-center space-x-2">
                              <Switch
                                checked={service.adminApproved}
                                onCheckedChange={() => handleToggleApproval(service)}
                                disabled={toggleApprovalMutation.isPending}
                              />
                              <span className="text-xs text-gray-500">
                                {service.adminApproved ? 'Approved' : 'Pending'}
                              </span>
                            </div>
                          </td>
                          <td className="py-3 px-4">
                            <div className="flex items-center space-x-2">
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => handleViewDetails(service)}
                              >
                                <Eye className="w-4 h-4" />
                              </Button>
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { 
  Store, 
  Search, 
  Eye, 
  Edit, 
  CheckCircle, 
  Clock, 
  UserCheck,
  AlertTriangle,
  MoreVertical,
  Package,
  Plus
} from "lucide-react";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { AddVendorModal } from "./AddVendorModal";

interface Vendor {
  id: string;
  email: string;
  businessName: string;
  contactEmail: string;
  contactName: string;
  phone?: string;
  address?: string;
  businessCategory: string;
  description?: string;
  nationalIdNumber?: string;
  taxPinNumber?: string;
  nationalIdUrl?: string;
  taxCertificateUrl?: string;
  verificationStatus: 'pending' | 'verified' | 'rejected';
  verificationNotes?: string;
  verifiedAt?: string;
  verifiedBy?: string;
  createdAt: string;
  updatedAt: string;
}

interface VendorManagementProps {
  onViewVendor?: (vendorId: string) => void;
}

export default function VendorManagement({ onViewVendor }: VendorManagementProps) {
  const [searchTerm, setSearchTerm] = useState("");
  const [showAddVendorModal, setShowAddVendorModal] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch real vendors from API
  const { data: vendors = [], isLoading } = useQuery({
    queryKey: ['/api/admin/vendors', searchTerm],
    retry: false,
  });

  // Vendor verification mutation
  const verifyVendorMutation = useMutation({
    mutationFn: async ({ vendorId, verificationStatus, verificationNotes }: { 
      vendorId: string; 
      verificationStatus: string; 
      verificationNotes?: string; 
    }) => {
      return await apiRequest(`/api/admin/vendors/${vendorId}/verify`, 'PATCH', { verificationStatus, verificationNotes });
    },
    onSuccess: (_, { verificationStatus }) => {
      toast({
        title: "Success",
        description: `Vendor ${verificationStatus} successfully`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/vendors'] });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/stats'] });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to update vendor status",
        variant: "destructive",
      });
    },
  });


  const filteredVendors = (vendors as Vendor[]).filter((vendor: Vendor) => {
    const matchesSearch = vendor.businessName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         vendor.contactEmail.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesSearch;
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'verified': return 'bg-green-100 text-green-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'rejected': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-KE');
  };

  const handleApproveVendor = (vendorId: string) => {
    verifyVendorMutation.mutate({
      vendorId,
      verificationStatus: 'verified',
      verificationNotes: 'Approved by admin'
    });
  };

  const handleRejectVendor = (vendorId: string) => {
    verifyVendorMutation.mutate({
      vendorId,
      verificationStatus: 'rejected',
      verificationNotes: 'Rejected by admin'
    });
  };

  const handleSuspendVendor = (vendorId: string) => {
    verifyVendorMutation.mutate({
      vendorId,
      verificationStatus: 'rejected',
      verificationNotes: 'Suspended by admin'
    });
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'verified': return <CheckCircle className="w-3 h-3" />;
      case 'pending': return <Clock className="w-3 h-3" />;
      case 'rejected': return <AlertTriangle className="w-3 h-3" />;
      case 'suspended': return <AlertTriangle className="w-3 h-3" />;
      default: return <Clock className="w-3 h-3" />;
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h3 className="text-xl font-semibold text-gray-900">Vendor Management</h3>
          <p className="text-gray-600">Manage vendor accounts and verification</p>
        </div>
        <Button 
          className="bg-buylock-primary hover:bg-buylock-primary/90"
          onClick={() => setShowAddVendorModal(true)}
        >
          <Plus className="w-4 h-4 mr-2" />
          Add Vendor
        </Button>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-6">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                <Input 
                  placeholder="Search vendors by business name or email..." 
                  className="pl-10" 
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            </div>

          </div>
        </CardContent>
      </Card>

      {/* Vendor Stats */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-green-100 p-3 rounded-full">
                <UserCheck className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Verified Vendors</p>
                <p className="text-2xl font-bold text-gray-900">{(vendors as Vendor[]).filter(v => v.verificationStatus === 'verified').length}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-yellow-100 p-3 rounded-full">
                <Clock className="w-6 h-6 text-yellow-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Pending Review</p>
                <p className="text-2xl font-bold text-gray-900">{(vendors as Vendor[]).filter(v => v.verificationStatus === 'pending').length}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Vendors List */}
      <Card>
        <CardHeader>
          <CardTitle>Vendor List ({filteredVendors.length} vendors)</CardTitle>
        </CardHeader>
        <CardContent className="p-6">
          <div className="space-y-4">
            {isLoading ? (
              Array.from({ length: 3 }).map((_, i) => (
                <div key={i} className="flex items-center justify-between p-4 border rounded-lg">
                  <div className="flex items-center space-x-4">
                    <div className="w-12 h-12 bg-gray-200 rounded-full animate-pulse"></div>
                    <div className="space-y-2">
                      <div className="h-4 bg-gray-200 rounded animate-pulse w-32"></div>
                      <div className="h-3 bg-gray-200 rounded animate-pulse w-48"></div>
                    </div>
                  </div>
                  <div className="h-8 bg-gray-200 rounded animate-pulse w-20"></div>
                </div>
              ))
            ) : (
              filteredVendors.map((vendor: Vendor) => (
                <div key={vendor.id} className="flex items-center justify-between p-4 border rounded-lg hover:bg-gray-50 transition-colors">
                  <div className="flex items-center space-x-4">
                    <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
                      <Store className="w-6 h-6 text-green-600" />
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-900">{vendor.businessName}</h3>
                      <p className="text-sm text-gray-600">{vendor.contactEmail}</p>
                      <div className="flex items-center space-x-4 text-xs text-gray-500 mt-1">
                        <span>{vendor.businessCategory}</span>
                        {vendor.nationalIdNumber && (
                          <>
                            <span>â€¢</span>
                            <span>ID: {vendor.nationalIdNumber}</span>
                            <span>â€¢</span>
                            <span>Tax PIN: {vendor.taxPinNumber}</span>
                          </>
                        )}
                        <span>â€¢</span>
                        <span>Joined: {formatDate(vendor.createdAt)}</span>
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center space-x-4">
                    <Badge className={getStatusColor(vendor.verificationStatus)}>
                      {vendor.verificationStatus}
                    </Badge>

                    <DropdownMenu>
                      <DropdownMenuTrigger asChild>
                        <Button variant="ghost" size="sm">
                          <MoreVertical className="w-4 h-4" />
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent align="end">
                        <DropdownMenuItem onClick={() => onViewVendor?.(vendor.id)}>
                          <Eye className="w-4 h-4 mr-2" />
                          View Details
                        </DropdownMenuItem>
                        <DropdownMenuItem>
                          <Edit className="w-4 h-4 mr-2" />
                          Edit Vendor
                        </DropdownMenuItem>
                        {vendor.verificationStatus === 'pending' && (
                          <>
                            <DropdownMenuItem 
                              className="text-green-600"
                              onClick={() => handleApproveVendor(vendor.id)}
                              disabled={verifyVendorMutation.isPending}
                            >
                              <CheckCircle className="w-4 h-4 mr-2" />
                              Approve Vendor
                            </DropdownMenuItem>
                            <DropdownMenuItem 
                              className="text-red-600"
                              onClick={() => handleRejectVendor(vendor.id)}
                              disabled={verifyVendorMutation.isPending}
                            >
                              <AlertTriangle className="w-4 h-4 mr-2" />
                              Reject Vendor
                            </DropdownMenuItem>
                          </>
                        )}
                        {vendor.verificationStatus === 'verified' && (
                          <DropdownMenuItem 
                            className="text-red-600"
                            onClick={() => handleSuspendVendor(vendor.id)}
                            disabled={verifyVendorMutation.isPending}
                          >
                            <AlertTriangle className="w-4 h-4 mr-2" />
                            Suspend Vendor
                          </DropdownMenuItem>
                        )}
                        {vendor.verificationStatus === 'rejected' && (
                          <DropdownMenuItem 
                            className="text-green-600"
                            onClick={() => handleApproveVendor(vendor.id)}
                            disabled={verifyVendorMutation.isPending}
                          >
                            <CheckCircle className="w-4 h-4 mr-2" />
                            Reactivate Vendor
                          </DropdownMenuItem>
                        )}
                      </DropdownMenuContent>
                    </DropdownMenu>
                  </div>
                </div>
              ))
            )}
          </div>

          {filteredVendors.length === 0 && (
            <div className="text-center py-8 text-gray-500">
              <Store className="w-12 h-12 mx-auto mb-4 text-gray-300" />
              <p>No vendors found matching your criteria</p>
            </div>
          )}
        </CardContent>
      </Card>

      <AddVendorModal 
        isOpen={showAddVendorModal}
        onClose={() => setShowAddVendorModal(false)}
      />
    </div>
  );
}

import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { 
  Store, 
  Search, 
  Eye, 
  CheckCircle, 
  AlertTriangle, 
  Clock,
  Calendar,
  DollarSign,
  Download,
  Filter,
  Mail,
  Phone,
  ExternalLink,
  Trash2,
  MapPin
} from "lucide-react";

interface VendorApplication {
  id: string;
  businessName: string;
  email: string;
  phone?: string;
  description?: string;
  vendorType?: string;
  nationalIdNumber: string;
  taxPinNumber?: string;
  businessLatitude?: string;
  businessLongitude?: string;
  locationDescription?: string;
  nationalIdUrl?: string;
  taxCertificateUrl?: string;
  verificationStatus: 'pending' | 'verified' | 'rejected';
  createdAt: string;
  updatedAt: string;
}

export default function VendorRequests() {
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch vendor applications from real API
  const { data: vendorApplications = [], isLoading } = useQuery({
    queryKey: ['/api/admin/vendor-applications'],
  });

  const filteredRequests = vendorApplications.filter((request: VendorApplication) => {
    const matchesSearch = request.businessName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         request.email.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === "all" || request.verificationStatus === statusFilter;
    return matchesSearch && matchesStatus;
  });

  // Approve vendor application
  const approveMutation = useMutation({
    mutationFn: async (applicationId: string) => {
      await apiRequest(`/api/admin/vendor-applications/${applicationId}/approve`, 'PUT');
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/vendor-applications'] });
      toast({
        title: "Success",
        description: "Vendor application approved successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to approve vendor application",
        variant: "destructive",
      });
    },
  });

  // Reject vendor application
  const rejectMutation = useMutation({
    mutationFn: async (applicationId: string) => {
      await apiRequest(`/api/admin/vendor-applications/${applicationId}/reject`, 'PUT', { 
        reason: 'Application rejected by admin' 
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/vendor-applications'] });
      toast({
        title: "Success",
        description: "Vendor application rejected",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to reject vendor application",
        variant: "destructive",
      });
    },
  });

  const handleApproveRequest = async (applicationId: string) => {
    approveMutation.mutate(applicationId);
  };

  const handleRejectRequest = async (applicationId: string) => {
    rejectMutation.mutate(applicationId);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "pending":
        return "bg-yellow-100 text-yellow-800 border-yellow-200";
      case "verified":
        return "bg-green-100 text-green-800 border-green-200";
      case "rejected":
        return "bg-red-100 text-red-800 border-red-200";
      default:
        return "bg-gray-100 text-gray-800 border-gray-200";
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "pending":
        return <Clock className="w-3 h-3" />;
      case "verified":
        return <CheckCircle className="w-3 h-3" />;
      case "rejected":
        return <AlertTriangle className="w-3 h-3" />;
      default:
        return <Clock className="w-3 h-3" />;
    }
  };

  const pendingCount = vendorApplications.filter((app: VendorApplication) => app.verificationStatus === 'pending').length;
  const verifiedCount = vendorApplications.filter((app: VendorApplication) => app.verificationStatus === 'verified').length;
  const rejectedCount = vendorApplications.filter((app: VendorApplication) => app.verificationStatus === 'rejected').length;

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Vendor Applications</h1>
        <p className="text-gray-600 mt-1">Review and manage vendor registration applications</p>
      </div>

      {/* Filter Controls */}
      <Card>
        <CardContent className="p-6">
          <div className="flex gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                <Input
                  placeholder="Search by business name or email..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Filter by status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Statuses</SelectItem>
                <SelectItem value="pending">Pending</SelectItem>
                <SelectItem value="verified">Verified</SelectItem>
                <SelectItem value="rejected">Rejected</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-yellow-100 p-3 rounded-full">
                <Clock className="w-6 h-6 text-yellow-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Pending</p>
                <p className="text-2xl font-bold text-gray-900">{pendingCount}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-green-100 p-3 rounded-full">
                <CheckCircle className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Verified</p>
                <p className="text-2xl font-bold text-gray-900">{verifiedCount}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-red-100 p-3 rounded-full">
                <AlertTriangle className="w-6 h-6 text-red-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Rejected</p>
                <p className="text-2xl font-bold text-gray-900">{rejectedCount}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-blue-100 p-3 rounded-full">
                <Store className="w-6 h-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Applications</p>
                <p className="text-2xl font-bold text-gray-900">
                  {vendorApplications.length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Applications List */}
      <Card>
        <CardHeader>
          <CardTitle>Vendor Applications ({filteredRequests.length} applications)</CardTitle>
        </CardHeader>
        <CardContent className="p-6">
          {isLoading ? (
            <div className="text-center py-8">
              <p className="text-gray-500">Loading vendor applications...</p>
            </div>
          ) : filteredRequests.length === 0 ? (
            <div className="text-center py-8">
              <Store className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-500">No vendor applications found</p>
            </div>
          ) : (
            <div className="space-y-4">
              {filteredRequests.map((application: VendorApplication) => (
                <div key={application.id} className="border rounded-lg p-4">
                  <div className="flex items-center justify-between mb-3">
                    <div className="flex items-center space-x-4">
                      <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                        <Store className="w-6 h-6 text-blue-600" />
                      </div>
                      <div>
                        <h3 className="font-semibold text-gray-900">{application.businessName}</h3>
                        <div className="flex items-center space-x-2 text-sm text-gray-600">
                          <Mail className="w-3 h-3" />
                          <span>{application.email}</span>
                          {application.phone && (
                            <>
                              <span>â€¢</span>
                              <Phone className="w-3 h-3" />
                              <span>{application.phone}</span>
                            </>
                          )}
                        </div>
                        <div className="flex items-center space-x-2 text-xs text-gray-500 mt-1">
                          <Calendar className="w-3 h-3" />
                          <span>Applied: {new Date(application.createdAt).toLocaleDateString()}</span>
                        </div>
                      </div>
                    </div>

                    <div className="flex items-center space-x-3">
                      <Badge className={`${getStatusColor(application.verificationStatus)} flex items-center gap-1`}>
                        {getStatusIcon(application.verificationStatus)}
                        {application.verificationStatus}
                      </Badge>
                    </div>
                  </div>

                  <div className="bg-gray-50 p-3 rounded mb-3">
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      <div>
                        <p className="font-medium text-gray-900">Business Description</p>
                        <p className="text-sm text-gray-600 mt-1">{application.description || 'No description provided'}</p>
                        {application.vendorType && (
                          <div className="mt-2">
                            <Badge variant="outline" className="text-xs">
                              {application.vendorType === 'registered' ? 'Registered Business' : 'Non-Registered Business'}
                            </Badge>
                          </div>
                        )}
                      </div>
                      <div>
                        <p className="font-medium text-gray-900">Verification Details</p>
                        <div className="text-sm text-gray-600 mt-1 space-y-1">
                          <p>National ID: {application.nationalIdNumber}</p>
                          {application.taxPinNumber && <p>Tax PIN: {application.taxPinNumber}</p>}
                        </div>
                      </div>
                      <div>
                        <p className="font-medium text-gray-900">Business Location</p>
                        <div className="text-sm text-gray-600 mt-1">
                          {application.businessLatitude && application.businessLongitude ? (
                            <div className="space-y-1">
                              <div className="flex items-center gap-1">
                                <span className="w-1 h-1 bg-green-500 rounded-full"></span>
                                <span>Location confirmed</span>
                              </div>
                              {application.locationDescription && (
                                <p className="text-xs italic">{application.locationDescription}</p>
                              )}
                              <p className="text-xs text-gray-500">
                                Lat: {parseFloat(application.businessLatitude).toFixed(6)}, 
                                Lng: {parseFloat(application.businessLongitude).toFixed(6)}
                              </p>
                            </div>
                          ) : (
                            <div className="flex items-center gap-1 text-amber-600">
                              <span className="w-1 h-1 bg-amber-500 rounded-full"></span>
                              <span>No location provided</span>
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      {application.nationalIdUrl && (
                        <Button 
                          size="sm" 
                          variant="outline"
                          onClick={() => window.open(`/api/admin/vendor-documents/${application.id}/nationalId`, '_blank')}
                          className="flex items-center gap-2"
                        >
                          <Download className="w-3 h-3" />
                          National ID
                        </Button>
                      )}
                      {application.taxCertificateUrl && (
                        <Button 
                          size="sm" 
                          variant="outline"
                          onClick={() => window.open(`/api/admin/vendor-documents/${application.id}/taxCertificate`, '_blank')}
                          className="flex items-center gap-2"
                        >
                          <Download className="w-3 h-3" />
                          Tax Certificate
                        </Button>
                      )}
                    </div>

                    {application.verificationStatus === 'pending' && (
                      <div className="flex space-x-2">
                        <Button 
                          size="sm" 
                          variant="outline" 
                          className="text-red-600 border-red-200 hover:bg-red-50"
                          onClick={() => handleRejectRequest(application.id)}
                          disabled={rejectMutation.isPending}
                        >
                          <AlertTriangle className="w-3 h-3 mr-1" />
                          Reject
                        </Button>
                        <Button 
                          size="sm" 
                          className="bg-green-600 hover:bg-green-700 text-white"
                          onClick={() => handleApproveRequest(application.id)}
                          disabled={approveMutation.isPending}
                        >
                          <CheckCircle className="w-3 h-3 mr-1" />
                          Approve
                        </Button>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

