            <CardContent className="space-y-4">
              {selectedAppointment.status === 'pending' && (
                <div className="flex space-x-3">
                  <Button 
                    className="bg-green-600 hover:bg-green-700"
                    onClick={() => handleAcceptAppointment(selectedAppointment.id)}
                    disabled={updateStatusMutation.isPending}
                  >
                    <CheckCircle className="w-4 h-4 mr-2" />
                    {updateStatusMutation.isPending ? "Accepting..." : "Accept Appointment"}
                  </Button>
                  <Button 
                    variant="outline"
                    className="text-red-600 hover:text-red-700 border-red-200"
                    onClick={() => handleDeclineAppointment(selectedAppointment.id)}
                    disabled={updateStatusMutation.isPending}
                  >
                    <XCircle className="w-4 h-4 mr-2" />
                    {updateStatusMutation.isPending ? "Declining..." : "Decline Appointment"}
                  </Button>
                </div>
              )}

              {selectedAppointment.status === 'accepted' && (
                <div className="flex flex-wrap gap-3">
                  <Button 
                    className="bg-indigo-600 hover:bg-indigo-700"
                    onClick={() => updateStatusMutation.mutate({
                      appointmentId: selectedAppointment.id,
                      status: 'starting_job',
                      vendorNotes: ''
                    })}
                    disabled={updateStatusMutation.isPending}
                  >
                    <Play className="w-4 h-4 mr-2" />
                    Start Job
                  </Button>
                  <Button 
                    className="bg-green-600 hover:bg-green-700"
                    onClick={() => updateStatusMutation.mutate({
                      appointmentId: selectedAppointment.id,
                      status: 'completed',
                      vendorNotes: ''
                    })}
                    disabled={updateStatusMutation.isPending}
                  >
                    <CheckCircle className="w-4 h-4 mr-2" />
                    Mark Completed
                  </Button>
                </div>
              )}

              {/* Status Update Section - Available for accepted appointments and beyond */}
              {!['pending', 'declined', 'cancelled', 'completed'].includes(selectedAppointment.status) && (
                <div className="space-y-4">
                  <div className="space-y-2">
                    <label className="text-sm font-medium text-gray-700">Update Status</label>
                    <Select value={newStatus} onValueChange={setNewStatus}>
                      <SelectTrigger className="w-full">
                        <SelectValue placeholder="Select new status..." />
                      </SelectTrigger>
                      <SelectContent>
                        {getAvailableStatusOptions(selectedAppointment.status).map((option) => (
                          <SelectItem key={option.value} value={option.value}>
                            <div className="flex items-center gap-2">
                              {option.icon}
                              {option.label}
                            </div>
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  {newStatus && (
                    <Button 
                      className="w-full bg-blue-600 hover:bg-blue-700"
                      onClick={() => {
                        updateStatusMutation.mutate({
                          appointmentId: selectedAppointment.id,
                          status: newStatus,
                          vendorNotes: ''
                        });
                        setNewStatus(""); // Reset selection
                      }}
                      disabled={updateStatusMutation.isPending}
                    >
                      {updateStatusMutation.isPending ? (
                        <>
                          <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                          Updating...
                        </>
                      ) : (
                        <>
                          Update to {getStatusLabel(newStatus)}
                        </>
                      )}
                    </Button>
                  )}
                </div>
              )}


            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-blue-100 p-3 rounded-full">
                <Calendar className="w-6 h-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Appointments</p>
                <p className="text-2xl font-bold text-gray-900">{stats.total}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-yellow-100 p-3 rounded-full">
                <Clock className="w-6 h-6 text-yellow-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Pending</p>
                <p className="text-2xl font-bold text-gray-900">{stats.pending}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-green-100 p-3 rounded-full">
                <CheckCircle className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Completed</p>
                <p className="text-2xl font-bold text-gray-900">{stats.completed}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-green-100 p-3 rounded-full">
                <DollarSign className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Earnings</p>
                <p className="text-2xl font-bold text-gray-900">KES {stats.totalEarnings.toLocaleString()}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Appointments List */}
      <Card>
        <CardHeader>
          <CardTitle>Your Appointments ({transformedAppointments.length})</CardTitle>
        </CardHeader>
        <CardContent className="p-6">
          <div className="space-y-4">
            {isLoading ? (
              <div className="flex items-center justify-center py-8">
                <Loader2 className="w-8 h-8 animate-spin text-buylock-primary" />
                <span className="ml-2">Loading appointments...</span>
              </div>
            ) : transformedAppointments.map((appointment) => (
              <div key={appointment.id} className="border rounded-lg p-4">
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center space-x-4">
                    <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                      <Calendar className="w-6 h-6 text-blue-600" />
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-900">{appointment.serviceName}</h3>
                      <div className="flex items-center space-x-2 text-sm text-gray-600">
                        <User className="w-3 h-3" />
                        <span>{appointment.customerName}</span>
                        <span>â€¢</span>
                        <Phone className="w-3 h-3" />
                        <span>{appointment.customerPhone}</span>
                      </div>
                      <div className="flex items-center space-x-2 text-xs text-gray-500 mt-1">
                        <Calendar className="w-3 h-3" />
                        <span>{appointment.appointmentDate} at {appointment.appointmentTime}</span>
                        <span>â€¢</span>
                        <MapPin className="w-3 h-3" />
                        <span>{appointment.city}, {appointment.state}</span>
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center space-x-3">
                    <div className="text-right">
                      <p className="font-bold text-buylock-primary">KES {appointment.totalAmount.toLocaleString()}</p>
                      <Badge className={`${getStatusColor(appointment.status)} flex items-center gap-1`}>
                        {getStatusIcon(appointment.status)}
                        {appointment.status}
                      </Badge>
                    </div>
                    <Button 
                      variant="outline" 
                      size="sm"
                      onClick={() => handleViewDetails(appointment)}
                    >
                      <Eye className="w-4 h-4 mr-1" />
                      View
                    </Button>
                  </div>
                </div>

                {appointment.notes && (
                  <div className="bg-gray-50 p-3 rounded mb-2">
                    <p className="text-sm font-medium text-gray-600 mb-1">Customer Request:</p>
                    <p className="text-sm text-gray-700">{appointment.notes}</p>
                  </div>
                )}

                {/* Status Update Section - Available for accepted appointments and beyond */}
                {appointment.status !== 'pending_acceptance' && appointment.status !== 'declined' && appointment.status !== 'cancelled' && appointment.status !== 'completed' && (
                  <div className="bg-gray-50 p-3 rounded">
                    <div className="space-y-2">
                      <label className="text-sm font-medium text-gray-700">Update Status</label>
                      <Select 
                        value={appointment.status} 
                        onValueChange={(newStatus) => {
                          updateStatusMutation.mutate({
                            appointmentId: appointment.id,
                            status: newStatus,
                            vendorNotes: ''
                          });
                        }}
                      >
                        <SelectTrigger className="w-full">
                          <SelectValue placeholder="Select new status..." />
                        </SelectTrigger>
                        <SelectContent>
                          {getAvailableStatusOptions(appointment.status).map((option) => (
                            <SelectItem key={option.value} value={option.value}>
                              <div className="flex items-center gap-2">
                                {option.icon}
                                {option.label}
                              </div>
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                  </div>
                )}

                {/* Accept/Decline Section for pending appointments */}
                {appointment.status === 'pending_acceptance' && (
                  <div className="bg-gray-50 p-3 rounded">
                    <div className="flex space-x-3">
                      <Button 
                        size="sm"
                        className="bg-green-600 hover:bg-green-700"
                        onClick={() => handleAcceptAppointment(appointment.id)}
                        disabled={updateStatusMutation.isPending}
                      >
                        <CheckCircle className="w-4 h-4 mr-2" />
                        Accept
                      </Button>
                      <Button 
                        size="sm"
                        variant="outline"
                        className="text-red-600 hover:text-red-700 border-red-200"
                        onClick={() => handleDeclineAppointment(appointment.id)}
                        disabled={updateStatusMutation.isPending}
                      >
                        <XCircle className="w-4 h-4 mr-2" />
                        Decline
                      </Button>
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>

          {!isLoading && transformedAppointments.length === 0 && (
            <div className="text-center py-8 text-gray-500">
              <Calendar className="w-12 h-12 mx-auto mb-4 text-gray-300" />
              <p>No appointments found</p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { vendorApiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { 
  DollarSign,
  TrendingUp,
  Clock,
  CheckCircle,
  AlertCircle,
  CreditCard,
  RefreshCw,
  Download,
  Send,
  Calendar,
  PiggyBank,
  Banknote
} from "lucide-react";
import { useCurrency } from "@/contexts/CurrencyContext";

interface EarningsData {
  totalEarnings: number;
  availableBalance: number;
  pendingBalance: number;
  confirmedOrders: number;
  pendingOrders: number;
  disputedOrders: number;
  lastPayoutDate?: string;
  lastPayoutAmount?: number;
}

interface OrderEarning {
  orderId: string;
  customerName?: string;
  orderDate: string;
  amount: number;
  status: 'pending' | 'confirmed' | 'disputed' | 'paid_out';
  items: string;
  confirmationDate?: string;
}

interface PayoutRequest {
  id: string;
  amount: number;
  requestDate: string;
  status: 'pending' | 'processing' | 'completed' | 'failed';
  bankAccount?: string;
  processedDate?: string;
  failureReason?: string;
}

export default function EarningsManagement({ vendorId }: { vendorId: string }) {
  const { formatPrice } = useCurrency();
  const queryClient = useQueryClient();
  const [payoutAmount, setPayoutAmount] = useState("");
  const [bankDetails, setBankDetails] = useState("");
  const [activeTab, setActiveTab] = useState("overview");

  // Fetch vendor earnings data
  const { data: earnings, isLoading: earningsLoading } = useQuery({
    queryKey: [`/api/vendor/${vendorId}/earnings`],
    queryFn: () => vendorApiRequest(`/api/vendor/${vendorId}/earnings`),
    retry: false,
  });

  // Fetch order earnings breakdown
  const { data: orderEarnings = [], isLoading: ordersLoading } = useQuery({
    queryKey: [`/api/vendor/${vendorId}/order-earnings`],
    queryFn: () => vendorApiRequest(`/api/vendor/${vendorId}/order-earnings`),
    retry: false,
  });

  // Fetch payout requests history
  const { data: payoutRequests = [], isLoading: payoutsLoading } = useQuery({
    queryKey: [`/api/vendor/${vendorId}/payout-requests`],
    queryFn: () => vendorApiRequest(`/api/vendor/${vendorId}/payout-requests`),
    retry: false,
  });

  // Request payout mutation
  const requestPayout = useMutation({
    mutationFn: async (data: { amount: string; bankDetails: string }) => {
      return vendorApiRequest(`/api/vendor/${vendorId}/request-payout`, 'POST', data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/vendor/${vendorId}/payout-requests`] });
      queryClient.invalidateQueries({ queryKey: [`/api/vendor/${vendorId}/earnings`] });
      setPayoutAmount("");
      setBankDetails("");
    },
  });

  if (earningsLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          {Array.from({ length: 4 }).map((_, i) => (
            <Card key={i}>
              <CardContent className="p-6">
                <div className="animate-pulse space-y-3">
                  <div className="h-4 bg-gray-200 rounded w-3/4"></div>
                  <div className="h-6 bg-gray-200 rounded w-1/2"></div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  const earningsData: EarningsData = earnings || {
    totalEarnings: 0,
    availableBalance: 0,
    pendingBalance: 0,
    confirmedOrders: 0,
    pendingOrders: 0,
    disputedOrders: 0
  };

  const confirmedEarnings = orderEarnings?.filter((e: OrderEarning) => e.status === 'confirmed') || [];
  const pendingEarnings = orderEarnings?.filter((e: OrderEarning) => e.status === 'pending') || [];
  const disputedEarnings = orderEarnings?.filter((e: OrderEarning) => e.status === 'disputed') || [];

  const handlePayoutRequest = () => {
    const amount = parseFloat(payoutAmount);
    const maxAmount = earningsData.availableBalance;

    if (!amount || amount <= 0) {
      alert('Please enter a valid amount');
      return;
    }

    if (amount > maxAmount) {
      alert(`Amount cannot exceed available balance of ${formatPrice(maxAmount)}`);
      return;
    }

    if (!bankDetails.trim()) {
      alert('Please enter bank account details');
      return;
    }

    requestPayout.mutate({
      amount: payoutAmount,
      bankDetails: bankDetails.trim()
    });
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h3 className="text-xl font-semibold text-gray-900">Earnings Management</h3>
          <p className="text-gray-600">Track your revenue and manage payouts</p>
        </div>
        <Button variant="ghost" size="sm" onClick={() => window.location.reload()}>
          <RefreshCw className="w-4 h-4 mr-2" />
          Refresh
        </Button>
      </div>

      {/* Earnings Overview */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-green-100 p-3 rounded-full">
                <DollarSign className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Earnings</p>
                <p className="text-2xl font-bold text-gray-900">
                  {formatPrice(earningsData.totalEarnings)}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-blue-100 p-3 rounded-full">
                <PiggyBank className="w-6 h-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Available Balance</p>
                <p className="text-2xl font-bold text-gray-900">
                  {formatPrice(earningsData.availableBalance)}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-yellow-100 p-3 rounded-full">
                <Clock className="w-6 h-6 text-yellow-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Pending Balance</p>
                <p className="text-2xl font-bold text-gray-900">
                  {formatPrice(earningsData.pendingBalance)}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="bg-purple-100 p-3 rounded-full">
                <CheckCircle className="w-6 h-6 text-purple-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Confirmed Orders</p>
                <p className="text-2xl font-bold text-gray-900">
                  {earningsData.confirmedOrders}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Detailed Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-4">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="earnings">Order Earnings</TabsTrigger>
          <TabsTrigger value="payouts">Payout Requests</TabsTrigger>
          <TabsTrigger value="request">Request Payout</TabsTrigger>
        </TabsList>

        <TabsContent value="overview" className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <CheckCircle className="w-5 h-5 text-green-600" />
                  <span>Confirmed Earnings</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-2xl font-bold text-green-600">
                  {formatPrice(confirmedEarnings.reduce((sum: number, e: OrderEarning) => sum + e.amount, 0))}
                </p>
                <p className="text-sm text-gray-600 mt-2">
                  {confirmedEarnings.length} confirmed orders
                </p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <Clock className="w-5 h-5 text-yellow-600" />
                  <span>Pending Earnings</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-2xl font-bold text-yellow-600">
                  {formatPrice(pendingEarnings.reduce((sum: number, e: OrderEarning) => sum + e.amount, 0))}
                </p>
                <p className="text-sm text-gray-600 mt-2">
                  {pendingEarnings.length} pending orders
                </p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <AlertCircle className="w-5 h-5 text-red-600" />
                  <span>Disputed Earnings</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-2xl font-bold text-red-600">
                  {formatPrice(disputedEarnings.reduce((sum: number, e: OrderEarning) => sum + e.amount, 0))}
                </p>
                <p className="text-sm text-gray-600 mt-2">
                  {disputedEarnings.length} disputed orders
                </p>
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        <TabsContent value="earnings" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Order Earnings Breakdown</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {orderEarnings.length > 0 ? (
                  orderEarnings.map((earning: OrderEarning) => (
                    <div key={earning.orderId} className="flex items-center justify-between p-4 border rounded-lg">
                      <div className="flex-1">
                        <div className="flex items-center space-x-4">
                          <div>
                            <p className="font-semibold text-gray-900">Order #{earning.orderId.slice(0, 8)}</p>
                            <p className="text-sm text-gray-600">{earning.customerName || 'Customer'}</p>
                            <p className="text-xs text-gray-500">{earning.items}</p>
                          </div>
                        </div>
                      </div>
                      <div className="text-right">
                        <p className="font-bold text-buylock-primary">
                          {formatPrice(earning.amount)}
                        </p>
                        <Badge 
                          variant={earning.status === 'confirmed' ? 'default' : 
                                 earning.status === 'pending' ? 'secondary' : 'destructive'}
                        >
                          {earning.status}
                        </Badge>
                        <p className="text-xs text-gray-500 mt-1">
                          {new Date(earning.orderDate).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="text-center py-8 text-gray-500">
                    <DollarSign className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                    <p>No order earnings data available</p>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="payouts" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Payout Request History</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {payoutRequests.length > 0 ? (
                  payoutRequests.map((payout: PayoutRequest) => (
                    <div key={payout.id} className="flex items-center justify-between p-4 border rounded-lg">
                      <div className="flex-1">
                        <div className="flex items-center space-x-4">
                          <div className="p-2 rounded-full bg-blue-100">
                            <CreditCard className="w-5 h-5 text-blue-600" />
                          </div>
                          <div>
                            <p className="font-semibold text-gray-900">Payout Request</p>
                            <p className="text-sm text-gray-600">{payout.bankAccount}</p>
                            <p className="text-xs text-gray-500">
                              Requested: {new Date(payout.requestDate).toLocaleDateString()}
                            </p>
                          </div>
                        </div>
                      </div>
                      <div className="text-right">
                        <p className="font-bold text-buylock-primary text-lg">
                          {formatPrice(payout.amount)}
                        </p>
                        <Badge 
                          variant={payout.status === 'completed' ? 'default' : 
                                 payout.status === 'processing' ? 'secondary' : 
                                 payout.status === 'pending' ? 'outline' : 'destructive'}
                        >
                          {payout.status}
                        </Badge>
                        {payout.processedDate && (
                          <p className="text-xs text-gray-500 mt-1">
                            Processed: {new Date(payout.processedDate).toLocaleDateString()}
                          </p>
                        )}
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="text-center py-8 text-gray-500">
                    <Banknote className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                    <p>No payout requests found</p>
                    <p className="text-sm">Your payout requests will appear here</p>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="request" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Request Payout</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Payout Amount
                  </label>
                  <Input
                    type="number"
                    placeholder="Enter amount"
                    value={payoutAmount}
                    onChange={(e) => setPayoutAmount(e.target.value)}
                    max={earningsData.availableBalance}
                  />
                  <p className="text-xs text-gray-500 mt-1">
                    Available: {formatPrice(earningsData.availableBalance)}
                  </p>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Bank Account Details
                  </label>
                  <Textarea
                    placeholder="Enter your bank account details..."
                    value={bankDetails}
                    onChange={(e) => setBankDetails(e.target.value)}
                    rows={3}
                  />
                </div>
              </div>

              <div className="flex justify-end">
                <Button
                  onClick={handlePayoutRequest}
                  disabled={requestPayout.isPending || !payoutAmount || !bankDetails}
                  className="bg-buylock-primary hover:bg-buylock-primary/90"
                >
                  <Send className="w-4 h-4 mr-2" />
                  {requestPayout.isPending ? 'Requesting...' : 'Request Payout'}
                </Button>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

import { useState, useEffect } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { vendorApiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Edit } from "lucide-react";
import MultipleImageUploader from "./MultipleImageUploader";

interface Category {
  id: string;
  name: string;
}

interface Subcategory {
  id: string;
  name: string;
  categoryId: string;
}

interface Product {
  id: string;
  name: string;
  description: string;
  shortDescription: string;
  price: string;
  originalPrice: string;
  categoryId: string;
  subcategoryId?: string;
  stockQuantity: number;
  imageUrl: string;
  imageUrls?: string[];
  tags: string[];
  isFeatured: boolean;
  isActive: boolean;
}

interface EditProductModalProps {
  vendorId: string;
  product: Product;
}

export default function EditProductModal({ vendorId, product }: EditProductModalProps) {
  const [open, setOpen] = useState(false);
  const { toast } = useToast();

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    shortDescription: "",
    price: "",
    originalPrice: "",
    categoryId: "",
    subcategoryId: "",
    stock: "",
    images: [] as string[],
    tags: "",
    isFeatured: false,
    isActive: true
  });

  // Initialize form with product data when modal opens
  useEffect(() => {
    if (open && product) {
      setFormData({
        name: product.name || "",
        description: product.description || "",
        shortDescription: product.shortDescription || "",
        price: product.price?.toString() || "",
        originalPrice: product.originalPrice?.toString() || "",
        categoryId: product.categoryId || "",
        subcategoryId: product.subcategoryId || "",
        stock: product.stockQuantity?.toString() || "",
        images: product.imageUrls || (product.imageUrl ? [product.imageUrl] : []),
        tags: product.tags?.join(', ') || "",
        isFeatured: product.isFeatured || false,
        isActive: product.isActive !== false
      });
    }
  }, [open, product]);

  // Fetch categories
  const { data: categories = [] } = useQuery<Category[]>({
    queryKey: ['/api/categories'],
    retry: false,
  });

  // Fetch all subcategories and filter by category
  const { data: allSubcategories = [] } = useQuery<Subcategory[]>({
    queryKey: ['/api/subcategories'],
    retry: false,
  });

  // Filter subcategories by selected category
  const subcategories = allSubcategories.filter(sub => sub.categoryId === formData.categoryId);

  // Update product mutation
  const updateProductMutation = useMutation({
    mutationFn: async (productData: any) => {
      return vendorApiRequest(`/api/vendor/products/${product.id}`, "PUT", productData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/vendor/${vendorId}/products`] });
      toast({
        title: "Success",
        description: "Product updated successfully!",
      });
      setOpen(false);
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update product",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name || !formData.description || !formData.price || !formData.categoryId) {
      toast({
        title: "Validation Error",
        description: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    const productData = {
      name: formData.name,
      description: formData.description,
      shortDescription: formData.shortDescription || formData.description.substring(0, 100),
      price: formData.price,
      originalPrice: formData.originalPrice || formData.price,
      categoryId: formData.categoryId,
      subcategoryId: formData.subcategoryId || null,
      stockQuantity: parseInt(formData.stock) || 0,
      imageUrl: formData.images.length > 0 ? formData.images[0] : product.imageUrl,
      imageUrls: formData.images.length > 0 ? formData.images : null,
      tags: formData.tags.split(',').map(tag => tag.trim()).filter(Boolean),
      isFeatured: formData.isFeatured,
      isActive: formData.isActive,
    };

    updateProductMutation.mutate(productData);
  };

  const handleInputChange = (field: string, value: any) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button variant="outline" size="sm">
          <Edit className="w-4 h-4" />
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Edit Product</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Basic Information */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label htmlFor="name">Product Name *</Label>
              <Input
                id="name"
                value={formData.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                placeholder="Enter product name"
                required
              />
            </div>

            <div>
              <Label htmlFor="price">Price (KES) *</Label>
              <Input
                id="price"
                type="number"
                value={formData.price}
                onChange={(e) => handleInputChange('price', e.target.value)}
                placeholder="0.00"
                required
              />
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label htmlFor="originalPrice">Original Price (KES)</Label>
              <Input
                id="originalPrice"
                type="number"
                value={formData.originalPrice}
                onChange={(e) => handleInputChange('originalPrice', e.target.value)}
                placeholder="0.00"
              />
            </div>

            <div>
              <Label htmlFor="stock">Stock Quantity</Label>
              <Input
                id="stock"
                type="number"
                value={formData.stock}
                onChange={(e) => handleInputChange('stock', e.target.value)}
                placeholder="0"
              />
            </div>
          </div>

          {/* Category Selection */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label htmlFor="category">Category *</Label>
              <Select value={formData.categoryId} onValueChange={(value) => {
                handleInputChange('categoryId', value);
                handleInputChange('subcategoryId', ''); // Reset subcategory
              }}>
                <SelectTrigger>
                  <SelectValue placeholder="Select category" />
                </SelectTrigger>
                <SelectContent>
                  {categories.map((category) => (
                    <SelectItem key={category.id} value={category.id}>
                      {category.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="subcategory">Subcategory</Label>
              <Select 
                value={formData.subcategoryId} 
                onValueChange={(value) => handleInputChange('subcategoryId', value)}
                disabled={!formData.categoryId}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select subcategory" />
                </SelectTrigger>
                <SelectContent>
                  {subcategories.map((subcategory) => (
                    <SelectItem key={subcategory.id} value={subcategory.id}>
                      {subcategory.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          {/* Description */}
          <div>
            <Label htmlFor="description">Description *</Label>
            <Textarea
              id="description"
              value={formData.description}
              onChange={(e) => handleInputChange('description', e.target.value)}
              placeholder="Detailed product description..."
              className="min-h-[100px]"
              required
            />
          </div>

          <div>
            <Label htmlFor="shortDescription">Short Description</Label>
            <Textarea
              id="shortDescription"
              value={formData.shortDescription}
              onChange={(e) => handleInputChange('shortDescription', e.target.value)}
              placeholder="Brief product summary..."
              className="min-h-[60px]"
            />
          </div>

          {/* Image Upload */}
          <div>
            <Label>Product Images</Label>
            <MultipleImageUploader 
              images={formData.images}
              onImagesChange={(images) => handleInputChange('images', images)}
              maxImages={5}
            />
          </div>

          {/* Tags */}
          <div>
            <Label htmlFor="tags">Tags (comma-separated)</Label>
            <Input
              id="tags"
              value={formData.tags}
              onChange={(e) => handleInputChange('tags', e.target.value)}
              placeholder="electronics, smartphone, accessories"
            />
          </div>

          {/* Switches */}
          <div className="flex items-center space-x-6">
            <div className="flex items-center space-x-2">
              <Switch
                id="isFeatured"
                checked={formData.isFeatured}
                onCheckedChange={(checked) => handleInputChange('isFeatured', checked)}
              />
              <Label htmlFor="isFeatured">Featured Product</Label>
            </div>

            <div className="flex items-center space-x-2">
              <Switch
                id="isActive"
                checked={formData.isActive}
                onCheckedChange={(checked) => handleInputChange('isActive', checked)}
              />
              <Label htmlFor="isActive">Active</Label>
            </div>
          </div>

          {/* Submit Button */}
          <div className="flex justify-end space-x-2 pt-4">
            <Button 
              type="button" 
              variant="outline" 
              onClick={() => setOpen(false)}
            >
              Cancel
            </Button>
            <Button 
              type="submit" 
              disabled={updateProductMutation.isPending}
            >
              {updateProductMutation.isPending ? "Updating..." : "Update Product"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

