} from "@shared/schema";
import bcrypt from "bcrypt";

export async function seedDatabase() {
  console.log("ðŸŒ± Seeding BuyLock database...");

  try {
    // 1. Create categories first
    console.log("ðŸ“ Creating categories...");

    const categoryData: InsertCategory[] = [
      {
        name: "Electronics",
        slug: "electronics",
